void mainImage(out vec4 col, in vec2 pos) {
    vec4 state = fetch(pos);
    //col = normalize(state.xy).xyxy * .5 + .5; return;
    float tmp = dot(state.xy, state.xy)*.5;
    col = mix(vec4(1), vec4(0,.5,1,1), min(tmp, 1.));
    col *= min(2.-tmp, 1.);
}

