// Barycentric to Cartesian 
vec3 bToC(vec3 A, vec3 B, vec3 C, vec3 barycentric) {
	return barycentric.x * A + barycentric.y * B + barycentric.z * C;
}

int Type=4;

vec3 nc,pab,pbc,pca;
vec3 icoF0;
