vec4 render(vec2 uv) {
  vec3 col = vec3 (0);

  initIcosahedron();


  vec3 ro = camera;

  vec3 lookAt = vec3(0.001,0. + sin(iTime)*0.2,-20.  + iTime);
  vec3 rd = getRd(ro, lookAt, uv);

  vec3 p = ro; float t = 0., tBeforeRefraction = 0.;


  float side = 1.;

  bool refracted = false;
  bool didHit = false;

  vec3 attenuation = vec3(1);

  #define ITERS 300
  for (int i = 0; i <= ITERS; i++) {
    vec2 d = map(p);
    d.x = d.x * side;
    glow += spectra(d.x - 8. + p.z*0.25 - sin(iTime), 18.)*0.1;
    if(d.x < 0.001) {

      vec3 n = getNormal(p) * side;

      //vec3 l = normalize(vec3(sin(iTime*0.28),sin(iTime*0.6), cos(iTime*0.25)))*25.21;;
      vec3 l = normalize(vec3(1,1,-1))*25.21;
      vec3 lDir = normalize(l - p);
      vec3 lDirB = normalize(vec3(1));

      vec3 l1Col = vec3(0.98,0.02,0.68);
      vec3 l2Col = vec3(0.1,0.2,0.95);
      //vec3 lDir = normalize(vec3(1));
      vec3 h = normalize(lDir - rd);
      vec3 hB = normalize(lDirB - rd);

      float lightAttenuation= 1./(length(p - l)*length(p - l));
      float lBAttenuation = 0.02;

      float diff = max(dot(lDir,n), 0.);
      float fres = pow(1. - abs(dot(n, -rd)), 9.);
      float spec = pow(max(dot(h, n), 0.), 1.);

      float diffB = max(dot(lDirB,n), 0.);
      float fresB = pow(max(dot(n, -rd), 0.), 4.);
      float specB = pow(max(dot(hB, n), 0.), 1.);

      //col += 1.;
          if (!didHit){
          	tBeforeRefraction = t;
          }      

      if (d.y == 1.) { // refract
        vec3 surfaceCol = vec3(0.9,0.9,1);
        refracted = true;
        side = -side;
        ro = p;
        rd = refract(rd, n, 1. + rd.y*0.02 + length(sin(p.xz))*0.001);
        t = 0.;
        d.x = 0.2;
        const float shininess = 0.9;
        col += mix(vec3(diff)*l1Col*surfaceCol,vec3(fres * spec*l1Col), shininess)*attenuation;
        col += mix(vec3(diffB)*l2Col*surfaceCol,vec3(fresB * specB*l2Col), shininess)*lBAttenuation;

        col = clamp(col, 0., 1.);
        //col += glow*0.001;
        attenuation *= 0.9;


      } else if (d.y == 0.) { // diffuse
        vec3 albedo = vec3(.1,.9,0.9);
      		tBeforeRefraction = t;
        col += mix(vec3(diff*albedo)*l1Col,vec3(pow(fres, 10.) * spec*l1Col), 0.9)*attenuation;
        col += mix(vec3(diffB)*l2Col,vec3(pow(fresB, 10.) * specB*l2Col), 0.3)*lBAttenuation;
        rd = refract(rd, n, 1. + rd.y*0.2);
        break; 
      } else if (d.y == 2.) { //reflect
        refracted = true;
        col += mix(vec3(diff)*l1Col,vec3(fres* spec*l1Col), 0.8)*attenuation;
        col += mix(vec3(diffB)*l2Col,vec3(fresB* specB*l2Col), 0.8)*lBAttenuation;
        rd = reflect(rd, n  + sin(n.y*0.1 + p.x*0.1)*0.05);
        ro = p;
        t = 0.;
        d.x = 0.1;
        attenuation *= vec3(0.8,0.7,0.9)*0.1;

      }

      attenuation *= lightAttenuation;
      didHit = true;

      }
    if(t > 100. || i == ITERS ) {
        if (!didHit) {
      		tBeforeRefraction = t;
        }
      break;
    }

    t += d.x;
    p = ro + rd*t;
  }
  col += glow*0.003*vec3(0.3,0.,0.2);
  col *= 2.;
  col *= vec3(1.17,1.1,1);

    col = max(col, vec3(0.));

