}

// Based on youtube tutorial
// https://www.youtube.com/watch?v=PGtv-dBi2wE
// and SDF functions article at
// https://iquilezles.org/articles/distfunctions

#define SPHERE_POS_1 vec3(0., 0., 0.)
#define SPHERE_RAD_1 1.
#define SPHERE_POS_2 vec3(-0.6, 0., -0.6)
#define SPHERE_RAD_2 0.9
#define PLANE_POS -4.
#define LIGHT_POS vec3(-0., 2.1, 0.2)
#define JUMP_SPEED 1.4

#define MAX_STEPS 100
#define MAX_DIST 100.
#define CLOSENESS 0.01

vec2 rescale(in vec2 p) {
    return vec2((p - 0.5 * iResolution.xy) / iResolution.y);
}

float twoUnion(float a, float b) {
    return min(a, b);
}

float twoSubtract(float a, float b) {
    return max(a, -b);
}

float twoIntersect(float a, float b) {
    return max(a, b);
}

float getOnion(float d, float r) {
    return abs(d) - r;
}

vec3 getInfiniteRep(in vec3 p, in vec3 c)
{
    vec3 q = mod(p + 0.5 * c, c) - 0.5 * c;
    return q;
}

// Get distance to sphere 1.
float getDistSphere1(vec3 p) {
    vec3 spherePos = SPHERE_POS_1;
    float dS = length(spherePos - p) - SPHERE_RAD_1;
    return dS;
}

// Get distance to sphere 2.
float getDistSphere2(vec3 p) {
    vec3 spherePos = SPHERE_POS_2;
    float dS = length(spherePos - p) - SPHERE_RAD_2;
    return dS;
}

// Get distance to both spheres.
float getDistSpheres(vec3 p) {
    float density = 4. + sin(iTime);
    vec3 rep = getInfiniteRep(p, vec3(density, density, 11.));
    float dS1 = getDistSphere1(rep);
    float dS2 = getDistSphere2(rep);
    float diff = twoSubtract(dS1, dS2);
    //float onion = getOnion(diff, 0.003);
    return diff;
}

// Get distance to plane and spheres.
float getDistAll(vec3 p) {
    float dS = getDistSpheres(p);
    float dP = p.y - PLANE_POS;
    //float dP = dS;
    return min(dS, dP);
}

float rayMarch(vec3 origin, vec3 direction) {
    float dist = 0.;
    for(int i=0; i < MAX_STEPS; ++i) {
        vec3 ray = origin + direction * dist;
        float step = getDistAll(ray);
        dist += step;
        if (step < CLOSENESS || dist > MAX_DIST) break;
    }
    return min(dist, MAX_DIST);
}

vec3 getNormal(vec3 p) {
    float toSurface = getDistAll(p);
    vec2 e = vec2(0.01, 0.);
    vec3 plane = toSurface - vec3(
        getDistAll(p - e.xyy),
        getDistAll(p - e.yxy),
        getDistAll(p - e.yyx));
    return normalize(plane);
}

float getLight(vec3 p) {
    vec3 lightPos = LIGHT_POS;
    lightPos.xz += 2. * vec2(sin(iTime), cos(iTime));
    vec3 lightVector = normalize(lightPos - p);
    vec3 surfaceVector = getNormal(p);
//    float diffusion = dot(surfaceVector, lightVector);
    float diffusion = dot(lightVector, surfaceVector);
    diffusion = clamp(diffusion, 0., 1.);

    float toLight = rayMarch(p + surfaceVector * 1.05 * CLOSENESS, lightVector);
    if (toLight < length(p - lightPos)) {
        diffusion *= 0.3;
    }

    return diffusion;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = rescale(fragCoord);
    vec3 color = vec3(1., 0.9, 0.7);

    // Camera position
    vec3 ro = vec3(0., 0.5, -9.);
    // Camera direction
    vec3 rd = normalize(vec3(uv.x, uv.y, 1.));
    // Distance to a shape.
    float dist = rayMarch(ro, rd);
    // Point at the shape
    vec3 ray = ro + rd * dist;
    // Light diffusion
    float diffusion = getLight(ray);
    color *= diffusion;
    // Sky
    vec3 sky = vec3(0.1, 0.5, 0.8);
    float skyMix = smoothstep(MAX_DIST * 0.6, MAX_DIST * 0.9, dist);

    // Final color
    vec3 final = mix(color, sky, skyMix);


    fragColor = vec4(final, 1.);
}

///////////
//
// Shadertoy mouse Y-coordinate off-by-1 bug
//
// The bug affects both normal mode canvas and full-screen canvas,
// it's easiest to test in full-screen mode.
//
// The cause is found in mouse event handling code where the Y-coordinate
// origin is changed from top-left to bottom-left. To do this the code
// subtracts the top-left Y-coordinate from the height of the canvas.
// But for 800x450 canvas, the topmost line is line number 449 and not 450,
// as the first line at the bottom is line number 0.
//
vec2 R;
vec4 mains(vec2 U) {
    if(any(equal(ivec2(iMouse.xy),ivec2(U))) && distance(iMouse.xy+0.5,U) > 3.0) {
        return vec4(1);
    }
    float pat = floor(mod(U.x+U.y+iTime*8.,16.0)/8.);
    return any(equal(ivec4(U.xyxy),ivec4(0,0,R-1.))) ? vec4(0,pat,0,1) : vec4(0,0,pat,1);
}
void mainImage(out vec4 O, vec2 U) {R=iResolution.xy;O=mains(U);}

