vec2 R;
const float pi = 3.14159;

mat2 rot(float a) {
    return mat2(cos(a), -sin(a), sin(a), cos(a));
}

// 3D simplex noise stuff from: https://www.shadertoy.com/view/XsX3zB
const float F3 =  .3333333;
const float G3 =  .1666667;
vec3 random3(vec3 c) {
	float j = 4096.*sin(dot(c,vec3(17., 59.4, 15.)));
	vec3 r;
	r.z = fract(512.*j);
	j *= .125;
	r.x = fract(512.*j);
	j *= .125;
	r.y = fract(512.*j);
	return r-.5;
}

float simplex3d(vec3 p) {
	 vec3 s = floor(p + dot(p, vec3(F3)));
	 vec3 x = p - s + dot(s, vec3(G3));
	 vec3 e = step(vec3(0.), x - x.yzx);
	 vec3 i1 = e*(1. - e.zxy);
	 vec3 i2 = 1. - e.zxy*(1. - e);
	 vec3 x1 = x - i1 + G3;
	 vec3 x2 = x - i2 + 2.*G3;
	 vec3 x3 = x - 1. + 3.*G3;
	 vec4 w, d;
	 w.x = dot(x, x);
	 w.y = dot(x1, x1);
	 w.z = dot(x2, x2);
	 w.w = dot(x3, x3);
	 w = max(.6 - w, 0.);
	 d.x = dot(random3(s), x);
	 d.y = dot(random3(s + i1), x1);
	 d.z = dot(random3(s + i2), x2);
	 d.w = dot(random3(s + 1.), x3);
	 w *= w;
	 w *= w;
	 d *= w;
	 return dot(d, vec4(52.));
}

// Sdf stuff
float Box( vec3 p, vec3 b ){
  vec3 q = abs(p) - b;
  return length(max(q,.0)) + min(max(q.x,max(q.y,q.z)),.0);
}
float Cylinder( vec3 p, float h, float r ){
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
  return min(max(d.x,d.y),.0) + length(max(d,.0));
}

// Satellite
float sat(vec3 rp){  
    float body=99., wings = 99., sr = .6;

    vec3 pan = vec3(1.5,.6,.025);
 	vec3 p0 = rp - vec3(1.7+sr, .4, 6.5);
    vec3 p1 = rp - vec3(0, .4, 6.5);
    vec3 p2 = rp - vec3(0, 1.1, 6.5); 
    vec3 p3 = rp - vec3(0, .5, 6.5); 

    p3.xy*=rot(pi/2.);
    p0.yz*=rot(.5);
    p1.yz*=rot(-.5);
    p2.yz*=rot(-.5);

    for(float i = 0.; i < 2.;i++){
    	wings = min(wings,Box(p0, pan));
        p0.x+=(pan.x*2.+sr*2.)*1.1;
    }

    body = min(body, Cylinder(p1, sr, 1.1));
    body = max(-Cylinder(p2, sr+.25, .25), body);
    body = min(body, Cylinder(p1, sr-.25, 1.3));
    body = min(body, Cylinder(p3, .05, 1.3));

    return min(wings, body);
}


float map(vec3 rp){
 	vec3 p = rp - vec3(-iTime-8., .0, 18.);
    vec3 b = vec3(50.,0.,0.);
    p = mod(p, b)-b*.5;  
    p.xy*=rot(iTime);

    return sat(p);
}

float trace(vec3 rd, vec3 ro){
 	float t = 0., d = 0.;   

    for(int i = 0; i < 32; i++){
    	d = map(ro + rd*t); 	   

        if(abs(d) < .006){break;}
        t += d * .95;
    }   
    return t;
}

