const float pi2 = 6.28318;
const float c = 32.03;
float osc(float t, float hz){return sin(t*hz*pi2);}

vec2 EndOfWorld(float t){

    float stp = c * -6.;
    float f = floor(mod(1.+t*.66,2.));
    float ow = 0.;

    ow = f*792.;
    ow += (1.-f)*392.;

    vec2 wave = vec2(osc(t, 261.63 + stp), osc(t, 262.63 + stp))
        + vec2(osc(t, 329.63 + stp), osc(t, 330.63 + stp))
        + vec2(osc(t, ow + stp), osc(t, 393. + stp));

    wave /= 3.;
    wave = clamp(wave, vec2(-.5), vec2(.5));

    return wave;
}

vec2 mainSound( in int samp, float time ){

    return EndOfWorld(time) * (1.-exp(-time*.2)) * .2; // fade in;
    return vec2(0);
}

