    vec3 rd = normalize(vec3(uv, 1. - dot(uv, uv) * .35));
    vec3 ro = vec3(.0,iTime - 5., -12. + iTime);

    rd.yz*=rot(.2);
    rd.xz*=rot(-.5);

    float t = march(rd, ro);

    ro += rd*t;

    vec3 n = normal(ro);
    vec3 col = color(ro, rd, n, t);

    f = vec4(sqrt(clamp(col, 0.0, 1.0)), 1.0);
}

