vec2 dim2from3(vec3 p3d)
{
    float ny = floor(p3d.z/SCALE.x);
    float nx = floor(p3d.z) - ny*SCALE.x;
    return vec2(nx, ny)*vec2(size3d.xy) + p3d.xy;
}

vec3 dim3from2(vec2 p2d)
{
    return vec3(p2d - size3d.xy*floor(p2d/size3d.xy),(floor(p2d.x/size3d.x) + SCALE.x*floor(p2d.y/size3d.y)));
}

vec4 voxel(sampler2D ch, vec3 p3d)
{
    return pixel(ch, round(dim2from3(p3d)), textureSize(ch, 0));
}

vec4 texel(sampler2D ch, ivec2 p)
{
    return pixel(ch, vec2(p + 1), textureSize(ch, 0));
}

vec4 texel(sampler2D ch, vec2 p)
{
    return pixel(ch, round(p+1.), textureSize(ch, 0));
}


//trilinear interpolation = linear interp between layers
vec4 trilinear(sampler2D ch, vec3 p3d)
{
    return mix(voxel(ch, vec3(p3d.xy, floor(p3d.z))),voxel(ch, vec3(p3d.xy, ceil(p3d.z))), fract(p3d.z));
}


float hash(float p)
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

float hash13(vec3 p3)
{
	p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

vec3 hash33(vec3 p3)
{
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy + p3.yxx)*p3.zyx);
}

vec3 hash31(float p)
{
   vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
   p3 += dot(p3, p3.yzx+33.33);
   return fract((p3.xxy+p3.yzz)*p3.zyx); 
}

vec4 hash41(float p)
{
	vec4 p4 = fract(vec4(p) * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);

}


float seed = 0.;
float rand()
{
    float r = hash(seed);
    seed = r;
    return r;
}

vec3 rand3()
{
    vec3 r = hash31(seed);
    seed = r.x*1.54879 + r.y*2.554963 + r.z*0.857836;
    return r;
}

vec4 rand4()
{
    vec4 r = hash41(seed);
    seed = r.x*1.54879 + r.y*2.554963 + r.z*0.857836 + r.w*3.5823758;
    return r;
}

//normally distributed random numbers
vec3 randn()
{
    vec4 rand = rand4();
    vec3 box_muller = sqrt(-2.*log(max(vec3(rand.x,rand.x,rand.z),1e-8)))*vec3(sin(2.*PI*rand.y),cos(2.*PI*rand.y),sin(2.*PI*rand.w));
    return box_muller;
}

//uniformly inside a sphere
vec3 random_sphere()
{
    return normalize(randn())*pow(rand(),0.333333);
}

vec3 cosdistr(vec3 dir, float w)
{
    vec3 rand_dir = normalize(randn());
    vec3 norm_dir = normalize(rand_dir - dot(dir,rand_dir)*dir);
    float u = w*rand();
    return normalize(dir*sqrt(1. - u) + norm_dir*sqrt(u));
}

bool inside(vec3 p)
{
    if(p.x > 1. && p.y > 1. && p.z > 1. && p.x < float(size3d.x)-1. && p.y < float(size3d.y)-1. && p.z < float(size3d.z)-1.)
    {
        return true;
    }
    else return false;
}

#define FOV 2.5
mat3 getCamera(vec2 angles)
{
   mat3 theta_rot = mat3(1,   0,              0,
                          0,   cos(angles.y),  sin(angles.y),
                          0,  -sin(angles.y),  cos(angles.y)); 

   mat3 phi_rot = mat3(cos(angles.x),   sin(angles.x), 0.,
        		       -sin(angles.x),   cos(angles.x), 0.,
        		        0.,              0.,            1.); 

   return theta_rot*phi_rot;
}

vec3 getRay(vec2 angles, vec2 pos)
{
    mat3 camera = getCamera(angles);
    return normalize(transpose(camera)*vec3(FOV*pos.x, 1., FOV*pos.y));
}

float minv(vec3 a)
{
    return min(min(a.x, a.y),a.z);
}

float maxv(vec3 a)
{
    return max(max(a.x, a.y),a.z);
}

float distance2border(vec3 p)
{
    vec3 a = vec3(size3d - 1.) - p;
    return min(minv(p),minv(a)) + 1.;
}

vec4 border_grad(vec3 p)
{
    const float dx = 0.001;
    const vec3 k = vec3(1,-1,0);
	return  (k.xyyx*distance2border(p + k.xyy*dx) +
			 k.yyxx*distance2border(p + k.yyx*dx) +
			 k.yxyx*distance2border(p + k.yxy*dx) +
			 k.xxxx*distance2border(p + k.xxx*dx))/vec4(4.*dx,4.*dx,4.*dx,4.);
}

//a rainbow colormap from Matlab
float interpolate(float val, float y0, float x0, float y1, float x1) 
{
    return (val-x0)*(y1-y0)/(x1-x0) + y0;
}

float base(float val) 
{
    if ( val <= -0.75 ) return 0.0;
    else if ( val <= -0.25 ) return interpolate( val, 0.0, -0.75, 1.0, -0.25 );
    else if ( val <= 0.25 ) return 1.0;
    else if ( val <= 0.75 ) return interpolate( val, 1.0, 0.25, 0.0, 0.75 );
    else return 0.0;
}

vec3 jet_colormap(float v)
{
    return vec3(base(v - 0.5),base(v),base(v + 0.5));
}

vec3 jet_range(float v, float a, float b)
{
    return jet_colormap(2.*clamp((v-a)/(b-a),0.,1.) - 1.);
}


//encoding 2 values in one number
float scaleFactor = 65530.0;
float cp = 256.0 * 256.0;


/* packs given two floats into one float */
vec4 enc(vec4 x, vec4 y) {
    vec4 x1 = round(x * scaleFactor);
    vec4 y1 = round(y * scaleFactor);
    vec4 f = (y1 * cp) + x1;
    return f;
}

/* unpacks given float to two floats */
void dec(in vec4 f, out vec4 x, out vec4 y){
  vec4 dy = floor(f / cp);
  vec4 dx = f - (dy * cp);
  y = (dy / scaleFactor);
  x = (dx / scaleFactor);
}

struct particle
{
    vec4 pos;
    vec4 vel;
};

particle get(sampler2D ch, vec3 p)
{
    p = clamp(p, vec3(0.), size3d);
    vec4 data = voxel(ch, p);
    particle part;
    dec(data, part.pos, part.vel);
    part.pos.xyz *= size3d;
    part.vel *= 10.;
    return part;
}

vec4 save(particle a)
{
    //scale the numbers to be in a [-1,1] range
    a.pos.xyz /= size3d;
    return enc(a.pos, 0.1*a.vel);
}

float force(float d)
{
    d *= 4.;
    return -2.*(0.5*exp(-0.5*d) - 2.*exp(-3.*d));
}


//ray marching primitives, credits to Inigo Quilez 
//https://iquilezles.org/articles/distfunctions
float sdBox( vec3 p, vec3 b )
{
    vec3 d = abs(p) - b;
    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

float sdVerticalCapsule( vec3 p, float h, float r )
{
  p.y -= clamp( p.y, 0.0, h );
  return length( p ) - r;
}

float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
  vec3 pa = p - a, ba = b - a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
  return length( pa - ba*h ) - r;
}

float sdLineBox(vec3 p, vec3 b, float r)
{
    p = abs(p);
    float cx = sdCapsule(p, b, vec3(b.x,b.y,0), r);
    float cy = sdCapsule(p, b, vec3(0,b.y, b.z), r);
    float cz = sdCapsule(p, b, vec3(b.x,0, b.z), r);
    return min(min(cx, cy),cz);
}
float sdPlane( vec3 p, vec4 n )
{
  // n must be normalized
  return dot(p,n.xyz) + n.w;
}


//SIMULATION STUFF
uniform sampler2D fakech0;

void Check(inout particle U, vec3 pos, vec3 dx)
{
    particle neighbor = get(fakech0, pos + dx);
    //check if the stored neighbouring particle is closer to this position 
    if(length(neighbor.pos.xyz - pos) < length(U.pos.xyz - pos))
    {
        U = neighbor; //copy the particle info
    }
}

void CheckRadius(inout particle U, vec3 p, int r)
{
    Check(U, p, vec3(-r, 0, 0));
    Check(U, p, vec3(r, 0, 0));
    Check(U, p, vec3(0, -r, 0));
    Check(U, p, vec3(0, r, 0));
    Check(U, p, vec3(0, 0, -r));
    Check(U, p, vec3(0, 0, r));
}

//get the force from a particle in p0 to a particle at p
vec3 getForce(vec3 p0, vec3 p)
{
	particle neighbor = get(fakech0, p0);
    vec3 dx = neighbor.pos.xyz - p;
    //only count if neighbor particle is inside of its cell to exclude repeated forces
    if(maxv(abs(neighbor.pos.xyz - round(p0))) <= 0.5)
        return normalize(dx)*force(length(dx));
    else
        return vec3(0.);
}

#define rad 2
vec3 calc_force(vec3 p)
{
    vec3 p0 = round(p);
    vec3 force = vec3(0.);
    for( int i=-rad; i<=rad; i++ )
        for( int j=-rad; j<=rad; j++ )
            for( int k=-rad; k<=rad; k++ )
    {
        vec3 dx = vec3(i,j,k);
        if(dx != vec3(0))
          force += getForce(p0+dx, p);
    }

    return force;
}


void init(out particle U, in vec3 p)
{
    if(distance2border(p) > 10.)
    {
        U.pos.xyz = vec3(3.*floor(p.xyz/3.));
        vec3 rnd = hash33(U.pos.xyz+0.5);
        U.pos.w = rnd.x;
        U.vel.xyz = 0.05*(rnd*2. - 1.);
        U.vel.w = clamp(0.4*(rnd.z)+sphere_rad,0.,2.);
    }    
    else
    {
        U.pos = vec4(-100.);
        U.vel = vec4(0.);
    }
}

void SimulationStep(inout particle U)
{
    vec4 border = border_grad(U.pos.xyz);
    vec3 cvec = -U.pos.xyz*vec3(0,0,1);
    vec3 G = 0.15*normalize(cvec)/size3d;

    vec3 force =calc_force(U.pos.xyz);
    vec3 bound =1.*normalize(border.xyz)*exp(-0.4*border.w*border.w);
    float cooling = 1. - (1.-exp(-0.3*length(U.vel.xyz)))*(0.01*exp(-0.05*dot(cvec,cvec)) + 0.01*exp(-0.4*border.w*border.w) + 0.08*exp(-0.1*dot(force,force)));
    U.vel.xyz =  U.vel.xyz*cooling + dt*(bound+force+G);
    U.pos.xyz += dt*U.vel.xyz;
}

void UpdateVoronoi(inout particle U, in vec3 p)
{
    //check neighbours 
    CheckRadius(U, p, 1);
    CheckRadius(U, p, 2);
    CheckRadius(U, p, 3);
    CheckRadius(U, p, 4);
}




//Rendering 

float SDF_particle(vec3 p0, vec3 p)
{
    particle point = get(fakech0, p0);
    return length(point.pos.xyz - p) - point.vel.w;
}

float SDF_particle_wlink(vec3 p0, vec3 p, vec3 p1)
{
    particle point = get(fakech0, p0);
    float pde = length(point.pos.xyz - p) - point.vel.w;
    #ifdef LINKS
        if(length(point.pos.xyz - p1) < 3.)
        {
             pde = min(pde, sdCapsule(p, point.pos.xyz, p1, sphere_rad*0.2));
        }
    #endif
    return pde;
}

vec4 opunion(vec4 a, vec4 b)
{
    if(a.w < b.w) return a;
    else return b;
}

//distance estimator to the voronoi particle field
float DE(vec3 p)
{
	float de = 1e5;

   	vec3 p0 =  round(clamp(p, vec3(0), size3d));
    if(minv(abs(p - size3d*0.5)) > 0.55*maxv(size3d)) 
        de = length(p - size3d*0.5);
    else
    {
        particle centr_partic = get(fakech0, p0);
        vec3 p1 = centr_partic.pos.xyz;
        de = min(de, length(p1-p) - centr_partic.vel.w);
        de = min(de, SDF_particle_wlink(p0+vec3(1,0,0), p, p1));
        de = min(de, SDF_particle_wlink(p0+vec3(-1,0,0), p, p1));
        de = min(de, SDF_particle_wlink(p0+vec3(0,1,0), p, p1));
        de = min(de, SDF_particle_wlink(p0+vec3(0,-1,0), p, p1));
        de = min(de, SDF_particle_wlink(p0+vec3(0,0,1), p, p1));
        de = min(de, SDF_particle_wlink(p0+vec3(0,0,-1), p, p1));
        if(de < sphere_rad*2.)
        {
            de = min(de, SDF_particle_wlink(p0+vec3(1,1,1), p, p1));
            de = min(de, SDF_particle_wlink(p0+vec3(-1,1,1), p, p1));
            de = min(de, SDF_particle_wlink(p0+vec3(-1,-1,1), p, p1));
            de = min(de, SDF_particle_wlink(p0+vec3(-1,-1,-1), p, p1));
            de = min(de, SDF_particle_wlink(p0+vec3(1,-1,-1), p, p1));
            de = min(de, SDF_particle_wlink(p0+vec3(1,1,-1), p, p1));
            de = min(de, SDF_particle_wlink(p0+vec3(-1,1,-1), p, p1));
            de = min(de, SDF_particle_wlink(p0+vec3(1,-1,1), p, p1));
        }
    }

    return de;
}

vec3 particle_color(vec3 p)
{
    vec4 a = vec4(1e5);
    vec3 p0 = round(p);
    for( int i=-1; i<=1; i++ )
        for( int j=-1; j<=1; j++ )
            for( int k=-1; k<=1; k++ )
    {
        vec3 dx = vec3(i,j,k);
        particle thisp = get(fakech0, p0+dx);
        a = opunion(a, vec4(jet_range(thisp.pos.w, -0.1, 1.2), SDF_particle(p0+dx, p)));
    }
    return a.xyz;
}

vec3 COL(vec3 p)
{
    if(inside(p))
    {
        return particle_color(p)*exp(-0.25*pow(max(DE(p),0.),2.));
    }
    else return vec3(0.);
}

vec4 ray_march_vol(vec3 p, vec3 r)
{
    vec4 color = vec4(0., 0., 0., 0.);
    vec4 background = vec4(0.1);
    for(int i = 0; i < 90; i++)
    {
        vec3 tcolor = COL(p);
        float density = length(tcolor);
        float noise  =(1.+0.5*rand());
        float d = 4.*DX*exp(-2.*min(density,2.))*noise;
        float opacity = 1.-exp(-OPACITY*density*d);
        float newa = max(color.w + (1. - color.w)*opacity,0.0001);
        color.xyz = (color.xyz*color.w + (1.-color.w)*opacity*tcolor)/newa;
        color.w = newa;
        if(1. - newa < 0.02) break;
        p += r*d;
    }
    return background + color;
}

vec3 truchet(vec2 p)
{
    float rnd = hash(floor(p.x) + floor(p.y)*1.55463);
    bool cond = (rnd>0.5)?(mod(p.x,1.)+mod(p.y,1.)>1.):(mod(p.x,1.)+mod(p.y,1.)<1.);
    return cond?jet_colormap(0.8*sin(5.*((1.-rnd)*p.x+rnd*p.y))):vec3(1.);
}

vec3 sdcolor(vec3 p)
{
    vec3 c = vec3(0.);
    if(inside(0.8*(p-size3d*0.5) + size3d*0.5))
    {  
        c = particle_color(p);
    }
    else c = vec3(1.);
    if(p.z < 2.)
    {	
        c = 0.5 + 0.5*truchet(0.1*p.xy);
    }
    if(sdLineBox(p - size3d*0.5, size3d*0.5, 0.5) < 0.2) c = 0.15+0.85*jet_colormap(0.9*sin(0.1*(p.x+p.y+p.z)));
    return c;
}


float map(vec3 p)
{
    float de_partic = DE(p); 
    de_partic = min(de_partic, sdPlane(p, vec4(0,0,1,0)));
    return min(de_partic, sdLineBox(p - size3d*0.5, size3d*0.5, 0.5));
}

vec4 calcNormal(vec3 p, float dx) {
	const vec3 k = vec3(1,-1,0);
	return   (k.xyyx*map(p + k.xyy*dx) +
			 k.yyxx*map(p + k.yyx*dx) +
			 k.yxyx*map(p + k.yxy*dx) +
			 k.xxxx*map(p + k.xxx*dx))/vec4(4.*dx,4.*dx,4.*dx,4.);
}

//usual ray marching stuff

float shadow_march(vec4 pos, vec4 dir, float distance2light, float light_angle)
{
	float light_visibility = 1.;
	float ph = 1e5;
	float dDEdt = 0.;
	pos.w = map(pos.xyz);
	int i = 0;
	for (; i < shadow_steps; i++) {

		dir.w += pos.w;
		pos.xyz += pos.w*dir.xyz;
        vec3 ra =rand3()-0.5;

		pos.w = (1. + 0.1*ra.x)*abs(map(pos.xyz));
        dir.xyz = normalize(dir.xyz + 0.01*pos.w*ra/2.5*rayfov*dir.w);

		float angle = max((pos.w - 2.5*rayfov*dir.w)/(max(0.0001,dir.w)*light_angle), 0.);

        light_visibility = min(light_visibility, angle);

		ph = pos.w;

        if(dir.w >= distance2light)
		{
			break;
		}

		if(dir.w > MAX_DIST || pos.w < max(2.*rayfov*dir.w, MIN_DIST))
		{
			break;
		}
	}

	if(i >= shadow_steps)
	{
		light_visibility=0.;
	}
	//return light_visibility; //bad
	light_visibility = clamp(2.*light_visibility - 1.,-1.,1.);
	return  0.5 + (light_visibility*sqrt(1.-light_visibility*light_visibility) + asin(light_visibility))/3.14159265; //looks better and is more physically accurate(for a circular light source)
}


///PBR functions 
vec3 fresnelSchlick(float cosTheta, vec3 F0)
{
    return F0 + (1.0 - F0) * pow(1.0 - cosTheta, 5.0);
}  

float DistributionGGX(vec3 N, vec3 H, float roughness)
{
    float a      = roughness*roughness;
    float a2     = a*a;
    float NdotH  = max(dot(N, H), 0.0);
    float NdotH2 = NdotH*NdotH;

    float num   = a2;
    float denom = (NdotH2 * (a2 - 1.0) + 1.0);
    denom = PI * denom * denom;

    return num / denom;
}

float GeometrySchlickGGX(float NdotV, float roughness)
{
    float r = (roughness + 1.0);
    float k = (r*r) / 8.0;

    float num   = NdotV;
    float denom = NdotV * (1.0 - k) + k;

    return num / denom;
}

float GeometrySmith(vec3 N, vec3 V, vec3 L, float roughness)
{
    float NdotV = max(dot(N, V), 0.0);
    float NdotL = max(dot(N, L), 0.0);
    float ggx2  = GeometrySchlickGGX(NdotV, roughness);
    float ggx1  = GeometrySchlickGGX(NdotL, roughness);

    return ggx1 * ggx2;
}
///END PBR functions

const float Br = 0.0025;
const float Bm = 0.0003;
const float g =  0.9800;
const vec3 nitrogen = vec3(0.650, 0.570, 0.475);
const vec3 Kr = Br / pow(nitrogen, vec3(4.0));
const vec3 Km = Bm / pow(nitrogen, vec3(0.84));

vec3 sky_color(in vec3 pos)
{
    pos = pos.xzy;
	// Atmosphere Scattering
	vec3 fsun = light.xzy;
	float brightnees = exp(-sqrt(pow(abs(min(5.*(pos.y-0.1),0.)),2.)+0.1));
	if(pos.y < 0.)
	{
		pos.y = 0.;
		pos.xyz = normalize(pos.xyz);
	}
    float mu = dot(normalize(pos), normalize(fsun));

	vec3 extinction = mix(exp(-exp(-((pos.y + fsun.y * 4.0) * (exp(-pos.y * 16.0) + 0.1) / 80.0) / Br) * (exp(-pos.y * 16.0) + 0.1) * Kr / Br) * exp(-pos.y * exp(-pos.y * 8.0 ) * 4.0) * exp(-pos.y * 2.0) * 4.0, vec3(1.0 - exp(fsun.y)) * 0.2, -fsun.y * 0.2 + 0.5);
	vec3 sky_col = brightnees* 3.0 / (8.0 * 3.14) * (1.0 + mu * mu) * (Kr + Km * (1.0 - g * g) / (2.0 + g * g) / pow(1.0 + g * g - 2.0 * g * mu, 1.5)) / (Br + Bm) * extinction;
	sky_col = 0.4*clamp(sky_col,0.,10.);
	return pow(sky_col,vec3(1./0.7)); 
}

vec3 ambient_sky_color(in vec3 pos)
{
	float y = pos.z;
    pos.z = 0.;
	return sky_color(pos)*exp(-abs(y));
}

