//3d voronoi particle tracking
void Interactivity(inout particle U, vec3 p)
{
    vec2 angles = texel(ch3,  ivec2(ANGLE_INDX,0)).xy;
    vec3 cray = getRay(angles, vec2(0.));
    vec3 cpos = texelFetch(ch3,  ivec2(POS_INDX,0), 0).xyz;
    vec4 manip = texelFetch(ch3,  ivec2(MANIP_INDX,0), 0);
    vec2 uv = (iMouse.xy - 0.5*size)/max(size.x, size.y);
    vec3 attract_pos = cpos + length(size3d*0.5 - cpos)*getRay(angles, uv);
    //add particle
    if(manip.x > 0.5) 
    {
        if(length(p - cpos) < 4.) 
        {
            U.pos.xyz = cpos+cray*0.6;
            U.vel.xyz = cray*0.6;
            U.pos.w = rand();
            U.vel.w = 0.4*rand()+sphere_rad;
        }
        else if(!inside(cpos)  && length(p - size3d*0.5)<4. )
        {
            U.pos.xyz = size3d*0.5;
            U.vel.xyz = -normalize(size3d*0.5 - attract_pos)*0.6;
            U.pos.w = rand();
            U.vel.w = 0.4*rand()+sphere_rad;
        }
    }
    if(manip.w > 0.5 && iMouse.z>0.)
    {

        U.vel.xyz += 0.02*dt*normalize(attract_pos - U.pos.xyz)/(2.*length((attract_pos - U.pos.xyz)/size3d)+1.); 
    }
    //remove everything
    if(manip.y > 0.5) 
    {
        U.pos.xyz = vec3(10.);
        U.vel.xyz = vec3(0.);
        U.pos.w = 0.;
        U.vel.w = 0.;
    }
}

