/// UTILITY
///
/// Using the GPU as the CPU here, pretty inefficient I guess


#define N_instructions 10.

#define CAMERA_SPEED 1.
#define MOUSE_SENSITIVITY 0.15/60.

//Keyboard constants
const int KEY_SPACE  = 32;
const int KEY_LEFT  = 37;
const int KEY_UP    = 38;
const int KEY_RIGHT = 39;
const int KEY_DOWN  = 40;
const int KEY_A     = 65;
const int KEY_B     = 66;
const int KEY_C     = 67;
const int KEY_D     = 68;
const int KEY_E     = 69;
const int KEY_F     = 70;
const int KEY_G     = 71;
const int KEY_H     = 72;
const int KEY_I     = 73;
const int KEY_J     = 74;
const int KEY_K     = 75;
const int KEY_L     = 76;
const int KEY_M     = 77;
const int KEY_N     = 78;
const int KEY_O     = 79;
const int KEY_P     = 80;
const int KEY_Q     = 81;
const int KEY_R     = 82;
const int KEY_S     = 83;
const int KEY_T     = 84;
const int KEY_U     = 85;
const int KEY_V     = 86;
const int KEY_W     = 87;
const int KEY_X     = 88;
const int KEY_Y     = 89;
const int KEY_Z     = 90;

bool isKeyPressed(int KEY)
{
	return texelFetch( ch3, ivec2(KEY,0), 0 ).x > 0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(iFrame < 1)
    {
        fragColor = vec4(0.);
    }

    if(fragCoord.x <= N_instructions && fragCoord.y <= 1.)
    {
        seed = iTime + fragCoord.x + fragCoord.y*3.141592653;
        SCALE = floor(ar*pow(iResolution.x*iResolution.y,0.1666666));
  	    size3d = vec3(floor(iResolution.xy/SCALE), SCALE.x*SCALE.y);

        //instruction
        int I = int(fragCoord.x); 
        fragColor = texelFetch(ch1,  ivec2(I,0), 0);
        vec4 mouse = texelFetch(ch1,  ivec2(MOUSE_INDX,0), 0);
        vec2 mousespeed = texelFetch(ch1,  ivec2(MOUSE_INDX,0), 0).xy;
        vec4 angles = texelFetch(ch1,  ivec2(ANGLE_INDX,0), 0);
        mat3 camera = transpose(getCamera(angles.xy));
        vec4 posit = texelFetch(ch1,  ivec2(POS_INDX,0), 0);
        vec4 vel = texelFetch(ch1,  ivec2(VEL_INDX,0), 0);
		vec4 speed = texelFetch(ch1,  ivec2(SPEED_INDX,0), 0);
        vec4 modes = texelFetch(ch1,  ivec2(MANIP_INDX,0), 0);
        vec4 norm = calcNormal(posit.xyz, 0.01);
        norm.xyz = norm.xyz/(length(norm.xyz) + 0.01);
        switch(I)
        {
        case MOUSE_INDX:  //mouse speed calculation 
            if(length(iMouse.zw - iMouse.xy) > 10.)
  		    {
   				fragColor.xy = iMouse.xy - fragColor.zw; // mouse delta
                if(iFrame < 1)
                {
                    fragColor.xy = vec2(0.);
                }
            }
            else
            {
				fragColor.xy = vec2(0.); // mouse delta
            }
    		fragColor.zw = iMouse.xy; // mouse pos
            break;

        case ANGLE_INDX:  //angle computation

   			fragColor.xy = fragColor.xy + fragColor.zw*MOUSE_SENSITIVITY; // angle delta
            fragColor.y = clamp(fragColor.y, -PI*0.5, PI*0.5);
            if(modes.w <0.5)
            {
                fragColor.zw += vec2(1,-1)*mouse.xy; // mouse pos
                fragColor.zw *= 0.8;
                fragColor.y *= 1.- 0.01*clamp(length(vel.xyz),0.,1.);
            }

             if(iFrame < 1)
            {
                fragColor.xy = vec2(-PI,0.1);
            }
            break;

        case POS_INDX:  //position

            float DeX = length(vel.xyz*speed.x)+0.0001;
            float MAXDX = map(fragColor.xyz + vel.xyz*speed.x) + norm.w;
            if(DeX > MAXDX)
                vel *= 0.25;
            fragColor.xyz += vel.xyz*speed.x;
   			fragColor.w = vel.w;
            if(iFrame < 1)
            {
                fragColor.xyz = size3d*vec3(0.5,1.7,0.7);
            }

            break;
         case VEL_INDX:  //velocity

            fragColor.w++;
            if(length(mousespeed) >0. || isKeyPressed(KEY_Z))
            {
                fragColor.w = 0.;
            }
            if(isKeyPressed(KEY_UP) || isKeyPressed(KEY_W))
   	   		{
   				fragColor.xyz += camera[1]*speed.x;
                fragColor.w = 0.;
            }
            if(isKeyPressed(KEY_DOWN) || isKeyPressed(KEY_S))
   	   		{
   				fragColor.xyz -= camera[1]*speed.x;
                fragColor.w = 0.;
            }
            if(isKeyPressed(KEY_RIGHT) || isKeyPressed(KEY_D))
   	   		{
   				fragColor.xyz += camera[0]*speed.x;
                fragColor.w = 0.;
            }
            if(isKeyPressed(KEY_LEFT) || isKeyPressed(KEY_A))
   	   		{
   				fragColor.xyz -= camera[0]*speed.x;
                fragColor.w = 0.;
            }
            fragColor.xyz *= 0.8; //slowing down

            //collision detection, removing the normal velocity component 
            #define collision_dist 0.1
          	fragColor.xyz += norm.xyz*max(dot(fragColor.xyz, -norm.xyz),0.)*exp(-max(norm.w,0.)/collision_dist);
            break;
          case SPEED_INDX: //camera max speed
            if(isKeyPressed(KEY_Q))
   	   		{
   				fragColor.x *= 1.01;
            }
            if(isKeyPressed(KEY_E))
   	   		{
   				fragColor.x *= 0.99;
            }
            if(iFrame < 1)
            {
                fragColor.x = CAMERA_SPEED;
            }
            break; 
          case MANIP_INDX:
            if(isKeyPressed(KEY_SPACE) && iFrame%10==0)
   	   		{
   				fragColor.x = 1.;
            }
            else
            {
                fragColor.x = 0.;
            }

            if(isKeyPressed(KEY_C))
   	   		{
   				fragColor.y = 1.;
            }
            else
            {
                fragColor.y = 0.;
            }

            if(isKeyPressed(KEY_P) && iFrame%10==0)
   	   		{
   				fragColor.z = 1. - fragColor.z;
            }

            if(isKeyPressed(KEY_M) && iFrame%10==0)
   	   		{
   				fragColor.w = 1. - fragColor.w;
            }

            if(iFrame < 1)
            {
                fragColor = vec4(0.,0.,1., 1.);
            }
            break; 
        }   
    } else discard;



}

