#define iTime (iTime + 9.)
#define rot(x) mat2(cos(x), -sin(x), sin(x), cos(x))
#define PI acos(-1.)
#define pi acos(-1.)
#define dmin(a, b) a.x < b.x ? a : b
#define fov (0.8 + sin(iTime)*0.05)

// thx nusan for refractions
// and knighty/lsdlive/tdhooper and others for folding

vec3 fold(vec3 p) {
	vec3 nc = vec3(-.5, -.809017, .309017 + sin(iTime)*0.002);
	for (int i = 0; i < 5; i++) {
		p.xy = abs(p.xy);
		p -= 2.*min(0., dot(p, nc))*nc;
	}
	return p - vec3(0, 0, 1.275);
}


vec3 getRd(vec3 ro, vec3 lookAt, vec2 uv){
	vec3 dir = normalize(lookAt - ro);
    vec3 right = normalize(cross(vec3(0,1,0), dir));
    vec3 up = normalize(cross(dir, right));
    return dir + right*uv.x*fov + up*uv.y*fov;
}

vec2 map(vec3 p){
	vec2 d = vec2(10e5);


    vec3 q = p;
    p = fold(p);
    vec3 z = p;
    float b = pow((1. + sin(p.x*3.)), 0.7) - 1.;
    vec3 l = p;
    l.xz *= rot(sin(iTime*0.7)*0.4 - 0.2);
    b = min(b, sin(l.x*3.)*3.);
    z.z += 0.2;
    z.x += 0.82;
    d = dmin(d, vec2(mix(dot(z - 0.6, vec3(0.1,0.1,0.2)), b, 0.1),1.));
    q = fold(q) - vec3(0.5,0.6,-0.2);
    q = abs(q);
    q.z -= 0.2;
    d = dmin(d, vec2( dot(q, normalize(vec3(1))),2.));
    p.x -= 0.2;
    p.y += 0.7;

    p = fold(p) - vec3(0.4,0.4,20.);
    p = fold(p) - vec3(0.,0.4,20.);
    p = fold(p) - vec3(0.,0.,1.);

    for (int i = 0;i < 2; i++){
    	p = abs(p);
        p.x -= 0.2;
    }

    p -= 7.;

    p = fold(p);

    p -= 0.6;
    d = dmin(d, vec2( max(p.x, p.y), 1. ));
    p.xy *= rot(2.5);

    d.x *= 1.;
    d.x += sin(p.x*10.)*0.06;
    return d;
}


vec3 getNormal(vec3 p){
    vec2 t = vec2(0.001,0.);
    return normalize(map(p).x - vec3(
        map(p - t.xyy).x,
        map(p - t.yxy).x,
        map(p - t.yyx).x
    ));
}


#define zoom (7. + sin(iTime)*0.4)
#define rotSpeed (iTime*0.2 + cos(iTime*0.7)*0.5 - sin(iTime*0.5)*0.9)

#define pal(x,t) (0.5 + sin(x*vec3(2.9,5.,1.2) + t + sin(iTime)))

vec3 glow = vec3(0);
#define mx (20.*iMouse.x/iResolution.x)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0);
    vec3 ro = vec3(sin(rotSpeed + mx)*zoom,0. + sin(iTime),cos(rotSpeed + mx)*zoom);
    vec3 lookAt = vec3(0);
    vec3 rd = getRd(ro, lookAt, uv);

    vec3 p = ro; float t, tBeforeRefraction = 0.;

    float bounce = 0.;
    vec3 attenuation = vec3(1);
    float side = 1.;
    #define ITERS 200.
    for(float i = 0.; i <= ITERS ; i ++){
    	vec2 d = map(p);
        d.x *= side;
		glow += pal(t*0.2 + exp(-d.x*10.) + 2., 2.6)* .095 / (.01 + d.x*d.x);

        if (d.x < 0.002){
            vec3 lDir = normalize(vec3(1));
            vec3 n = getNormal(p)*side;
            vec3 h = normalize(lDir - rd);

            float diff = max(dot(lDir,n),0.);
            float fres = pow(1. - max(dot(-rd,n),0.), 20.);
            float spec = pow(max(dot(h,n),0.), 10.);
            if (d.y == 2.) {
            	col += mix( (sin(vec3(2.9,2.9,2.6)*glow*0.005) + 7.)*0.1 *diff ,vec3(0.005*glow*min(fres*spec*diff, 1.)), 0.2)*attenuation;
                break;
            } else {
                col += mix( 0.001*glow *diff ,vec3(0.005*glow*min(fres*spec, 1.)), 0.5)*attenuation;
                attenuation *= vec3(0.2,0.4,0.9)*0.6;
                ro = p;
                rd = refract(rd, n, 1. + n.y*0.07 + n.x*0.2);
                side = -side;
                bounce++;
                if(bounce == 1.) {
                    tBeforeRefraction = t;
                }

                t = 0.;
                d.x = 0.2;
            }



            //break;
        }
        if (t > 50. || i == ITERS) {
            if (bounce == 0.) {
				tBeforeRefraction = t;
            }
        	break;
        }
        t += d.x;
        p = ro + rd*t;
    }

    col -= exp(-7. +tBeforeRefraction*0.14);

    col *= 1.9;
    col = clamp(col, 0., 1.);

    col = mix(col, vec3(0.02,0.03,0.05),smoothstep(0.,1.,0.2 + tBeforeRefraction*0.01));
    col = pow(col, vec3(0.45));

    col *= 1.5;
    col *= 1. - pow(length(uv)*0.5 + 0.37, 4.);
    fragColor = vec4(col,tBeforeRefraction);
}

vec2 bezier(in vec2 p1, in vec2 p2, in vec2 p3, in float t)
{
    float oneminust = 1. - t;
    return oneminust * oneminust * p1 + 2. * oneminust * t * p2 + t * t * p3;
}

float eye(in vec2 uv, in float startY, in float endY)
{
    float blink = snoise(vec3(iTime, 1., 1.));
    float f = bezier(vec2(0., startY), vec2(.5, 1.2), vec2(1., endY), uv.x).y;
    f = smoothstep(f, f + 0.01, uv.y);
    float f1 = bezier(vec2(0., startY), vec2(0.), vec2(1., endY), uv.x).y;
    f1 = smoothstep(f1, f1 + 0.01, uv.y);
    float pnoise = snoise(vec3(iTime * .1, 1., 1.));
    float pnoise2 = snoise(vec3(1., iTime * .1, 1.));
    float r = length(vec2(uv - vec2(0.5 + pnoise * .3, 0.44 + pnoise2 * .2)));
    float p = smoothstep(0.2, 0.1, r);
    float iris = smoothstep(0.3, 0.2, r);
	return blink > .4 ? 0.0 : clamp(((1. - f) * f1) - p - (iris * .6), 0., 1.);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord -.5*iResolution.xy) / iResolution.y;
    float pixelsize = 0.025;
	uv = floor(uv/pixelsize)*pixelsize;
    //uv -= mod(uv,pixelsize)-pixelsize*0.5;
    float f = eye((uv * 1.5) - vec2(-1.05, -0.4), 0.3, 0.2);
    f += eye((uv * 1.5) - vec2(0.05, -0.4), 0.2, 0.3);
    vec3 col = vec3(f);
    fragColor = vec4(col,1.0);
}

