vec2 rotate(in vec2 uv, in float a)
{
	//uv -= .5;
    uv *= mat2(cos(a), -sin(a),
               sin(a), cos(a));
    //uv += .5;
    return uv;
}
float pn(vec3 p) {
	//noise function by CPU https://www.shadertoy.com/view/4sfGRH
    vec3 i = floor(p); 
	vec4 a = dot(i, vec3(1., 57., 21.)) + vec4(0., 57., 21., 78.);
    vec3 f = cos((p-i)*3.141592653589793)*(-.5) + .5;  
	a = mix(sin(cos(a)*a), sin(cos(1.+a)*(1.+a)), f.x);
    a.xy = mix(a.xz, a.yw, f.y);   
	return mix(a.x, a.y, f.z);
}

float getNoise(vec2 pos, float pulse, float time) {
	vec3 q = vec3(pos * 2., pos.x-pos.y + time * 0.3);
	float b = (pulse * 1.6) + pn(q * 2.) + 2.8;
	b +=  .25 * pn(q * 4.);
	b +=  .25  * pn(q * 8.);
	b +=  .5  * pn(vec3(pos, pos.x-pos.y + time * 0.3) * 12.23);
	b = pow(b,0.5);	
	return b;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.y += 1.;
    uv.x *= iResolution.x / iResolution.y;

    float r = length(uv) * 2.;
    float f = 0.;
    uv = rotate(uv, iTime * .1);
    for(int j; j<10; j++)
    {
        uv = rotate(uv, 1.12);
        float p = 0.0 + (getNoise(uv * 2., 0., iTime)) * .2 * abs(uv.x);
        float a = length(uv.y);
        f += smoothstep(p - .1, p, a) * smoothstep(p + .1, p, a);
    }
    f = smoothstep(.6, .9, f);
    vec3 col = vec3(0.5, 0.5 + .2 * cos(iTime + uv.y), 1.) * (f + f * .5) * 2. - r;

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define S(a, b, t) smoothstep(a,b,t)
#define LAYER_COUNT 10.
#define MOON_SIZE .15
#define TREE_COL vec3(.8, .8, 1.)
#define ORBIT_SPEED .025
#define SCROLL_SPEED .3
#define ROT -0.785398
#define ZOOM .4
#define STAR_SPEED .25

float N11(float p)
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

float N21(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

float DistLine(vec2 p, vec2 a, vec2 b) {
    vec2 pa = p-a;
    vec2 ba = b-a;
    float t = clamp(dot(pa, ba)/ dot(ba, ba), 0., 1.);
    return length(pa - ba*t);
}


float DrawLine(vec2 p, vec2 a, vec2 b) {
    float d = DistLine(p, a, b);
    float m = S(.00125, .000001, d);
    float d2 = length(a-b);
    m *= S(1., .5, d2) + S(.04, .03, abs(d2-.75));
    return m;
}

float ShootingStar(vec2 uv) {    
    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv);

    float h = N21(id);

    float line = DrawLine(gv, vec2(0., h), vec2(.1, h));
    float trail = S(.12, .0, gv.x);

    return line * trail;
}

float LayerShootingStars(vec2 uv) {
    float t = iTime * STAR_SPEED;
    vec2 rv1 = vec2(uv.x - t, uv.y + t);
    rv1.x *= 1.05;

    float r = 3. * ROT;
    float s = sin(r);
    float c = cos(r);
    mat2 rot = mat2(c, -s, s, c);
    rv1 *= rot;
    rv1 *= ZOOM * .9;

    vec2 rv2 = uv + t * 1.2;
    rv2.x *= 1.05;

    r = ROT;
    s = sin(r);
    c = cos(r);
    rot = mat2(c, -s, s, c);
    rv2 *= rot;
    rv2 *= ZOOM * 1.1;

    float star1 = ShootingStar(rv1);
    float star2 = ShootingStar(rv2);
    return clamp(0., 1., star1 + star2);
}

float GetHeight(float x) {
    return sin(x*.412213)+sin(x)*.512347;
}

float TaperBox(vec2 p, float wb, float wt, float yb, float yt, float blur) {
    //bottom edge
    float m = S(-blur, blur, p.y - yb);
    //top edge
    m *= S(blur, -blur, p.y - yt);
    //mirror x to get both edges
    p.x = abs(p.x);
    //side edges
    // 0 p.y = yb 1 p.y = yt
    float w = mix(wb, wt, (p.y - yb) / (yt - yb));
    m *= S(blur, -blur, p.x - w);
    return m;
}

vec4 Tree(vec2 uv, vec3 col, float blur) {
    float m = TaperBox(uv, .03, .03, -.05, .25, blur); //trunk
    m += TaperBox(uv, .2, .1, .25, .5, blur); //canopy 1
    m += TaperBox(uv, .15, .05, .5, .75, blur); //canopy 2
    m += TaperBox(uv, .1, .0, .75, 1., blur); //top

    blur *= 3.;
    float shadow = TaperBox(uv-vec2(.2,0.), .1, .5, .15, .253, blur);
    shadow += TaperBox(uv+vec2(.25,0.), .1, .5, .45, .503, blur);
    shadow += TaperBox(uv-vec2(.25,0.), .1, .5, .7, .753, blur);
    col -= shadow*.8;

    return vec4(col, m);
}

vec4 Layer(vec2 uv, float blur) {
    vec4 col = vec4(0);
    float id = floor(uv.x);

    //random [-1, 1]
    float n = N11(id)*2.-1.;
    float x = n*.3;
    float y = GetHeight(uv.x);

    //ground
    float ground = S(blur, -blur, uv.y-y);
    col += ground;

    y = GetHeight(id + .5 - x);

    //vertical grid
    uv.x = fract(uv.x)-.5;
    //					 offset		scale tree size		color
    vec4 tree = Tree((uv+vec2(x,-y))*vec2(1, 1.+n*.2), vec3(1), blur);

    col = mix(col, tree, tree.a);
    col.a = max(ground, tree.a);
    return col;
}

vec2 MoonPos() {
    float t = iTime * ORBIT_SPEED;
    return vec2(sin(t), cos(t))/2.;
}

vec4 MoonGlow(vec2 uv) {
    vec2 moonPos = MoonPos();
    float md = length(uv-(moonPos - vec2(.07, 0.01)));
    float moon = S(.1, -.01, md-MOON_SIZE*8.);
    moon = mix(0., moon, clamp((moonPos.y + .2)*3., 0., 1.));

    vec4 col = vec4(moon);
    md = clamp(1.-md, 0., 1.);
    md *= md;
    col.rgb *= md;
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5*iResolution.xy)/iResolution.y;
    vec2 M = (iMouse.xy/iResolution.xy)*2.-1.;
    float t = iTime * SCROLL_SPEED;
    float blur = .005;

    float twinkle = dot(length(sin(uv+t)), length(cos(uv*vec2(22., 6.7)-t*3.)));
    twinkle = sin(twinkle*5.)*.5+.5;
    float stars = pow(N21(uv*1000.), 1024.)*twinkle;
    vec4 col = vec4(stars);

    vec2 moonPos = MoonPos();
    float moon = S(.01, -.01, length(uv-moonPos)-MOON_SIZE);
    col *= 1.-moon;
    moon *= S(-.01, .05, length(uv-(moonPos+vec2(.1,.05)))-MOON_SIZE);
    col += moon;

    col += LayerShootingStars(uv);

    vec4 layer;
    for (float i=0.; i <1.; i+=1./LAYER_COUNT) {
        float scale = mix(30., 1., i);
        blur = mix(.05, .005, i);
        layer = Layer(uv*scale+vec2(t+i*100.,i)-M, blur);
        layer.rgb *= (1.-i) * TREE_COL;
        col = mix(col, layer, layer.a);
    }
    col += MoonGlow(uv);

    layer = Layer(uv+vec2(t,1.)-M, .07);
    col = mix(col, layer*.05, layer.a);

    fragColor = col;
}

//
// Murakami Infinity by Philippe Desgranges
// Email: Philippe.desgranges@gmail.com
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
//

// I have recently grown a fascination towards the art of Takashi Murakami, 
// especially his colorful flower series.
// His vision is, not unlike Andy Warhol, that art can be mass produced with
// endless variations of a basic concept. This led me to the thinking that, 
// at the extreme, these compositions could be auto-generated.
//
// I tried to reproduce Murakami's compositions following rules that are as true as
// possible to the reference painting.
//
// I stuck with the black & white eyes although they are red and blue on most of the
// paintings because I find them more pleasing this way. I might make a variation someday.
//
// The flower is re-created with a serie of ellipses in polar coordinates.
//
// I hope this brings you joy.

// p.s : I just realized mhnewman attempted something similar, oh well... ;)


#define S(a,b,t) smoothstep(a,b,t)

// An ellipse signed distance function by iq
// https://iquilezles.org/articles/ellipsedist
float sdEllipse( in vec2 z, in vec2 ab )
{
    vec2 p = vec2(abs(z));

    if( p.x > p.y ){ p=p.yx; ab=ab.yx; }

    float l = ab.y*ab.y - ab.x*ab.x;
    float m = ab.x*p.x/l; float m2 = m*m;
    float n = ab.y*p.y/l; float n2 = n*n;
    float c = (m2 + n2 - 1.0)/3.0; float c3 = c*c*c;
    float q = c3 + m2*n2*2.0;
    float d = c3 + m2*n2;
    float g = m + m*n2;

    float co;

    if( d<0.0 )
    {
        float p = acos(q/c3)/3.0;
        float s = cos(p);
        float t = sin(p)*sqrt(3.0);
        float rx = sqrt( -c*(s + t + 2.0) + m2 );
        float ry = sqrt( -c*(s - t + 2.0) + m2 );
        co = ( ry + sign(l)*rx + abs(g)/(rx*ry) - m)/2.0;
    }
    else
    {
        float h = 2.0*m*n*sqrt( d );
        float s = sign(q+h)*pow( abs(q+h), 1.0/3.0 );
        float u = sign(q-h)*pow( abs(q-h), 1.0/3.0 );
        float rx = -s - u - c*4.0 + 2.0*m2;
        float ry = (s - u)*sqrt(3.0);
        float rm = sqrt( rx*rx + ry*ry );
        float p = ry/sqrt(rm-rx);
        co = (p + 2.0*g/rm - m)/2.0;
    }

    float si = sqrt( 1.0 - co*co );

    vec2 closestPoint = vec2( ab.x*co, ab.y*si );

    return length(closestPoint - p ) * sign(p.y-closestPoint.y);
}


// rotates pos to align the up vector towards up
vec2 rotUp(vec2 pos, vec2 up)
{
    vec2 left = vec2(-up.y, up.x);
    return left * pos.x + up * pos.y;
}

const float pi = 3.1415926;
const float pi2 = 6.2831853;
const float quadrant = pi / 6.0;

const float blackLevel = 0.3; // True black is too aggressive



// The mouth is the intersection of two ellipses, I traced them in photoshop to
// compute the right radii and offsets
float mouthDst(vec2 uv)
{
    return max(sdEllipse(uv - vec2(0.0, -0.17), vec2(0.30, 0.2055)),
               sdEllipse(uv - vec2(0.0,  0.07), vec2(0.14, 0.2055)));
}

// For the eye, I use simpler circle distance maths in a scales and rotated space
// as I don't need an accurate distance function to create an outline
vec4 eye(vec2 uv, vec2 up, vec2 spot1, vec2 spot2, float scale)
{
    uv = rotUp(uv, up);
    uv.x *= 1.5;

    float len = length(uv);
    float len2 = length(uv + spot1);// vec2(0.010, 0.025));
    float len3 = length(uv + spot2);// vec2(-0.005, -0.017));

    float aa = scale * 4.0 / iResolution.x;

    vec4 eye;

    eye.a = S(0.04 + aa, 0.04, len);

    eye.rgb = vec3(S(0.014, 0.014 - aa, len2) + S(0.02, 0.02 - aa, len3) + blackLevel);

    return eye;
}

const float cRatio = 1.0 / 255.0;

// I wanted the color palette to be true to the 16 hue rainbow used
// by Murakami but I didn't manage to reproduce the orange-yellow-green part
// using simple maths so I defaulted to a palette. Then I realized I couldn't target
// Webgl < 3.0 (Wich was one of my objectives) with array constructor so I decided
// to build a function selecting the right color with a dichotomic approch in hope
// that the compiler will make a decent job of optimizing all those branches.
vec3 palette(float id)
{
	if (id < 6.0)
    {
        //[0 - 5]
        if (id < 3.0)
        {   //[0 - 2]
            if (id < 1.0) return vec3(181.0, 23.0, 118.0) * cRatio;
            else if (id < 2.0) return vec3(225.0, 27.0, 104.0) * cRatio;
            else return vec3(230.0, 40.0, 24.0) * cRatio;
        }
        else
        {   //[3 - 5]
            if (id < 4.0) return vec3(240.0, 110.0, 14.0) * cRatio;
            else if (id < 5.0) return vec3(253.0, 195.0, 2.0) * cRatio;
            else return vec3(253.0, 241.0, 121.0) * cRatio;
        }
    }
    else
    {   //[6 - 11]
        if (id < 9.0)
        {   //[6 - 8]
            if (id < 7.0) return vec3(167.0, 202.0, 56.0) * cRatio;
            else if (id < 8.0) return  vec3(0.0, 152.0, 69.0) * cRatio;
            else return vec3(2.0, 170.0, 179.0) * cRatio;
        }
        else
        {   //[9 - 11] The darker color are at the end to be avoided by mod
            if (id < 10.0) return vec3(25.0, 186.0, 240.0) * cRatio;
            else if (id < 11.0) return  vec3(0.0, 98.0, 171.0) * cRatio;
            else return vec3(40.0, 49.0, 118.0) * cRatio;
        }
    }
}



// Adapted from BigWIngs
vec4 N24(vec2 t) {
    float n = mod(t.x * 458.0 + t.y * 127.3, 100.0);
	return fract(sin(n*vec4(123., 1024., 1456., 264.))*vec4(6547., 345., 8799., 1564.));
}

// That's where the meat of the shader is, drawing a flower
vec4 flower(vec2 uv, vec2 id, float minSz, float maxSz, float scale)
{
    vec4 rnd = N24(id);//mod(id.x, 300.0) * mod(id.y, 500.0) * 13.56);

    float rdScale = mix(minSz, maxSz, rnd.z);

    scale *= rdScale; // The border thickness & AA is scale-independant

    uv.xy *= rdScale;

    uv.x += 0.1 + rnd.x * 0.5;
    uv.y += 0.25 + rnd.y * 0.2;
    uv.y += sin(iTime * mix(0.4, 1.0, rnd.y) + (rnd.w * 45.0)) * 0.2;


    float aa2 = scale * 3.0 / iResolution.x;

    float centerDst = length(uv);

    if (centerDst > 0.5) return vec4(0.0);

    float edge; // Mask for the outline edge

    vec4 color = vec4(1.0, 1.0, 1.0, 1.0); // Underlying color


    float thick = 0.002 * scale;

    if (centerDst < 0.2)
    {
        //Face part

        float thres = 0.2 - thick;

        // inner part of edge circle surrounding the head
        edge =  S(thres, thres - aa2, centerDst);

        float mouth = mouthDst(uv);

        // edge of the mouth
        edge *= S(thick, thick + aa2, abs(mouth));

        // face color
        float faceRnd = fract(rnd.x * 45.0 + rnd.y * 23.45);
        if (faceRnd < 0.5) 
        {
            // Flowers with classic yellow / red faces
        	color.rgb = (mouth < 0.0) ? vec3(1.0, 0.0, 0.0) : vec3(1.0, 1.0, 0.0); 
        }
        else
        {
            // Flowers with white face / random color mouth
            float colId = mod(faceRnd * 545.456, 11.0);
            color.rgb = (mouth < 0.0) ? palette(colId) : vec3(1.0); 
        }

        // Eyes
        vec4 eyeImg;
        if (uv.x > 0.0)
        {
           eyeImg = eye(uv - vec2(0.075, 0.095), vec2(-0.7, 1.2),
                       vec2(0.007, 0.025), vec2(-0.004, -0.019), scale);
        }
        else   
        {
           eyeImg = eye(uv - vec2(-0.075, 0.095), vec2(0.7, 1.2),
                       vec2(0.024, 0.010), vec2(-0.016, -0.009), scale);
        }

        color.rgb = mix(color.rgb, eyeImg.rgb, eyeImg.a);
    }
    else
    {
        float rot = iTime * rnd.y * 0.5;
        float angle = fract((atan(uv.x, uv.y) + rot) / pi2);

        float section = angle * 12.0;
        float sectionId = floor(section);

        if (rdScale < 0.86 && rnd.w > 0.55)
        {
           // Rainbow flower
           color.rgb = palette(sectionId);//mod(sectionId + (rnd.x + rnd.y) * 345.456, 12.0));
        }
        else if (rnd.y > 0.05)
        {
            //Alternating flower
            if (mod(sectionId, 2.0) == 0.0)
            {
                // Color 1
                float colId = mod((rnd.x + rnd.y) * 345.456, 10.0);
                color.rgb = palette(colId);
            }
            else if (rnd.x > 0.75)
            {
                // Color 2
                float colId = mod((rnd.w + rnd.z) * 545.456, 11.0);
                color.rgb = palette(colId);
            }
            // else, Color2 is white by default
        }
		// else, fully white petals

        if (centerDst < 0.36)
        {
            //intermediate part, concentric bars

            float sectionX = fract(section);
            float edgeDist = 0.5 - abs(sectionX - 0.5);

            edgeDist *= centerDst; // Untaper bar space so bars have constant thickness

            float aa = scale * 7.0 / iResolution.x;
            float bar = thick * 1.7;
            edge = S(bar, bar + aa, edgeDist);

            // outer part of edge circle surrounding the head
            float thres = 0.2 + thick;
            float head = S(thres, thres + aa2, centerDst);
            edge *= head;
        }
        else
        {
            // Petal tips are actually ellipses, they could have been approximated them with
            // circles but I didn't because I have OCD and I needed the ellipse SDF 
            // for the mouth anyways ;)

            // Angle to the center of the quadrant
            float quadAngle = (sectionId + 0.5) * quadrant - rot + pi; 

            // Center of the ellipse
            vec2 petalUp = vec2(-sin(quadAngle), -cos(quadAngle));
            vec2 petalCenter = petalUp * 0.36;

            // Rotation of the ellipse basis
            vec2 petalSpace = rotUp(uv - petalCenter, petalUp);

            // Signed distance function of the ellipse
            float petalDst = sdEllipse(petalSpace, vec2(0.0944, 0.09));

            //border edge and alpha mask
            float borderIn = S(thick + aa2, thick, petalDst);
            float borderOut = S(-thick, -thick - aa2, petalDst);

            edge = (borderOut);

            color.a = borderIn;
        }
    }

    color.rgb = mix(vec3(blackLevel), color.rgb,edge);

    return color;
}


vec3 flowerLayer(vec2 uv, vec3 bg, float minSz, float maxSz, float scale)
{
    vec2 cellId = floor(uv);
    vec2 cellUv = fract(uv) - vec2(0.5);

    //Check only 4 cells because the flower function is rather costly
    for (float x = 0.0; x <= 1.0; x += 1.0)
    {
        for (float y = 0.0; y <= 1.0; y += 1.0)
    	{
            vec2 offset = vec2(x, y);

            vec4 f = flower(cellUv - offset, cellId + offset, minSz, maxSz, scale);
    		bg = mix(bg, f.rgb, f.a);
    	}
    }

    return bg;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y; 

    float sc = 1.65 + cos(iTime* 0.1) * 0.25;

    uv.xy *= sc;

    uv.x += 35.9;
    uv.y += 45.0;


    float scroll = iTime * 0.25;


   	float t = iTime * 0.23;
    vec3 col = vec3(0.8) + vec3(0.2) * sin(t + vec3(0.0, pi * 0.5, pi));

    col = flowerLayer(uv * 4.0 + vec2(scroll, -scroll * 0.3 + 59.0), col, 0.8, 1.0, 2.0 * sc);
    col = flowerLayer(uv * 3.0 + vec2(scroll, -scroll * 0.2 + 59.0), col, 0.7, 1.25, 1.5 * sc);
    col = flowerLayer(uv * 2.0 + vec2(scroll, -scroll * 0.1 + 345.0), col, 0.8, 2.0, 1.0 * sc);

    // Output to screen
    fragColor = vec4(col,1.0);
}

