// Cubic interpolation
float cubemix(float a, float b, float t) {
    float c = t*t*(3.-2.*t);
    return mix(a, b, c);
}

float rand( vec2 p ) {
	vec2 f = fract(p);
	p = floor(p);
	float v = p.x+p.y*1000.0;
	vec4 r = vec4(v, v+1.0, v+1000.0, v+1001.0);
	r = fract(10000.0*sin(r*.001));
	f = f*f*(3.0-2.0*f);
	return 2.0*(mix(mix(r.x, r.y, f.x), mix(r.z, r.w, f.x), f.y))-1.0;
}

float valuenoise(vec2 uv) {
    vec2 iuv = floor(uv);
    vec2 offset = vec2(0.,1.);
    float v00 = rand(iuv);
    float v01 = rand(iuv+offset.xy);
    float v10 = rand(iuv+offset.yx);
    float v11 = rand(iuv+offset.yy);
    vec2 disp = fract(uv);
    float v0 = cubemix(v00, v01, disp.y);
    float v1 = cubemix(v10, v11, disp.y);
    return cubemix(v0, v1, disp.x)*.5+.5;
}

float fractalnoise(vec2 uv, float mag) {
    float d = valuenoise(uv);
    int i;
    float fac = 1.;
    vec2 disp = vec2(0., 1.);
    for (i=0; i<3; i++) {
        uv -= mag * iTime * disp * fac;
        fac *= 0.5;
        d += valuenoise(uv/fac)*fac;
    }
    return d;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x*20.;
	uv.y -= iTime*10.;
    float val = fractalnoise(uv, 5.);
    vec3 col = mix(vec3(1., 0.2, 0.), vec3(1., 1., 0.5), smoothstep(0.5, 1., val));
    float phase1 = iTime * 20.5;
    float offset = smoothstep(-0.5, 0.5, sin(iTime*20.))*40.;
    float offset2 = sin(iTime*15.25) * 40.;
	float left = iResolution.x*(0.5 + 0.1*(sin(phase1) - 1.)) + offset + offset2;
    float right = iResolution.x*(0.5 - 0.1*(cos(phase1) - 1.)) + offset + offset2;
    float bottom = iResolution.y*0.2 + offset * 0.5;
    float top = iResolution.y*0.55 + offset*0.5;
    if (fragCoord.x < left || fragCoord.x > right || (fragCoord.y < top && fragCoord.y > bottom)) {
    	col = vec3(0.);   
    }
    fragColor = vec4(col,1.0);
}

