#define A 3

mat2 rot(float a)
{
    float s = sin(a),c = cos(a);
    return mat2(c,s,-s,c);
}

vec3 random33(vec3 st)
{
    st = vec3(dot(st,vec3(127.1, 311.7,811.5)),
                dot(st, vec3(269.5, 183.3,211.91)),
                dot(st, vec3(511.3, 631.19,431.81))
                );
    return -1.0 + 2.0 * fract(sin(st) * 43758.5453123);
}

float norm(vec3 p, float n)
{
    vec3 t=pow(abs(p),vec3(n));
    return pow(t.x+t.y,1./n);
}

vec4 celler3D(vec3 i,vec3 sepc)
{
    float stime = iTime / 1.5;
    vec3 sep = i * sepc;
    vec3 fp = floor(sep);
    vec3 sp = fract(sep);
    float dist = 5.;
    vec3 mp = vec3(0.);

    for (int z = -1; z <= 1; z++)
    {
        for (int y = -1; y <= 1; y++)
        {
            for (int x = -1; x <= 1; x++)
            {
                vec3 neighbor = vec3(x, y ,z);
                vec3 pos = vec3(random33(fp+neighbor));
                pos = sin( (pos*50. +stime/(230. + 100.*cos(stime/130.))) )* 0.5 + 0.5;

                float shape = 0.5 + clamp(sin(stime),0.,1.) *30.;
                float rate = clamp( (sin(stime/2.)),0.,1.);
                float divs = norm(neighbor + pos - sp,shape);
                divs = mix(length(neighbor + pos - sp),divs,rate);
                mp = (dist >divs)?pos:mp;
                dist = (dist > divs)?divs:dist;
            }
        }
    }
    return vec4(mp,dist);
}

void scene(out vec3 color,in vec2 F)
{
    vec2 p = (F.xy * 2.0 - iResolution.xy) / min(iResolution.x,iResolution.y);
    //vec3 color = vec3(0.);

    vec3 cp = vec3(0.,0.,-5.);
    vec3 cd = vec3(0.,0.,1.);
    vec3 cu = vec3(0.,1.,0.);
    vec3 cs = normalize(cross(cd,cu));
    cu = normalize(cross(cd,cs));

    float target = 5.0;

    vec3 rd = normalize(vec3(p.x * cs + p.y * cu + cd * target));

    float depth = 0.;

    float stime = iTime/ 150.;

    for(int i = 0 ; i < 3 ; i++)
    {
        vec3 rp = cp + rd * depth;
        vec4 celler = celler3D(rp,vec3(50.)+1.1*sin(stime) );
        float w = celler.w;
        w = max(celler.w,0.001);

        if (celler.w - 0.3 < 0.0001 && depth > 0.0001)
        {
            color += celler.xyz - celler.w;
            break;
        }
        if(depth > 1.){break;}
        depth += w;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //supersampling | oversampling
	//https://youtu.be/Pcph14WlLsk   

    vec3 color = vec3(0.);

    for(int i = 0;i < A;i++ )
    {
        for(int j = 0; j < A;j++)
        {
            vec2 d = vec2(float(i),float(j)) - vec2(float(i),float(j))/2.;
            d /= float(A);
            scene(color,fragCoord + d);
        }
    }
    color /= float(A * A);

    fragColor = vec4(color ,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 u = 3.*(2.*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m = 8.*(2.*iMouse.xy-iResolution.xy)/iResolution.y;
    float pi = 3.14159265359;

    vec2 p = cos(4.1+vec2(0.,pi*.5))*(2.7+sin(iTime)*.2);
    if(iMouse.z > .5) p = m;

    u = u*length(p);

    for(float i = .5; i < 20.; ++i)
    {
        float e = mod(i,2.)-.5;
        e = mix(   6.,				//close hand fan
                1./6.,				//open  hand fan
                e);										
        u = sin(atan(u.x,u.y)*e+vec2(0.,pi*.5))*pow(length(u),e);
        u-= p;														
    }
    fragColor = .5+.5*sin(6.28*(.55+length(u)/length(p)+vec4(.2,.05,.1,.0)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 u = 2.*(2.*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m = 8.*(2.*iMouse.xy-iResolution.xy)/iResolution.y;
    float pi = 3.14159265359;

    vec2 p = cos(pi*11./6.+vec2(0.,pi*.5))*(4.+sin(iTime));
    if(iMouse.z > .5) p = m;

    u = u*length(p);

    for(float i = .5; i < 20.; ++i)
    {
        float e = mod(i,2.)-.5;
        e = mix(   3.,												//close hand fan
                1./3.,												//open  hand fan
                e);										
        u = sin(atan(u.x,u.y)*e+vec2(0.,pi*.5))*pow(length(u),e);	//space wrap
        u-= p;														//move space	
    }
    fragColor = vec4(.2,.2,.3,.0)*length(p)/length(u);
}

// Plento
vec2 R;
const float pi = 3.14159;

mat2 rot(float a) {
    return mat2(cos(a), -sin(a), sin(a), cos(a));
}

#define s iTime*3.8

// The stairs sdf
float map(vec3 rp){
    //float k = floor(rp.x*.75)*.25;
    rp.yz += s;
    return min(rp.y - floor(rp.z), 1.- (rp.z - floor(rp.y)));
}



vec3 normal( in vec3 pos ){ // Can def get rid of this
    vec2 e = vec2(0.002, -0.002);
    return normalize(
        e.xyy * map(pos + e.xyy) + 
        e.yyx * map(pos + e.yyx) + 
        e.yxy * map(pos + e.yxy) + 
        e.xxx * map(pos + e.xxx));
}

float march(vec3 rd, vec3 ro){
 	float t = 0., d = 0.;   

    for(int i = 0; i < 44; i++){
    	d = map(ro + rd*t); 	   

        if(abs(d) < .005 || t > 64.){
            break;
        }

        t += d * .75;
    }

    return t;
}

// Color the stairs and also fake the staircase width
vec3 render(vec3 ro, vec3 rd, vec3 n, vec2 u, float t){
    vec3 lp = ro + vec3(0., .016, -.7);
    vec3 ld = lp-ro;

    float dif = max(dot(n, ld), .0);
    vec3 col = vec3(0);


    ro.yz+=s;
    ro.x*=.5;

    float c = smoothstep(0.1, 0.15, sin(ro.x*4.));
    vec3 cc = .4 + .34*cos(2.*pi*(vec3(0.7, 4.8, 0.5)*floor(ro.z)));

    col = mix(vec3(.0), cc, c)*dif;
    col = mix(vec3(0), col,  smoothstep(9.9, 10.1, 30./abs(ro.x-4.33)));

	return col;   
}



void mainImage( out vec4 f, in vec2 u ){
    R = iResolution.xy;
    vec2 uv = vec2(u.xy - 0.5*R.xy)/R.y;

    vec3 rd = normalize(vec3(uv, 1.0));
    vec3 ro = vec3(0.5,6.6 , -6.);

    rd.yz*=rot(.4);
    rd.xz*=rot(-.75);

    float t = march(rd, ro);

    ro += rd*t;

    vec3 n = normal(ro);
    vec3 col = render(ro, rd, n, u, t);

    col*=smoothstep(.52, .2, uv.y);
    f = vec4(sqrt(clamp(col, .0, 1.)), 1.);

}

