vec3 ACESFilm(vec3 x)
{
    float a = 2.51;
    float b = 0.03;
    float c = 2.43;
    float d = 0.59;
    float e = 0.14;
    return (x*(a*x+b))/(x*(c*x+d)+e);
}

vec3 HDRmapping(vec3 color, float exposure)
{
	// Exposure tone mapping
    vec3 mapped = ACESFilm(color * exposure);
    // Gamma correction 
    return pow(mapped, vec3(1.0 / 2.2));
}

vec4 ambient_occlusion(in vec4 pos, inout vec4 norm)
{	
	vec3 pos0 = pos.xyz;

	float occlusion_angle = 0.;

	//step out
    vec3 color = vec3(0.);
    float mind =  max(0.05*norm.w, sphere_rad*0.5);
	pos.xyz += mind*norm.xyz;
    norm.xyz = cosdistr(norm.xyz, 0.01);
	//march in the direction of the normal
	for(int i = 0; i < 2; i++)
	{
        pos.w = abs(map(pos.xyz));
		pos.xyz += pos.w*norm.xyz;

		norm.w = length(pos0 - pos.xyz);

		occlusion_angle += clamp((pos.w - mind)/(norm.w),0.,1.);
	}
     color += 2.*ambient_sky_color(norm.xyz)  + 5.*texture(ch2, norm.xzy).xyz;
	return vec4(color*(occlusion_angle*0.65+0.35), occlusion_angle)/2.;
}




vec3 lighting(vec4 color, vec2 pbr, vec4 pos, vec4 dir, vec4 norm, float shadow) 
{
	vec3 albedo = color.xyz;
	norm.w = dir.w;
    vec3 N = norm.xyz;
	vec4 ambient_color = ambient_occlusion(pos, norm);

	float metallic = pbr.x;
	vec3 F0 = vec3(0.04); 
	F0 = mix(F0, albedo, metallic);

	//reflectance equation
	vec3 Lo = vec3(0.0);
	vec3 V = -dir.xyz;


	{ //ambient occlusion contribution
		float roughness = pbr.y;
		vec3 L = normalize(norm.xyz);
		vec3 H = normalize(V + L);
		vec3 radiance = ambient_color.xyz;        

		// cook-torrance brdf
		float NDF = DistributionGGX(N, H, roughness);        
		float G   = GeometrySmith(N, V, L, roughness);      
		vec3 F    = fresnelSchlick(max(dot(H, V), 0.0), F0);       

		vec3 kS = F;
		vec3 kD = vec3(1.0) - kS;
		kD *= 1.0 - metallic;	  

		vec3 numerator    = NDF * G * F;
		float denominator = 4.0 * max(dot(N, V), 0.0) * max(dot(N, L), 0.0);
		vec3 specular     = numerator / max(denominator, 0.001);  

		// add to outgoing radiance Lo
		float NdotL = max(dot(N, L), 0.0);                
		Lo += (kD * albedo / PI + specular) * radiance * NdotL;
	}

	vec3 sun_color = sky_color(light);

	{ //light contribution
		float roughness = pbr.y;
		vec3 L = normalize(light);
		vec3 H = normalize(V + L);
		vec3 radiance = sun_color*shadow;        

		// cook-torrance brdf
		float NDF = DistributionGGX(N, H, roughness);        
		float G   = GeometrySmith(N, V, L, roughness);      
		vec3 F    = fresnelSchlick(max(dot(H, V), 0.0), F0);       

		vec3 kS = F;
		vec3 kD = vec3(1.0) - kS;
		kD *= 1.0 - metallic;	  

		vec3 numerator    = NDF * G * F;
		float denominator = 4.0 * max(dot(N, V), 0.0) * max(dot(N, L), 0.0);
		vec3 specular     = numerator / max(denominator, 0.001);  

		// add to outgoing radiance Lo
		float NdotL = max(dot(N, L), 0.0);                
		Lo += (kD * albedo / PI + specular) * radiance * NdotL;
	}

	return Lo;
}

