//
// 2020 - A celebration of new year by Philippe Desgranges
// Email: Philippe.desgranges@gmail.com
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
//

// Note: This shader is a fairly standard 2D composition with two layers. The digits
// are produced with bespoke signed distance functions (the fact that 2020 has only two diferent
// digits made the process easier).
// The background is itslef a composition of 3 layers of cellular bokeh with some color tweaks
// similar to techniques shown in BigWings tutorials.
//
// There is no huge technical feat but I wanted to create a warm and colorfull image.
// Tell me what you think :D


#define S(a,b,c) smoothstep(a,b,c)

// outputs a colored shape with a white border from distance field (RGBA premultiplied)
vec4 border(vec3 color, float dist)
{
    vec4 res;

    float aa = 30. / iResolution.x;

    res.a = S(0.25 + aa, 0.25, dist); 
    res.rgb = mix(color, vec3(res.a),  S(0.1, 0.1 + aa, dist)); 

    return res;
}

// Blend a premultiplied rbga color onto rgb
vec3 premulBlend(vec4 src, vec3 dst)
{
    return dst * (1.0 - src.a) + src.rgb;
}

// Blend a premultiplied rbga color onto rgba (accurate alpha handling)
vec4 premulBlend(vec4 src, vec4 dst)
{
    vec4 res;
    res.rgb = dst.rgb * (1.0 - src.a) + src.rgb;
    res.a = 1.0 - (1.0 - src.a) * (1.0 - dst.a); 

    return res;
}


// Distance field to the digit 0
float zeroDst(vec2 uv)
{
    float dist;

    uv.y -= 0.5;

    if (uv.y > 0.0) // upper part
    {
        uv.y = pow(uv.y, 1.8);
    	dist = length(uv);
    }
    else if (uv.y > -1.1) // middle part
    {
        dist = abs(uv.x);
    }
    else  // lower part
    {
        uv.y += 1.1;
        uv.y = pow(-uv.y, 1.8);
    	dist = length(uv);
    }

    return (abs(dist - 0.725) - 0.275);
}

// a box distance function
float box(vec2 p, vec2 b )
{
	vec2 q = abs(p) - b;
	return length(max(q,0.0)) + min(max(q.x,q.y),0.0);
}

// Distance field to the digit 2
float twoDst(vec2 uv)
{
    uv.y -= 0.5;

    float topBar = box((uv + vec2(0.725, 0.0)) * vec2(1.0, 1.4), vec2(0.275, 0.0));

    if (uv.y > 0.0) // Top 'curve'
    {
        uv.y = pow(uv.y, 1.8);
    	float dist = length(uv);
        return max(-topBar, (abs(dist - 0.725) - 0.275));
    }
    else
    {
        float bottomBar = box((uv + vec2(0.0, 1.83)) * vec2(1.0, 1.4), vec2(0.95, 0.299));

        float two = min(topBar, bottomBar);

        if (uv.y > -1.8)
        {    
            float curve = (cos(uv.y * 2.0) - 1.0) * 0.7;
            float x = 0.0 + uv.x - curve;
            float mid = abs(uv.y + 0.4) * 0.7;
            float x2 = 0.0 + uv.x - curve - mid * mid * 0.15 + 0.01;

            two = min(two, max(-x + 0.45, x2 -1.0));
        }
    	return two;
    }

}


// Coordinate transform from global uv space to charcter space with poition and rotation
vec2 letterUVs(vec2 uv, vec2 pos, float angle)
{
    float c = sin(angle);
    float s = cos(angle);
    float sc = 1.35;
    uv -= pos;
    return uv.x * vec2(s * sc, c) + uv.y * vec2(-c * sc, s);
}

float shadowsIntensity = 0.74;
float shadowRadius = 1.1;

// Character two with outline and shadow (premultiplied RGBA)
vec4 twoCol(vec2 uv, vec3 col, vec2 pos, float angle)
{
    uv = letterUVs(uv, pos, angle);
    vec4 res = border(col, twoDst(uv));

    uv.y += 0.14;
    res.a = min(res.a +  S(shadowRadius, -1.0, twoDst(uv)) * shadowsIntensity, 1.0);

    return res;
}

// Character zero with outline and shadow (premultiplied RGBA)
vec4 zeroCol(vec2 uv, vec3 col, vec2 pos, float angle)
{
    uv = letterUVs(uv, pos, angle);
    vec4 res = border(col, zeroDst(uv));

    uv.y += 0.14;
    res.a = min(res.a +  S(shadowRadius, -1.0, zeroDst(uv)) * shadowsIntensity, 1.0);

    return res;
}


vec3 red = vec3(0.9, 0.01, 0.16);
vec3 yellow = vec3(0.96, 0.70, 0.19); // 248, 181, 51
vec3 green = vec3(0.00, 0.63, 0.34);  //1, 162, 88
vec3 blue = vec3(0.01, 0.57, 0.76);   //5, 142, 197

// 2020 with colors and shadows (premultiplied rgba)
vec4 yearCol(vec2 uv)
{
    float angle = sin(iTime) * 0.3;

    vec4 date = twoCol(uv, red, vec2(-2.5, 0.0), angle);
    date = premulBlend(zeroCol(uv, yellow, vec2(-0.8, 0.0), angle), date);
    date = premulBlend(twoCol(uv, green, vec2(0.8, 0.0), angle), date);
    date = premulBlend(zeroCol(uv, blue, vec2(2.5, 0.0), angle), date);

    return  date;
}

// Borrowed from BigWIngs (random 1 -> 4)
vec4 N14(float t) {
	return fract(sin(t*vec4(123., 104., 145., 24.))*vec4(657., 345., 879., 154.));
}


// Compute a randomized Bokeh spot inside a grid cell
float bokehSpot(vec2 uv, vec2 id, float decimation)
{
    float accum = 0.0;

    for (float x = -1.0; x <= 1.0; x += 1.0)
    {
        for (float y = -1.0; y <= 1.0; y += 1.0)
        {
            vec2 offset = vec2(x, y);
            vec2 cellId = id + offset;
            vec4 rnd = N14(mod(cellId.x, 300.0) * 25.3 + mod(cellId.y, 300.0) * 6.67);

            vec2 cellUV = uv - offset + rnd.yz * 0.5;

            float dst = length(cellUV);

            float radSeed = sin(iTime * 0.02 + rnd.x * 40.0);
            float rad =  (abs(radSeed) - decimation) / (1.0 - decimation);

            float intensity = S(rad, rad - 0.15, dst);

            accum += intensity;
        }
    }

    return accum;
}

// Computes a random layer of bokeh spots
float bokehLayer(vec2 uv, float decimation)
{
    vec2 id = floor(uv);
    vec2 cellUV = (uv - id) - vec2(0.5, 0.5) ;

    float intensity = bokehSpot(cellUV, id, decimation);

    return intensity;
}


// Computes the bokeh background
vec3 bokeh(vec2 uv)
{
    //accumulates several layers of bokeh
    float intensity = bokehLayer(uv * 1.4 + vec2(iTime * 0.3, 0.0), 0.9) * 0.2;
    //intensity += bokehLayer(uv * 0.8 + vec2(200.0 + iTime * 0.3, 134.0), 0.9) * 0.3;
    intensity += bokehLayer(uv * 0.5 + vec2(0.0 + iTime * 0.3, 334.0), 0.95) * 0.15;  
    intensity += bokehLayer(uv * 3.0 + vec2(iTime * 0.3, 99.0), 0.95) * 0.05;

    float cDist = max(0.0, 1.0 - length(uv) * 0.05);

    intensity = cDist + intensity;

    // Vary color with intensity
    vec3 chroma = mix(vec3(1.0, 1.0, 0.4), vec3(1.0, 0.7, 0.5), uv.y * 0.1 + 0.4 + intensity * 0.4);

    return chroma * intensity;
}


// Final composition
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    uv *= 3.0;

    vec4 dateCol =  yearCol(uv); // 2020

    vec3 bg = bokeh(uv);

    //add a bit of light
    dateCol.rgb -= uv.y * 0.15 * dateCol.a;
    bg.rgb -= uv.y * 0.03;

    // blend 2020 and BG
    vec3 col = premulBlend(dateCol, bg);

    // Gamma correction to make the image warmer
    float gamma = 0.8;
    col.rgb = pow(col.rgb, vec3(gamma));

    fragColor = vec4(col,1.0);
}

#define MAX_STEPS 100
#define MAX_DIST 100.0
#define SURF_DIST 0.01

//
float sdSphere(vec3 point, vec3 offset, float radius) {
	return length(point - offset) - radius;
}

//
float sdPlane(vec3 point, vec3 offset, vec3 normal) {
	return dot(point - offset, normal.xyz);
}

//
float GetDist(vec3 point) {

    float sphereDistance = sdSphere(point, vec3(0, 1, 6), 1.0);
    float planeDistance = sdPlane(point, vec3(0, 0, 6), normalize(vec3(0, 1, -0.4)));

    float minDistance = min(sphereDistance, planeDistance);

    return minDistance;
}

//
float RayMarch(vec3 rayOrigin, vec3 rayDrection) {
	float distanceFromOrigin = 0.0;

    for(int i = 0; i < MAX_STEPS; i++) {
    	vec3 point = rayOrigin + rayDrection * distanceFromOrigin;
        float minDistance = GetDist(point);
        distanceFromOrigin += minDistance;

        if(distanceFromOrigin > MAX_DIST || minDistance < SURF_DIST) 
            break;
    }

    return distanceFromOrigin;
}

//
vec3 GetNormal(vec3 hitPoint) {
	float hitDistance = GetDist(hitPoint);
    vec2 epsilon = vec2(.01, 0);

    vec3 hitNormal = hitDistance - vec3(
        	GetDist(hitPoint - epsilon.xyy),
        	GetDist(hitPoint - epsilon.yxy),
        	GetDist(hitPoint - epsilon.yyx)
    	);

    return normalize(hitNormal);
}

//
float GetDiffuseLight(vec3 hitPoint) {
	vec3 lightPos = vec3(0, 5, 6);
    lightPos.xz += vec2(sin(iTime), cos(iTime)) * 10.0;

    vec3 lightDirection = normalize(lightPos - hitPoint);
    vec3 hitNormal = GetNormal(hitPoint);

    float diffuseLight = dot(hitNormal, lightDirection);
    diffuseLight = clamp(diffuseLight, 0.0, 1.0);

    float shadowTolightDist = RayMarch(hitPoint + hitNormal * SURF_DIST * 2.0, lightDirection);
    if(shadowTolightDist < length(lightPos - hitPoint)) 
        diffuseLight *= .1;

    return diffuseLight;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;

    vec3 rayOrigin = vec3(0, 3, -3);
    vec3 rayDirection = normalize(vec3(uv.x, uv.y, 1));

    float dist = RayMarch(rayOrigin, rayDirection);
    vec3 hitPoint = rayOrigin + dist * rayDirection;
    float diffuseLight = GetDiffuseLight(hitPoint);

    vec3 col = vec3(diffuseLight);

    fragColor = vec4(col,1.0);
}

// All the distance functions from:https://iquilezles.org/articles/distfunctions
// raymarching based from https://www.shadertoy.com/view/wdGGz3
#define USE_MOUSE 0
#define MAX_STEPS 200
#define MAX_DIST 100.
#define SURF_DIST .001
#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define matRotateX(rad) mat3(1,0,0,0,cos(rad),-sin(rad),0,sin(rad),cos(rad))
#define matRotateY(rad) mat3(cos(rad),0,-sin(rad),0,1,0,sin(rad),0,cos(rad))
#define matRotateZ(rad) mat3(cos(rad),-sin(rad),0,sin(rad),cos(rad),0,0,0,1)
#define PI 3.14159265


vec4 combine(vec4 val1, vec4 val2 ){
    return (val1.w < val2.w)?val1:val2;
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
    return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float sdCappedCylinder( vec3 p, float h, float r )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

// https://www.shadertoy.com/view/wdGXzK
// http://mercury.sexy/hg_sdf/
vec2 pModPolar(inout vec2 p, float repetitions, float fix) {
    float angle = 2.0*PI/repetitions;
    float a = atan(p.y, p.x) + angle/2.;
    float r = length(p);
    float c = floor(a/angle);
    a = mod(a,angle) - angle/2.*fix;
    p = vec2(cos(a), sin(a))*r;

    return p;
}

// puma logo
float dPumaLogo(vec3 p, float artifact) {
    float y = 0.25;
    // stripe
    mat3 rot = matRotateX(radians(90.0));
    float dstripe0 = sdCappedCylinder((p-vec3(0.0,1.0+y,0.0))*rot,1.0,0.1);
    float dstripe1 = sdCappedCylinder((p-vec3(-1.15,1.0+y,0.0))*rot,1.0,0.2);

    mat3 rotZ = matRotateZ(radians(6.0));
    vec3 bp = (p-vec3(-0.63,1.88+y,0.0));

    const float k = -1.0;
    float c = cos(k*bp.x);
    float s = sin(k*bp.x);
    mat2  m = mat2(c,-s,s,c);
    vec3  q = vec3(m*bp.xy,bp.z);

    mat3 rotZ2 = matRotateZ(radians(3.5));
    vec3 bp2 = (p-vec3(-0.85,1.87+y,0.0));
    float sc = mix(1.5,0.77,smoothstep(-1.0,1.0,bp2.x));
    bp2.y *= sc;    

    float dstripe2 = sdBox(q*rotZ,vec3(1.0,0.15,0.1))*0.6;
    float dstripe3 = sdBox(bp2*rotZ2,vec3(1.0,0.15,0.1))*0.6;

    float dstripe = min(max(-p.y+1.3,max(-dstripe1,dstripe0 )),min(max(-p.x-0.5, dstripe2),dstripe3));

    // logo outline
    p.x *= 0.55;
    float doutline0 = sdCappedCylinder((p-vec3(-0.2,1.55+y,0.0))*rot,1.1,0.1)*artifact;
    float doutline1 = sdCappedCylinder((p-vec3(-0.2,1.55+y,0.0))*rot,1.0,0.2)*artifact;
    float doutline = max(-doutline1,doutline0);
    return min(dstripe,doutline);
}

vec4 pumaLogo(vec3 p){
    p.y -= iTime*5.0;
    p.y = mod(p.y,10.0)-5.0;
    p *=  matRotateY(radians(-90.0));
    p*=0.8;


    float dlogo = dPumaLogo(p,0.3);

    return vec4(vec3(1.0),dlogo*0.9);
}

float dTwoZeroX2(vec3 p) {
    vec3 prevP = p;
    p.x = mod(p.x,2.8)-1.4;

    vec3 twoPos = vec3(-0.7,0.0,0.0);
    float dtwo = sdBox(p+twoPos,vec3(0.6,1.0,0.1));
    float dtwo1 = sdBox(p+twoPos+vec3(-0.6,-0.4,0.0),vec3(0.8,0.25,0.2));
    float dtwo2 = sdBox(p+twoPos+vec3(0.6,0.45,0.0),vec3(0.8,0.25,0.2));
    float dresTwo = max(-dtwo2,max(-dtwo1,dtwo))*0.9;

    vec3 zeroPos = vec3(0.7,0.0,0.0);
    float dzero = sdBox(p+zeroPos,vec3(0.6,1.0,0.1));
    float dzero1 = sdBox(p+zeroPos,vec3(0.3,0.7,0.2));
    float dresZero = max(-dzero1,dzero)*0.9;

    p = prevP;

    float dres = max(sdBox(p,vec3(2.8,2.0,0.3)),min(dresTwo,dresZero));
    return dres*0.9;
}

float cubicInOut(float t) {
    return t < 0.5
    ? 4.0 * t * t * t
    : 0.5 * pow(2.0 * t - 2.0, 3.0) + 1.0;
}

// A smart way to control the animation. function is from "http://qiita.com/gaziya5/items/29a51b066cb7d24983d6"
float animscene(in float t, float w, float s) {
    return clamp(t - w, 0.0, s) / s;  
}

vec4 GetDist(vec3 p) {
    vec3 prevP = p;

    p.xz = pModPolar(p.xz,10.0,1.0);
    p.x -= 20.0;

    vec4 logo = pumaLogo(p);

    p = prevP;
    float d2020 = dTwoZeroX2(p);
    float dlogo = dPumaLogo(p+vec3(0.0,1.7,0.0),0.6);

    float animTime = mod(iTime,6.0);
    float morphVal = 0.0;
    morphVal += cubicInOut(animscene(animTime, 1.0, 1.0))*1.0;
    morphVal -= cubicInOut(animscene(animTime, 3.0, 1.0))*1.0;

    vec4 logo2 = vec4(vec3(1.0),mix(dlogo,d2020,morphVal));

    vec4 model = combine(logo,logo2);
    return model;
}

vec4 RayMarch(vec3 ro, vec3 rd) {
    vec4 dO= vec4(0.0);

    for(int i=0; i<MAX_STEPS; i++) {
        vec3 p = ro + rd*dO.w;
        vec4 dS = GetDist(p);
        dO.w += dS.w;
        dO.xyz = dS.xyz;
        if(dO.w>MAX_DIST || dS.w<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    float d = GetDist(p).w;
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy).w,
        GetDist(p-e.yxy).w,
        GetDist(p-e.yyx).w);

    return normalize(n);
}

float shadowMap(vec3 ro, vec3 rd){
    float h = 0.0;
    float c = 0.001;
    float r = 1.0;
    float shadow = 0.5;
    for(float t = 0.0; t < 30.0; t++){
        h = GetDist(ro + rd * c).w;
        if(h < 0.001){
            return shadow;
        }
        r = min(r, h * 16.0 / c);
        c += h;
    }
    return 1.0 - shadow + r * shadow;
}

float GetAmbientOcclusion(vec3 p, vec3 n)
{
    const int steps = 4;
    const float delta = 0.15;

    float a = 0.0;
    float weight = 4.;
    for(int i=1; i<=steps; i++) {
        float d = (float(i) / float(steps)) * delta; 
        a += weight*(d - RayMarch(p + n*d,n).w);
        weight *= 0.5;
    }
    return clamp(1.0 - a, 0.0, 1.0);
}

vec2 GetLight(vec3 p) {
    vec3 lightPos = vec3(2,8,3);
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l)*.5+.5, 0., 1.);
    float d = RayMarch(p+n*SURF_DIST*2., l).w;

    float ao = GetAmbientOcclusion(p,n);

    float lambert = max(.0, dot( n, l))*0.1;

    float shadow = shadowMap(p + n * 0.001, l);

    return vec2((lambert+dif*ao),max(0.9, shadow)) ;
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}

vec3 bg(vec2 p){
    vec3 col = .94*max(mix(vec3(0.0,0.5,0.0)+(.1-length(p)/3.),vec3(1),.1),0.);
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;

    vec3 col = vec3(0);

    vec3 ro = vec3(0, 4, -5);
    #if USE_MOUSE == 1
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);
    #else
    ro.xz *= Rot(radians(iTime*-30.0));
    ro.yz *= Rot(radians(30.0));
    #endif

    vec3 rd = R(uv, ro, vec3(0,1,0), 1.);

    vec4 d = RayMarch(ro, rd);

    if(d.w<MAX_DIST) {
        vec3 p = ro + rd * d.w;
        vec3 n = GetNormal(p);
        vec3 r = reflect(rd,n);
        float spec = pow(max(0.0,r.y),20.);
        float dif = dot(n,normalize(vec3(1,2,3)))*0.5+0.5;
        col = mix(bg(r.xy),vec3(dif),0.5)+spec;
    } else {
        // background
        col = bg(uv);
    }

    fragColor = vec4(col,1.0);
}

/**
 This shader is basically an improvement upon a previous fireworks shader I did a while
 ago (www.shadertoy.com/view/Ws3SRS). Here, each firework spawns at a random position
 instead of the just following the same pattern in a loop, so the fireworks show can be
 watched and enjoyed infinitely. The fireworks spawn from behind the buildings, but in
 front of the mountains. I hope everyone finds watching this relaxing and enjoyable!

 I wish a very happy and a prosperous new year to everyone!
*/

#define PI  3.141592653589793
#define TAU 6.283185307179586
#define EPS 1e-4

// Helper macros 
#define C(x) clamp(x, 0., 1.)
#define S(a, b, x) smoothstep(a, b, x)
#define F(x, f) (floor(x * f) / f)

// Fireworks control variables
#define FIREWORK_COUNT 6
#define FIREWORK_DURATION 8.5
#define FIREWORK_LOW .75
#define FIREWORK_HIGH 1.05
#define ROCKET_PARTICES 16
#define ROCKET_DURATION 1.5
#define FLASH_DURATION ROCKET_DURATION + .2 
#define THRUSTER_SPEED .25
#define EXPLOSION_STRENGTH .025;
#define EXPLOSION_PARTICLES 128

// Hash functions by Dave_Hoskins
vec2 hash21(float p)
{
	vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
	p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx + p3.yz) * p3.zy);

}

vec3 hash31(float p)
{
	uvec3 n = uint(int(p)) * uvec3(1597334673U, 3812015801U, 2798796415U);
	n = (n.x ^ n.y ^ n.z) * uvec3(1597334673U, 3812015801U, 2798796415U);
	return vec3(n) * (1. / float(0xffffffffU));
}

float hash11(float p)
{
	uvec2 n = uint(int(p)) * uvec2(1597334673U, 3812015801U);
	uint q = (n.x ^ n.y) * 1597334673U;
	return float(q) * (1. / float(0xffffffffU));
}

// Function to remap a value from [a, b] to [c, d]
float remap(float x, float a, float b, float c, float d)
{
    return (((x - a) / (b - a)) * (d - c)) + c;
}

// Noise (from iq)
float noise (in vec3 p) {
	vec3 f = fract (p);
	p = floor (p);
	f = f * f * (3. - 2. * f);
	f.xy += p.xy + p.z * vec2 (37., 17.);
	f.xy = texture (iChannel0, (f.xy + .5) / 256., -256.).yx;
	return mix (f.x, f.y, f.z);
}

// Tiny fbm
float fbm (in vec3 p) {
	return noise (p) + noise (p * 2.) / 2. + noise (p * 4.) / 4.;
}

// Building window lights from www.shadertoy.com/view/wtt3WB
float windows (vec2 uv, float offset)
{
    vec2 grid = vec2(20., 1.);
    uv.x += offset;
    float n1 = fbm((vec2(ivec2(uv * grid)) + .5).xxx);
    uv.x *= n1 * 6.;
    vec2 id = vec2(ivec2(uv * grid)) + .5;
    float n = fbm(id.xxx);
    vec2 lightGrid = vec2(79. * (n + .5), 250. * n);
    float n2 = fbm((vec2(ivec2(uv * lightGrid + floor(iTime * .4) * .2)) + .5).xyx);
    vec2 lPos = fract(uv * lightGrid);
    n2 = (lPos.y < .2 || lPos.y > .7) ? 0. : n2;
    n2 = (lPos.x < .5 || lPos.y > .7) ? 0. : n2;
    n2 = smoothstep(.225, .5, n2);
	return (uv.y < n - 0.01) ? n2 : 0.;
}

// Building skyline 
float buildings(vec2 st)
{
    // An fbm style amalgamation of various cos functions
    float b = .1 * F(cos(st.x*4.0 + 1.7), 1.0);
    b += (b + .3) * 0.3 * F(cos(st.x*4.-0.1), 2.0);
    b += (b-.01) * 0.1 * F(cos(st.x*12.0), 4.);
    b += (b-.05) * 0.3 * F(cos(st.x*24.0), 1.0);
    return C((st.y + b - .1) * 100.);
}

// Twinkly stars, derived and modified from: www.shadertoy.com/view/4s33zf
float stars(vec2 st, vec2 fragCoord)
{
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;
    uv.y += .3;
    uv.y = abs(uv.y);
    float t = iTime * .1;
    vec2 h = pow(hash21(uv.x * iResolution.y + uv.y), vec2(50.));
    float twinkle = sin((st.x + t + cos(st.y * 50. + t)) * 25.);
    twinkle *= cos((st.y * .187 - t * 4.16 + sin(st.x * 11.8 + t * .347)) * 6.57);
    twinkle = twinkle * .5 + .5;
    return h.x * h.y * twinkle * 1.5;
}

// Fireworks improved upon my previous shader: https://www.shadertoy.com/view/Ws3SRS
vec3 fireworks(vec2 st)
{
	vec2 fireworkPos, particlePos;
    float radius, theta, radiusScale, spark, sparkDistFromOrigin, shimmer,
        shimmerThreshold, fade, timeHash, timeOffset, rocketPath;
    vec3 particleHash, fireworkHash, fireworkCol, finalCol;
    for (int j = 0; j < FIREWORK_COUNT; ++j)
    {
        timeHash = hash11(float(j + 1) * 9.6144 + 78.6118);
        timeOffset = float(j + 1) + float(j + 1) * timeHash;
        // This hash changes after each firework cycle (rocket + explosion)
        fireworkHash = hash31(471.5277 * float(j) + 1226.9146
			+ float(int((iTime+timeOffset) / FIREWORK_DURATION))) * 2. - 1.;
        fireworkCol = fireworkHash * .5 + .5;
    	fireworkHash.y = remap(fireworkHash.y, -1., 1., FIREWORK_LOW, FIREWORK_HIGH);
        // Random firework x coordinate but confined to a certain column based on j
        fireworkHash.x = ((float(j) + .5 + fireworkHash.x * .25)
			/ float(FIREWORK_COUNT)) * 2. - 1.;
        // Duration of each firework with a random start time
        float time = mod(iTime + timeOffset, FIREWORK_DURATION);
        if (time > ROCKET_DURATION)
        {
            fireworkPos = vec2(fireworkHash.x, fireworkHash.y);
            for (int i = 0; i < EXPLOSION_PARTICLES; ++i)
            {
                // Unique hash that yeilds a separate spread pattern for each explosion
                particleHash = hash31(float(j) * 1291.1978 + float(i) 
					* 1619.8196 + 469.7119);
                theta = remap(particleHash.x, 0., 1., 0., TAU); // [0, 2.*PI]
                radiusScale = particleHash.y * EXPLOSION_STRENGTH;
                // Radius expands exponentially over time, i.e. explosion effect
                radius = radiusScale * time * time;
                particlePos = vec2(radius * cos(theta), radius * sin(theta));
                // particlePos.y -= 8. * pow(particlePos.x, 4.); 
                // willow effect
                particlePos.y -= max(EPS, pow(length(particlePos) - 0.05, 2.0) * 1.25);
                // slightly descend back down due to gravity
                particlePos.y -= pow(radius / radiusScale, 3.0) * 4e-5; 
                // bloom
                spark = .0003 / pow(length(st - particlePos - fireworkPos), 1.7);
                sparkDistFromOrigin = 2. * length(fireworkPos - particlePos);
                // Shimmering effect for explosion particles
                shimmer = max(0., sqrt(sparkDistFromOrigin) 
                    * (sin((iTime * max(1.3, fireworkHash.z * 2.0) + particleHash.y * TAU) * 18.)));
                shimmerThreshold = FIREWORK_DURATION * .9;
                // Fade after a certain time threshold
                fade = C((FIREWORK_DURATION * 2.) * radiusScale - radius);
                finalCol += mix(spark, spark * shimmer, smoothstep(shimmerThreshold
					* radiusScale, (shimmerThreshold + 1.) * radiusScale , radius))
                    * fade * fireworkCol;
            }

            // Initial instant flash for the explosion
            if(time < FLASH_DURATION)
            	finalCol += spark / (.01 + mod(time, ROCKET_DURATION));
        }
        else
        {
            rocketPath = mod(time, ROCKET_DURATION) / ROCKET_DURATION;
            // ease out sine
            rocketPath = sin(rocketPath / (ROCKET_DURATION * .75) * PI * .5);
            fireworkPos = vec2(fireworkHash.x, 
                    rocketPath * fireworkHash.y);
            // Slight random wiggle for the rocket's path
    		fireworkPos.x += sin(st.y * 50. + time) * fireworkCol.z * .0035;

            // Rockets flying before the explosion
            for (int i = 0; i < ROCKET_PARTICES; ++i)
            {
                particleHash = hash31((float(i) * 603.6837) + 1472.3486);
                // rocket trail size
                float t = time * (2. - time);
                radius = mod(time + particleHash.y, THRUSTER_SPEED) / THRUSTER_SPEED
					* particleHash.z * .1;
                // Confine theta to a small value for a vertical thrust effect
                theta = remap(particleHash.x, 0., 1., 0., PI * .1) + PI * 1.45;
                particlePos = vec2(radius * cos(theta), radius * sin(theta));
                finalCol += 8e-5 / pow(length(st - particlePos - fireworkPos), 1.1)
                    * mix(vec3(1.4, .7, .2), vec3(1.4), radius * 16.);
            }
        }
    }  
    return finalCol;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (2. * fragCoord - iResolution.xy) / min(iResolution.y, iResolution.x);
    uv.y += .3; // shift the horizon a bit lower
    float reflection = 0.;

    if (uv.y < 0.)
    {
        reflection = 1.;
        // watery distortion in the lake (improved)
        /**
        uv.x += sin((uv.y * 64. + sin(iTime) * .2)
			* cos(uv.y * 128. - cos(iTime) * .1)) * .15;
		*/

        // slightly better looking water waves
        uv.x += cos(uv.y * 192. - iTime * .6) * sin(uv.y * 96. + iTime * .75) * .042;
    }

    // Our special uv coord that gives us reflection effect for pratically free
    vec2 st = vec2(uv.x, abs(uv.y));
    vec3 col = vec3(0.);

    // Background mountain
    float mountain = sin(1.69 * st.x * 1.38 * cos(2.74 * st.x) + 4.87
		* sin(1.17 * st.x)) * .1 - .18 + st.y;
    mountain = C(S(-.005, .005, mountain));

    float building = buildings(st);

    // Finally blend everything together

    // Sky color
    col += vec3(.18 - st.y * .1, .18 - st.y * .1, .1 + st.y * .03);
    // Blend the mountain and the sky
    col = col * mountain + vec3(.1 - st.y * .1, .1 - st.y * .1, .08) * (1. - mountain);
    // Occlude the mountain with the building skyline
    col *= building;

    // Yellow-ish window color tint
    col += windows(st * .8, 2.) * (1.-building) * vec3(1.2, 1., .8); 

    // Moon white circle
    float moon = smoothstep(.3, .29, length(st-vec2(1., .8)));

    //Twinkly stars, masked by the buildings, mountain, and the moon
    col += stars(st, fragCoord) * mountain * building * (1. - moon);

    // Cut the main moon circle with an offset inverted one to make a crescent
    moon *= smoothstep(.32, .48, length(st-vec2(.92, .88))) * 1.25;

    // Add the moon to the scene
    col += moon * vec3(1.2, 1.18, 1.);


    // Fireworks launch from behind the buildings, but in front of the mountains
    col += C(fireworks(st)) * (building + moon);

    // Slightly change of the reflections to watery blueish-green
    col.r -= reflection * .05;
    col.gb += reflection * .01;

    fragColor = vec4(col - hash11(fragCoord.x * fragCoord.y * 0.2 * (iTime + 50.0)) * 0.008, 1.0);
}

// Fork of "Lorentz Attractor 3D" by michael0884. https://shadertoy.com/view/3lt3WX
// 2019-12-31 20:33:24

// Fork of "Volumetric shader template" by michael0884. https://shadertoy.com/view/tlc3Wf
// 2019-12-30 20:57:24

//#define VOLUMETRIC
#define FULL_UNION
#define sphere_rad 1.5

float SDF_particle(vec3 p0, vec3 p)
{
    particle point = get(ch0, p0);
    return length(point.pos.xyz - p) - sphere_rad;
}

vec4 opunion(vec4 a, vec4 b)
{
    if(a.w < b.w) return a;
    else return b;
}

//distance estimator to the voronoi particle field
float DE(vec3 p)
{
	float de = 1e5;

   	vec3 p0 =  round(clamp(p, vec3(0), size3d));
    if(length(p - size3d*0.5) > 0.7*length(size3d)) 
        de = length(p - size3d*0.6);
    else
    {	
        de = min(de, SDF_particle(p0, p));
        de = min(de, SDF_particle(p0+vec3(1,0,0), p));
        de = min(de, SDF_particle(p0+vec3(-1,0,0), p));
        de = min(de, SDF_particle(p0+vec3(0,1,0), p));
        de = min(de, SDF_particle(p0+vec3(0,-1,0), p));
        de = min(de, SDF_particle(p0+vec3(0,0,1), p));
        de = min(de, SDF_particle(p0+vec3(0,0,-1), p));
    }

    if(de < sphere_rad*2.)
    {
		de = min(de, SDF_particle(p0+vec3(1,1,0), p));
        de = min(de, SDF_particle(p0+vec3(-1,-1,0), p));
        de = min(de, SDF_particle(p0+vec3(0,1,1), p));
        de = min(de, SDF_particle(p0+vec3(0,-1,-1), p));
        de = min(de, SDF_particle(p0+vec3(1,0,1), p));
        de = min(de, SDF_particle(p0+vec3(-1,0,-1), p));
        //reduces the number of artifacts even further, since it is using all neighboring voxels in a 3*3*3 cube
        #ifdef FULL_UNION
            de = min(de, SDF_particle(p0+vec3(1,-1,0), p));
            de = min(de, SDF_particle(p0+vec3(-1,1,0), p));
            de = min(de, SDF_particle(p0+vec3(0,-1,1), p));
            de = min(de, SDF_particle(p0+vec3(0,1,-1), p));
            de = min(de, SDF_particle(p0+vec3(-1,0,1), p));
            de = min(de, SDF_particle(p0+vec3(1,0,-1), p));
            de = min(de, SDF_particle(p0+vec3(1,-1,0), p));
            de = min(de, SDF_particle(p0+vec3(-1,1,0), p));
            de = min(de, SDF_particle(p0+vec3(0,1,-1), p));
            de = min(de, SDF_particle(p0+vec3(0,-1,1), p));
            de = min(de, SDF_particle(p0+vec3(1,0,-1), p));
            de = min(de, SDF_particle(p0+vec3(-1,0,1), p));
            de = min(de, SDF_particle(p0+vec3(1,1,1), p));
            de = min(de, SDF_particle(p0+vec3(1,1,-1), p));
            de = min(de, SDF_particle(p0+vec3(1,-1,1), p));
            de = min(de, SDF_particle(p0+vec3(1,-1,-1), p));
            de = min(de, SDF_particle(p0+vec3(-1,1,1), p));
            de = min(de, SDF_particle(p0+vec3(-1,1,-1), p));
            de = min(de, SDF_particle(p0+vec3(-1,-1,-1), p));
        #endif
    }

    return de;
}

vec3 particle_color(vec3 p)
{
    vec4 a = vec4(1e5);
    vec3 p0 = round(p);
    for( int i=-1; i<=1; i++ )
        for( int j=-1; j<=1; j++ )
            for( int k=-1; k<=1; k++ )
    {
        vec3 dx = vec3(i,j,k);
        particle thisp = get(ch0, p0+dx);
        a = opunion(a, vec4(jet_range(thisp.pos.w, -0.1, 1.2), SDF_particle(p0+dx, p)));
    }
    return a.xyz;
}

vec3 COL(vec3 p)
{
    if(inside(p))
    {
        return particle_color(p)*exp(-0.25*pow(max(DE(p),0.),2.));
    }
    else return vec3(0.);
}

#define MAX_MARCHES 130
#define DX 1.5
#define OPACITY 0.2
vec4 ray_march_vol(vec3 p, vec3 r)
{
    vec4 color = vec4(0., 0., 0., 0.);
    vec4 background = vec4(0.1);
    for(int i = 0; i < MAX_MARCHES; i++)
    {
        vec3 tcolor = COL(p);
        float density = length(tcolor);
        float noise  =(1.+0.25*sin(1754.*length(p)+p.x));
        float d = DX*exp(-2.*min(density,2.))*noise;
        float opacity = 1.-exp(-OPACITY*density*d);
        float newa = max(color.w + (1. - color.w)*opacity,0.0001);
        color.xyz = (color.xyz*color.w + (1.-color.w)*opacity*tcolor)/newa;
        color.w = newa;
        if(1. - newa < 0.01) break;
        p += r*d;
    }
    return background + color;
}

float sdBox( vec3 p, vec3 b )
{
    vec3 d = abs(p) - b;
    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

float sdVerticalCapsule( vec3 p, float h, float r )
{
  p.y -= clamp( p.y, 0.0, h );
  return length( p ) - r;
}

float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
  vec3 pa = p - a, ba = b - a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
  return length( pa - ba*h ) - r;
}

float sdLineBox(vec3 p, vec3 b, float r)
{
    p = abs(p);
    float cx = sdCapsule(p, b, vec3(b.x,b.y,0), r);
    float cy = sdCapsule(p, b, vec3(0,b.y, b.z), r);
    float cz = sdCapsule(p, b, vec3(b.x,0, b.z), r);
    return min(min(cx, cy),cz);
}

vec3 sdcolor(vec3 p)
{
    vec3 c = vec3(0.);
    if(inside(0.8*(p-size3d*0.5) + size3d*0.5))
    {  
        c = particle_color(p);
    }
    else c = vec3(0.1);
    if(sdLineBox(p - size3d*0.5, size3d*0.5, 0.5) < 1. || p.z < 1.) c = vec3(1.);
    return c;
}

float sdPlane( vec3 p, vec4 n )
{
  // n must be normalized
  return dot(p,n.xyz) + n.w;
}

float map(vec3 p)
{
    float de_partic = DE(p); 
    de_partic = min(de_partic, sdPlane(p, vec4(0,0,1,0)));
    return min(de_partic, sdLineBox(p - size3d*0.5, size3d*0.5, 0.5));
}

vec4 calcNormal(vec3 p, float dx) {
	const vec3 k = vec3(1,-1,0);
	return   (k.xyyx*map(p + k.xyy*dx) +
			 k.yyxx*map(p + k.yyx*dx) +
			 k.yxyx*map(p + k.yxy*dx) +
			 k.xxxx*map(p + k.xxx*dx))/vec4(4.*dx,4.*dx,4.*dx,4.);
}

//usual ray marching stuff
float rayfov;
vec3 light;

#define shadow_steps 256
#define MIN_DIST 0.001
#define MAX_DIST 1000.
float shadow_march(vec4 pos, vec4 dir, float distance2light, float light_angle)
{
	float light_visibility = 1.;
	float ph = 1e5;
	float dDEdt = 0.;
	pos.w = map(pos.xyz);
	int i = 0;
	for (; i < shadow_steps; i++) {

		dir.w += pos.w;
		pos.xyz += pos.w*dir.xyz;
        float noise  =(1.+0.1*sin(1754.*length(pos)+pos.x));
		pos.w = noise*map(pos.xyz);

		float y = pos.w*pos.w/(2.0*ph);
        float d = (pos.w+ph)*0.5*(1.-dDEdt);
		float angle = d/(max(MIN_DIST,dir.w-y)*light_angle);

        light_visibility = min(light_visibility, angle);

		//minimizing banding even further
		dDEdt = dDEdt*0.75 + 0.25*(pos.w-ph)/ph;

		ph = pos.w;

        if(dir.w >= distance2light)
		{
			break;
		}

		if(dir.w > MAX_DIST || pos.w < max(0.5*rayfov*dir.w, MIN_DIST))
		{
			return 0.;
		}
	}

	if(i >= shadow_steps)
	{
		light_visibility=0.;
	}
	//return light_visibility; //bad
	light_visibility = clamp(2.*light_visibility - 1.,-1.,1.);
	return  0.5 + (light_visibility*sqrt(1.-light_visibility*light_visibility) + asin(light_visibility))/3.14159265; //looks better and is more physically accurate(for a circular light source)
}

float ambient_occlusion(in vec4 pos, in vec4 norm)
{	
	vec3 pos0 = pos.xyz;

	float occlusion_angle = 0.;
	vec3 direction = normalize(norm.xyz);
	//step out
	pos.xyz += 0.1*norm.w*direction;
	//march in the direction of the normal
	for(int i = 0; i < 2; i++)
	{
		pos.xyz += pos.w*direction;
		pos.w = map(pos.xyz);

		norm.w = length(pos0 - pos.xyz);
		occlusion_angle += clamp(pos.w/norm.w,0.,1.);
	}

	occlusion_angle /= 2.; // average weighted by importance
	return (0.5-cos(3.14159265*occlusion_angle)*0.5);
}

vec4 ray_march(vec3 p, vec3 r)
{
    float td = 0.;
    int i;
    for(i = 0; i < 100; i++)
    {
    	float de = map(p);
        if(de < rayfov*td || td > MAX_DIST)
        {
            break;
        }
        p += de*r;
        td += de;
    }
    vec3 col =sdcolor(p);
    vec4 norm = calcNormal(p,MIN_DIST);
    p += r*(norm.w-rayfov*td);
    float ao = ambient_occlusion(vec4(p,0.), vec4(norm.xyz, td));
    float shad = shadow_march(vec4(p+norm.xyz*0.05,0.), vec4(light,0.), 400.,0.03);
    float ill = max(dot(norm.xyz, light)*(shad  + 0.35*ao*(1. - shad)), 0.0);
    return vec4(col*ill,1.);
}





void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    rayfov = FOV/iResolution.x;
     SCALE = floor(ar*pow(iResolution.x*iResolution.y,0.1666666));
    size3d = vec3(floor(iResolution.xy/SCALE), SCALE.x*SCALE.y);

    vec2 uv = (fragCoord - 0.5*size)/max(size.x, size.y);

    vec2 angles = vec2(2.*PI, PI)*(iMouse.xy/iResolution.xy - 0.5);

    if(iMouse.w < 1.)
    {
        angles = vec2(0.2*iTime, 0.3*sin(0.2*iTime)+0.2);
    }
    vec3 ray = getRay(angles, uv);
    vec3 cray = getRay(angles, vec2(0.));
    light = vec3(0.6,0.50,1.);
    light.z = max(light.z, 0.9);
    light = normalize(light);
    float d = sqrt(dot(vec3(size3d), vec3(size3d)))*0.55;
    vec3 pos = vec3(size3d)*0.5 - cray*d;

    // Output to screen
    vec4 t = texel(ch0, fragCoord);
    vec3 p = dim3from2(fragCoord);
    #ifdef VOLUMETRIC
   	 fragColor = 2.*ray_march_vol(pos, ray);
    #else
     fragColor = 1.5*ray_march(pos, ray);
	#endif
}

common_code:
//definitions
#define size iResolution.xy
#define texel(a, p) texelFetch(a, ivec2(p), 0)
#define pixel(a, p, s) texture(a, p/vec2(s))
#define ch0 iChannel0
#define ch1 iChannel1
#define ch2 iChannel2
#define ch3 iChannel3
#define PI 3.14159265

#define dt 2.

#define ar vec2(1.35,0.8)
vec2 SCALE;
vec3 size3d;

vec2 dim2from3(vec3 p3d)
{
    float ny = floor(p3d.z/SCALE.x);
    float nx = floor(p3d.z) - ny*SCALE.x;
    return vec2(nx, ny)*vec2(size3d.xy) + p3d.xy;
}

vec3 dim3from2(vec2 p2d)
{
    return vec3(p2d - size3d.xy*floor(p2d/size3d.xy),(floor(p2d.x/size3d.x) + SCALE.x*floor(p2d.y/size3d.y)));
}

vec4 voxel(sampler2D ch, vec3 p3d)
{
    return pixel(ch, dim2from3(p3d), textureSize(ch, 0));
}

//trilinear interpolation = linear interp between layers
vec4 trilinear(sampler2D ch, vec3 p3d)
{
    return mix(voxel(ch, vec3(p3d.xy, floor(p3d.z))),voxel(ch, vec3(p3d.xy, ceil(p3d.z))), fract(p3d.z));
}

float hash13(vec3 p3)
{
	p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

vec3 hash33(vec3 p3)
{
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy + p3.yxx)*p3.zyx);

}

bool inside(vec3 p)
{
    if(p.x > 1. && p.y > 1. && p.z > 1. && p.x < float(size3d.x)-1. && p.y < float(size3d.y)-1. && p.z < float(size3d.z)-1.)
    {
        return true;
    }
    else return false;
}

#define FOV 2.5
mat3 getCamera(vec2 angles)
{
   mat3 theta_rot = mat3(1,   0,              0,
                          0,   cos(angles.y),  sin(angles.y),
                          0,  -sin(angles.y),  cos(angles.y)); 

   mat3 phi_rot = mat3(cos(angles.x),   sin(angles.x), 0.,
        		       -sin(angles.x),   cos(angles.x), 0.,
        		        0.,              0.,            1.); 

   return theta_rot*phi_rot;
}

vec3 getRay(vec2 angles, vec2 pos)
{
    mat3 camera = getCamera(angles);
    return normalize(transpose(camera)*vec3(FOV*pos.x, 1., FOV*pos.y));
}

float minv(vec3 a)
{
    return min(min(a.x, a.y),a.z);
}

float maxv(vec3 a)
{
    return max(max(a.x, a.y),a.z);
}

float distance2border(vec3 p)
{
    vec3 a = vec3(size3d + 1.) - p;
    return min(minv(p),minv(a));
}

vec4 border_grad(vec3 p)
{
    const float dx = 0.001;
    const vec3 k = vec3(1,-1,0);
	return  (k.xyyx*distance2border(p + k.xyy*dx) +
			 k.yyxx*distance2border(p + k.yyx*dx) +
			 k.yxyx*distance2border(p + k.yxy*dx) +
			 k.xxxx*distance2border(p + k.xxx*dx))/vec4(4.*dx,4.*dx,4.*dx,4.);
}

//a rainbow colormap from Matlab
float interpolate(float val, float y0, float x0, float y1, float x1) 
{
    return (val-x0)*(y1-y0)/(x1-x0) + y0;
}

float base(float val) 
{
    if ( val <= -0.75 ) return 0.0;
    else if ( val <= -0.25 ) return interpolate( val, 0.0, -0.75, 1.0, -0.25 );
    else if ( val <= 0.25 ) return 1.0;
    else if ( val <= 0.75 ) return interpolate( val, 1.0, 0.25, 0.0, 0.75 );
    else return 0.0;
}

vec3 jet_colormap(float v)
{
    return vec3(base(v - 0.5),base(v),base(v + 0.5));
}

vec3 jet_range(float v, float a, float b)
{
    return jet_colormap(2.*clamp((v-a)/(b-a),0.,1.) - 1.);
}


//encoding 2 values in one number
float scaleFactor = 65530.0;
float cp = 256.0 * 256.0;


/* packs given two floats into one float */
vec4 enc(vec4 x, vec4 y) {
    vec4 x1 = floor(x * scaleFactor);
    vec4 y1 = floor(y * scaleFactor);
    vec4 f = (y1 * cp) + x1;
    return f;
}

/* unpacks given float to two floats */
void dec(in vec4 f, out vec4 x, out vec4 y){
  vec4 dy = floor(f / cp);
  vec4 dx = f - (dy * cp);
  y = (dy / scaleFactor);
  x = (dx / scaleFactor);
}

struct particle
{
    vec4 pos;
    vec4 vel;
};

particle get(sampler2D ch, vec3 p)
{
    p = clamp(p, vec3(0.), size3d);
    vec4 data = voxel(ch, p);
    particle part;
    dec(data, part.pos, part.vel);
    part.pos.xyz *= size3d;
    part.vel *= 10.;
    return part;
}

vec4 save(particle a)
{
    //scale the numbers to be in a [-1,1] range
    a.pos.xyz /= size3d;
    return enc(a.pos, 0.1*a.vel);
}

float force(float d)
{
    d *= 2.3;
    return -(0.5*exp(-0.5*d) - 3.*exp(-3.*d));
}

buffer_a_code:
//3d voronoi particle tracking

void Check(inout particle U, vec3 pos, vec3 dx)
{
    particle neighbor = get(ch0, pos + dx);
    //check if the stored neighbouring particle is closer to this position 
    if(length(neighbor.pos.xyz - pos) < length(U.pos.xyz - pos))
    {
        U = neighbor; //copy the particle info
    }
}

void CheckRadius(inout particle U, vec3 p, int r)
{
    Check(U, p, vec3(-r, 0, 0));
    Check(U, p, vec3(r, 0, 0));
    Check(U, p, vec3(0, -r, 0));
    Check(U, p, vec3(0, r, 0));
    Check(U, p, vec3(0, 0, -r));
    Check(U, p, vec3(0, 0, r));
}

//get the force from a particle in p0 to a particle at p
vec3 getForce(vec3 p0, vec3 p)
{
	particle neighbor = get(ch0, p0);
    vec3 dx = neighbor.pos.xyz - p;
    //only count if neighbor particle is inside of its cell to exclude repeated forces
    if(maxv(abs(neighbor.pos.xyz - p0)) <= 0.5)
        return normalize(dx)*force(length(dx));
    else
        return vec3(0.);
}

vec3 calc_force(vec3 p)
{
    vec3 p0 = round(p);
    vec3 force = vec3(0.);
    for( int i=-2; i<=2; i++ )
        for( int j=-2; j<=2; j++ )
            for( int k=-2; k<=2; k++ )
    {
        vec3 dx = vec3(i,j,k);
        if(dx != vec3(0))
          force += getForce(p0+dx, p);
    }

    return force;
}


void mainImage( out vec4 pix, in vec2 pos )
{
    SCALE = floor(ar*pow(iResolution.x*iResolution.y,0.1666666));
    size3d = vec3(floor(iResolution.xy/SCALE), SCALE.x*SCALE.y);
    //voxel
    vec3 p = dim3from2(pos);

    //this particle
    particle U;
    if(iFrame < 1)
    {
        if(distance2border(p) > 10.)
        {
             U.pos.xyz = vec3(2.*round(p.xyz/2.));
             U.pos.w = hash13(U.pos.xyz+10.);
             U.vel.xyz = 0.4*(hash33(U.pos.xyz+0.1)*2. - 1.);
        }    
        else
        {
            U.pos = vec4(1e5);
        	U.vel = vec4(1e5);
        }
    }
    else
    {
        //this particle
        U = get(ch0, p);

        //check neighbours 
        CheckRadius(U, p, 1);
        CheckRadius(U, p, 2);
        CheckRadius(U, p, 3);
        CheckRadius(U, p, 4);

        vec4 border = border_grad(U.pos.xyz);

        U.vel.xyz =  U.vel.xyz*0.6 + dt*(4.*normalize(border.xyz)*exp(-0.4*border.w*border.w)+calc_force(U.pos.xyz)-vec3(0, 0., 0.01));
        U.pos.xyz += dt*U.vel.xyz;

    }
    pix = save(U);
}
