void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord/iResolution.xy;
	vec2 uvn = (fragCoord - 0.5*iResolution.xy)/iResolution.xy;

	fragColor = vec4(0);
    // Radial blur
    float steps = 16.;
    float scale = 0.00 + pow(dot(uvn,uvn),2.1)*0.4;
    //float chromAb = smoothstep(0.,1.,pow(length(uv - 0.5), 0.3))*1.1;
    float chromAb = pow(length(uv - 0.5),1.)*2.2;
    vec2 offs = vec2(0);
    vec4 radial = vec4(0);
    for(float i = 0.; i < steps; i++){
        scale *= 0.97;
        vec2 target = uv + offs;
        offs -= normalize(uvn)*scale/steps;
    	radial.r += texture(iChannel0, target + chromAb*1.4/iResolution.xy).x;
    	radial.g += texture(iChannel0, target).y;
    	radial.b += texture(iChannel0, target - chromAb*1./iResolution.xy).z;
    }
    radial /= steps;

    fragColor += radial;


    fragColor.r *= 0.97 + dot(uvn,uvn)*0.6;

    fragColor.t *= 1.  - smoothstep(0.,1.,dot(uvn,uvn))*0.;


    fragColor = pow(fragColor, vec4(0.4545));


    fragColor = max(fragColor, 0.);
    fragColor *= 1. - dot(uvn,uvn)*1.   ;
}

