// thanks to wyatt for teaching me how to do buffer feedback stuff thru his tutorials
// if you would like to do something similar, i recommend checking them out
// the code here is rather messy



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0);

    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;

    col = max(col, 0.);
    #define pal(a,b,c,d,e) ((a) + (b)*sin((c)*(d)+ (e)))

    vec2 puv = vec2(atan(uv.y,uv.x)/6.28, length(uv));

    puv.y += sin(puv.x*6.28*(7.) + uv.x*20. + iTime  + sin(uv.y*20. + iTime)*6. )*0.004;

    col += smoothstep(0.,0.01,-puv.y + 0.1);

    col *= pal(0.,1.5,vec3(0.1,.4,0.7), 1.5, 2. + iTime*2. + puv.y*200.);

    vec2 nc = fragCoord/iResolution.xy - 100.*(0. + length(puv.y)*2.)*normalize(uv)/iResolution.xy*iTimeDelta;

	vec2 st = 1./iResolution.xy;

    vec2 lc = nc;
    vec4 u = texture(iChannel1, lc + vec2(0,st.y) );
    vec4 d = texture(iChannel1, lc + vec2(0,-st.y) );
    vec4 l = texture(iChannel1, lc + vec2(-st.x,0) );
    vec4 r = texture(iChannel1, lc + vec2(st.x,0) );

    vec2 ddb = (vec2( r.b - l.b, u.b - d.b));
    vec2 dd = normalize(vec2( r.r - l.r, u.r - d.r));
    #define rot(x) mat2(cos(x),-sin(x),sin(x),cos(x))

    dd *= rot(3.14/2.);
    ddb *= rot(3.14/2. - 3. - sin(iTime)*0.5 - 0.1);

    nc += dd*st*0.1;
    nc -= ddb*st*0.2;

    if(iFrame > 0)
    	col = mix(col, texture(iChannel1, nc, 0.99).xyz, 0.996);

    fragColor = vec4(col,1.0);
}

