float gT;
    float sc;
float note(float note, float octave){ 
	return 27.5*exp2(((octave*12. ) + note )/12.);//pow(2., );
}

vec4 r11(float x){return texture(iChannel0,vec2(x)/256.);}
vec2 hash22(vec2 p)   
{
    p  = fract(p * vec2(5.3983, 5.4427));
    p += dot(p.yx, p.xy +  vec2(21.5351, 14.3137));
    return fract(vec2(p.x * p.y * 95.4337, p.x * p.y * 97.597));
}
vec2 hash22a(vec2 p){
    p *= 2000.;
	return texture(iChannel0, vec2((p + 1./floor(p/256.)))/256.).xz;
}

vec2 noise(float t)
{
    return hash22(vec2(t, t * 1.423)) * 2.0 - 1.0;
}

vec2 lpnoise(float t, float fq)
{
    t *= fq;

    float ft = fract(t);
    float tn = t - ft;
    ft = smoothstep(0.0, 1.0, ft);

    vec2 n0 = noise(floor(tn + 0.0) / fq);
    vec2 n1 = noise(floor(tn + 1.0) / fq);

    return mix(n0, n1, ft);
}

#define bitreduction(s,amt) s = sign(s) * floor(abs(s)/amt)*amt
#define sampleratereduction(t, amt) t = floor(t/(amt))*(amt)
#define smoothify(s, amt) mix(s,sign(s)*smoothstep(0.0,1.,abs(s)), amt)

vec2[8] N;



vec2 kick(float t, inout float sc){
	init;

    sampleratereduction(t, 0.000006);

    float md = beat/4.;
    float pl = 16.;
    t = mod(t, md*pl);
    float idx = floor(t/md);
    t = mod(t, md);

    float env = exp(-t*40.7);
    s += sin((exp2(-t*80.1)*340. + 60.)*tt )*env;

    s = mix(s,sin(s*2.)*1., 0.5);


    //bitreduction(s, 0.001);

    //s = sign(s)*smoothstep(0.,1.,abs(s));


    s = clamp(s,-1., 1.);


    float att = float(pattK[int(idx)]);
    s *= att;
    sc = 1. - pow(env, 0.15)*att;
    sc = smoothstep(0.,1.,sc);
    ret;
}

vec2 snare(float t){
	init;


    float md = beat;
    float pl = 8.;
    t = mod(t, md*pl);
    float idx = floor(t/md);
    t = mod(t, md);

    float env = exp2(-t*70.);

    vec2 n =lpnoise(t,10000.5);
    vec2 nb =lpnoise(t,20000.5);
    float f = 300.+ 500.*exp2(-t*80.)+ n.x*6.5 ;


    s += sin(f*tt + n.x*0.25 + nb.x*0.5*exp(-t*20.))*env;
    s += sin(f*tt*4. + n.x*0.25 + nb.x*4.2*exp(-t*10.))*pow(env, 0.8)*0.03;

    s += (n + nb)*0.05*exp(-t*12.);


    s *= float(pattS[int(idx)]);
    ret;
}

vec2 lead(float t){
	init;

    float to;
    vec2 nn = N[0];
    nn += N[1]*0.9;
    nn += N[5]*0.45;
    nn += N[7]*0.1;


    vec2 id;
    float r = bar;

    t = mod(t, r*float(chordCnt));

    id.x = floor(t/r);

    t = mod(t,bar);
    float reps = 32./8.;
    id.y = floor(t/(bar/reps));

    id.y = mod(id.y, 4.);

    t = mod(t,bar/reps);



    if (gT > msr*2.)
    	sampleratereduction(t, (1.2*1./iSampleRate));

    float amt = 0.9;
    float env = exp(-t*0.2);



	float n = chords[int(id.x)][int(id.y)];


    float oct = 6.;
    s += sin( note(n, oct) *tt +sin(note(n, 8.) *tt)*exp(-t*20.)*0.05 )*env;
    s += sin( note(n, oct) *tt +sin(note(n, 8.) *tt)*exp(-t*20.)*0.05 + nn*amt)*env;
    s -= sin( note(n, oct) *tt +sin(note(n, 8.) *tt)*exp(-t*20.)*0.05 )*env;



    if (gT > msr*2.)
    	bitreduction(s, 0.7);
    s *= float(pattL[int(id.y)]);


    s *= 1.;
    ret;
}


vec2 leadb(float t){
	init;

    float to;
    vec2 nn = N[0];


    vec2 id;
    float r = bar;
    t = mod(t, r*float(chordCnt));

    id.x = floor(t/r);

    float att = float(pattL[int( mod(floor(t/(bar/(32./8.))), 8.) )]);
    t = mod(t,bar);
    float reps = 32./2.;
    id.y = floor(t/(bar/reps));

    id.y = mod(id.y, 4.);

    t = mod(t,bar/reps);


    if (gT > msr*2.)
    	sampleratereduction(t, (1.2*1./iSampleRate));

    float amt = 0.1;
    float env = exp(-t*0.2);



	float n = chords[int(id.x)][int(id.y)];


    float oct = 5.;
    s += sin( note(n, oct) *tt +sin(note(n, 8.) *tt)*exp(-t*20.)*0.05 )*env;
    s += sin( note(n, oct) *tt +sin(note(n, 8.) *tt)*exp(-t*20.)*0.05 + nn*amt)*env;
    s -= sin( note(n, oct) *tt +sin(note(n, 8.) *tt)*exp(-t*20.)*0.05 )*env;



    if (gT > msr*2.)
    	bitreduction(s, 0.7);
    s *= 1. - att;


    s *= 0.05;
    ret;
}

