vec2 tri(float t,float f){
    return vec2(abs(fract(f*t) - 0.5));
}

vec2 bass(float t){
	init;

    float to;


    vec2 id;
    float r = bar;
    t = mod(t, r*4.);

    id.x = floor(t/r);

    t = mod(t,r);
    float reps = 1.;
    id.y = floor(t/(bar/reps));

    id.y = mod(id.y, 4.);

    t = mod(t,bar/reps);


    if(gT < msr*6.){
    	sampleratereduction(t, (10./iSampleRate)*2.1);
    	sampleratereduction(t, (20./iSampleRate)*0.1);
    } else if(gT < msr*8.){
    	sampleratereduction(t, (10./iSampleRate)*(2.1 + sin(t)));
    	sampleratereduction(t, (20./iSampleRate)*0.1);
    }

    float env = exp(-t*0.04);

	float n = chords[int(id.x)][int(id.y)];

    //n += floor(id.y/4.)*12.;

    s += sin( note(n, 1.) *tt 
             +sin(note(n, 4.) *tt + 
             	+sin(note(n, 2.) *tt)*exp(-t*0.2)*0.1 

                 )*exp(-t*0.5)*0.4
             +sin(note(n, 1.) *tt + 
             	+sin(note(n, 10.) *tt)*exp(-t*0.2)*0.
                 )*exp(-t*0.5)*0.4
            )*env;


    s *= 1.;

    vec2 oS = s;

    if(gT < msr*4.){
    	bitreduction(s, (0.25));
    }

    s = mix(oS, s, 0.8);


    s = sin(s*2.5);

    s *= 0.7*0.6; // what is maths
    ret;
}



vec2 chord(float t){
	init;


    float r = bar;
    t = mod(t, r*float(chordCnt));
    int id = int(floor(t/r));

    //sampleratereduction(t, (1./iSampleRate));
    sampleratereduction(t, 0.00003);

    vec2 nn = N[1];
    nn += N[2]*0.9;
    nn += N[7]*0.45;

    nn *= 0.01*sin(t);
    float m = 1.;
    float width = 15.;
    s += sin( note(chords[id][1], 4.) *tt + nn.x*1.)*0.5;
    s += sin( note(chords[id][1], 4.) *tt + nn.x*1.)*0.5;
    float oct = 3.;
    for(float i = 0.; i < 4. + min(iSampleRate, 0.); i++){
        float a = (1. + i/2.);
    	//s += sin( note(chords[id].x, 3.) *tt + nn.x)*m;
    	s.x += sin( note(chords[id].x, oct) *tt*a + nn.x)*m;
    	s.x += sin( note(chords[id].y, oct) *tt*a + nn.x)*m;
    	s.x += sin( note(chords[id].z, oct) *tt*a + nn.x)*m;
    	s.x += sin( note(chords[id].w, oct) *tt*a + nn.x)*m;
    	s.y += sin( note(chords[id].x, oct) *tt*a + nn.y + width)*m;
    	s.y += sin( note(chords[id].y, oct) *tt*a + nn.y + width)*m;
    	s.y += sin( note(chords[id].z, oct) *tt*a + nn.y + width)*m;
    	s.y += sin( note(chords[id].w, oct) *tt*a + nn.y + width)*m;
    	m *= 0.5;
    }

    //bitreduction(s, 0.01);

    s *= 0.03;
    s = sin(s*3.);
    //s /= 2.;
    //s = sin(s);
	ret;
}




vec2 cguit(float t){
	init;


    float r = bar;
    t = mod(t, r*float(chordCnt));
    int id = int(floor(t/r));


    float rb = bar/8.;
    float env = exp(-mod(t, rb)*2.)*float(pattG[int(mod(floor(t/rb*2.), r*4.))]);

    //sampleratereduction(t, (1./iSampleRate));
    //sampleratereduction(t, (2./iSampleRate));
    if(gT < msr*6.)
    	sampleratereduction(t, 0.0005);

    vec2 nn = N[0];
    nn += N[1]*0.9;
    nn += N[3]*0.45;

    nn *= 0.01*sin(t);
    float m = 1.;
    float width = 15.;
    s += sin( note(chords[id][1], 3.) *tt + nn.x*1.);
    s += sin( note(chords[id][1], 3.) *tt + nn.x*1.);
    float oct = 3.;  
    for(float i = 0.; i < 5. + min(iSampleRate, 0.); i++){
        float a = (1. + i/1.);
    	//s += sin( note(chords[id].x, 3.) *tt + nn.x)*m;
    	s.x += sin( note(chords[id].x, oct) *tt*a + nn.x)*m;
    	s.x += sin( note(chords[id].y, oct) *tt*a + nn.x)*m;
    	s.x += sin( note(chords[id].z, oct) *tt*a + nn.x)*m;
    	s.x += sin( note(chords[id].w, oct) *tt*a + nn.x)*m;
    	s.y += sin( note(chords[id].x, oct) *tt*a + nn.y + width)*m;
    	s.y += sin( note(chords[id].y, oct) *tt*a + nn.y + width)*m;
    	s.y += sin( note(chords[id].z, oct) *tt*a + nn.y + width)*m;
    	s.y += sin( note(chords[id].w, oct) *tt*a + nn.y + width)*m;
    	m *= 0.75;
    }
    s *= env;
    if(gT < msr*4.)
    	bitreduction(s, 0.4);

    s *= 0.04;
    //s = sin(s*3.);
    //s /= 2.;
    //s = sin(s);
	ret;
}


vec2 hats(float t){
	init;

    float r = hbeat;
    t = mod(t + 0., r);

    float o = offsH[int(mod(floor(t/hbeat), 7.))]*r;
    t -= o;


    sampleratereduction(t, 0.0002);
    vec2 nn = N[0] + N[1]  + N[2] + N[3]  ;

    float amt = 0.02;
    float env = exp(-t*7.4);
    //float f =  sin((12000. )*tt)*exp(-t*0.4)*0.215 +sin((3000. )*tt)*exp(-t*2.)*0.5 + ( 2000. + exp(-t*60.)*100.)*tt +lpnoise(t, 200500.).x*1.;
    float f =  sin((12000. )*tt)*exp(-t*0.4)*0.215 +sin((3000. )*tt)*exp(-t*2.)*0.5 + ( 3000. + exp(-t*60.)*100.)*tt +lpnoise(t, 2500.).x*1.;
    s += sin( f )*env;
    s += amt*sin( f + nn )*env;
    s -= amt*sin( f )*env;

    bitreduction(s, 0.5);
    s *= 1.;
    s *= min(floor(t) + 1., 1.);
    ret;
}
vec2 hatsb (float t){
	init;

    t = mod(t + 0., beat);


    vec2 nn = N[0] + N[1]*0.87  + N[2]*0.6 + N[3]*0.5  ;

    float amt = 0.5;
    float env = exp(-t*1.4);
    float f =  sin(
        (2000. )*tt)*exp(-t*0.4)*0.215 
        +sin((4000. )*tt)*exp(-t*2.)*0.5 
        + ( 1250. + exp(-t*60.)*100.)*tt +N[6].x*1.;
    s += sin( f )*env;
    s += amt*sin( f + nn )*env;
    s -= amt*sin( f )*env;

    float bitreduction = 0.75;
    s = sign(s) * floor(abs(s)/bitreduction)*bitreduction;

    s *= 0.4;
    ret;
}

vec2 crash(float t){
	init;

    t = mod(t + 0., msr/2.);

    vec2 nn = N[1];

    float amt = 2.;
    float env = exp(-t*0.1);
    float f =  sin(
        (2000. )*tt)*exp(-t*0.4)*0.215 
        +sin((4000. )*tt)*exp(-t*2.)*0.5 
        + ( 1250. + exp(-t*60.)*100.)*tt +lpnoise(t, 200500.).x*1.;
    s += sin( f )*env;
    s += amt*sin( f + nn )*env;
    s -= amt*sin( f )*env;


    s *= 0.9;
    bitreduction(s, 0.9);

    s *= 0.04;
    ret;
}

vec2 cReverb(float t){
	init;

    if (gT > msr*2.)
    	sampleratereduction(t, (8.2*1./iSampleRate));


    float iters = 100.;
    float size = 150.;

    if(gT > msr *8.)
        iters *= 4.;

    for(float i = 0.; i < iters + min(iSampleRate, 0.); i++ ){
        float att = smoothstep(1.,0.9,i/iters);
    	//s += lead(t - size*i/iSampleRate + 10000.2*N[int(mod(i,7.))].x/iSampleRate )*(pow(sc,0.9))*att;
        float m = size*i/iSampleRate + noise(i).x*100./iSampleRate;
        s += lead(t + m )*(pow(sc,0.9))*att*0.1;
    	if (gT > msr*2.)
			s += cguit(t + m);
        s += leadb(t + m);
    	if (gT > msr*2.)
        	s += hats(t + m)*(0.2 + pow(sc,1.4))*0.001;
    }
    s /= iters;
    s *= 3.;

    if (gT > msr*2.)
    	s = mix(s,s * (pow(sc,0.9)), 0.5);
    //bitreduction(s, 0.0001);

    ret;
}


void initKickPatt(float t){

    float r = beat/4.;

    float id = floor(t/r);


    for(int i = 0; i < 16 + min(int(iSampleRate),0); i++){

        vec4 r = r11(id + float(i));

    	pattK[i] = r.x < probK[i];

    }
}


void initNoise(float t){

