vec2 mainSound( in int samp, float t )
{
    t += OFFS;
    gT = t;
    //t += msr*4.;
    vec2 s= vec2(0);

    initKickPatt(t);
    initNoise(t);


    vec2 k = kick(t,sc)*1.;
    vec2 sn = snare(t);

    vec2 ha = hats(t)*(0.2 + pow(sc,1.4))*0.01;
    vec2 hb = hatsb(t);

    vec2 drums = k + sn*1. + ha*3. ;
    //drums *= 0.5;
    drums = sin(drums*1.2);
    //drums = smoothify(drums, 0.2);
    //vec2 hb= hatsb(t)*(pow(sc,4.4))*0.1;
    vec2 bass = bass(t)*(pow(sc,2.4))*0.3;
    vec2 c = chord(t)*(pow(sc,1.))*0.4;
    vec2 cguit = cguit(t)*(pow(sc,1.))*0.5;
    vec2 l = lead(t)*(pow(sc,0.9))*0.4;
    vec2 lb = leadb(t)*(pow(sc,0.9))*0.4;
    vec2 lc = leadc(t)*(pow(sc,0.9))*0.4;
	vec2 cr = crash(t);

    vec2 rev = cReverb(t);
    //vec2 lb = leadb(t)*(pow(sc,0.9))*0.1;
    //vec2 cr = crash(t)*(pow(sc,0.9))*0.1;


    if(t < msr*2.){
        if (t < beat){
        	s += cr*0.2;
        }

        //s += cguit;
        //s += bass;
        //s += k;
        s += lb*0.1;
        //s += ha*2.;
        //s += hb*0.1;

        s += rev;

        s += lb;

        if (t > msr*1.){
            s += lb;
            s += cguit*0.4;
        	//s += ha*2.;

        }
        if (t > msr*1.75){
        	s += k;
        }

    }else if (t < msr*4.){
        s += c;
        s += rev;
        if (t > msr*4. - msr){
        	s += bass;
            s += cr;
        	s += cguit;
        }
    } else {
        s += bass;
        s += drums;
        s += rev;
        s += cguit;
        s += lc;
    }

    s *= 0.2;

    s = mix(s,sign(s)*smoothstep(0.0,1.,abs(s)), (0.1 + sin(t)*0.)*1.);
    s = clamp(s,-1.,1.);

    return s;
}

