// based on
// "ShaderToy Tutorial - Ray Marching for Dummies!" 
// by Martijn Steinrucken aka BigWings/CountFrolic - 2018
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .01

struct Intersection { float _distance; vec3 color; };

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

Intersection unionOp(Intersection a, Intersection b) {
    if (a._distance < b._distance) {
        return a;
    } else {
        return b;
    }
}
Intersection substractionOp(Intersection a, Intersection b) {
    if (a._distance > -b._distance) {
        return Intersection(a._distance, a.color);
    } else {
        return Intersection(-b._distance, a.color);
    }
}
Intersection intersectionOp(Intersection a, Intersection b ) {
    return Intersection(max(a._distance, b._distance), a.color);
}

mat2 Rot(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

vec3 H33(vec3 p) {
    return texture(iChannel2, (p/40.).zy + vec2(0.5, 0.3), 0.).xyz;
}

vec3 getLightPos() {
    vec3 lightPos = vec3(0, 10, -2);
    lightPos.xz += vec2(sin(iTime), cos(iTime))*3.;
    return lightPos;
}

Intersection RayMarch(vec3, vec3, float);

vec3 getSphereNormal(vec3 p, vec3 center) {
    return normalize(p - center);
}

// colors
vec3 orange = vec3(0.8, 0.4, 0.1);
vec3 yellow = vec3(0.8, 0.7, 0.4);
vec3 blue = vec3(0.1, 0.2, 0.9);
vec3 green = vec3(0.1, 0.6, 0.2);
vec3 purple = vec3(0.6, 0.2, 0.8);
vec3 cyan = vec3(0.1, 0.6, 0.8);
vec3 red = vec3(0.8, 0.1, 0.1);
vec3 lightBlue = vec3(0.5, 0.5, 0.9);
Intersection GetDist(vec3 p) {
	vec4 s1 = vec4(3, 5, -2, 1);
	vec4 s2 = vec4(-2, 5, -2, 1);

    Intersection sphere1Dist = Intersection(length(p-s1.xyz)-s1.w, orange);
    Intersection sphere2Dist = Intersection(length(p-s2.xyz)-s2.w, orange);

    vec3 lightBulbPos = getLightPos() + vec3(0., .0, 0.);
    Intersection lightBulbDist = Intersection(length(p - lightBulbPos)-0.4, yellow);
    float hDistanceToPlaneBorder = max(p.x, p.z) - 20.;
    Intersection planeDist = Intersection(hDistanceToPlaneBorder > 0. ?
                                          sqrt(hDistanceToPlaneBorder*hDistanceToPlaneBorder + p.y*p.y)
                                          : p.y, blue);
    Intersection boxDist = Intersection(sdBox(p - vec3(3, 5, 2), vec3(1.)), green);
    Intersection boxDist2 = Intersection(sdBox(p - vec3(-7, 5, -2), vec3(1.)), red);
    Intersection lightBulbRemoval = Intersection(sdBox(p - lightBulbPos, vec3(.3, .1, .3)),yellow);

    Intersection d = Intersection(1e10, vec3(0));
    d = unionOp(d, planeDist);
    d = unionOp(d, Intersection(p.z + 20., purple));
    d = unionOp(d, Intersection(p.x + 20., cyan));
    d = unionOp(d, Intersection(20. - p.x, H33(p)));
    d = unionOp(d, Intersection(20. - p.z, lightBlue));
    d = unionOp(d, Intersection(40. - p.y, vec3(.1)));
    d = unionOp(d, sphere1Dist);
    d = unionOp(d, sphere2Dist);
    d = unionOp(d, boxDist);
    d = unionOp(d, boxDist2);
    //d = unionOp(d, lightBulbDist);
    //d = unionOp(d, lightBulbRemoval);
    Intersection lightBulb = substractionOp(lightBulbRemoval, lightBulbDist);
    d = unionOp(d, Intersection(1.*lightBulb._distance, lightBulb.color));
    return d;
}

Intersection wormwholeX(vec3 p, vec3 rd, vec4 s1, vec4 s2) {
    vec3 s1tos2 = s2.xyz - s1.xyz;
    float mirrorDistance1 = 2.*(p.x - s1.x);
    s1tos2.x -= mirrorDistance1;
    vec3 n1 = getSphereNormal(p, s1.xyz);
    vec3 newRD1 = abs(dot(n1, rd)) * 2. * n1 + rd;
    newRD1.x *= -1.; // the wormhole is folded in dimensions xw
    vec3 mirroredPoint1 = p + s1tos2 + newRD1*SURF_DIST*2.;
    Intersection s1color = RayMarch(mirroredPoint1, newRD1, MAX_DIST); // note the color is a ray in the other sphere
    Intersection sphere1Dist = Intersection(length(p-s1.xyz)-s1.w, s1color.color);
    return sphere1Dist;
}

Intersection GetDistRecursive(vec3 p, vec3 rd) {
	vec4 s1 = vec4(3, 5, -2, 1);
	vec4 s2 = vec4(-2, 5, -2, 1);

    Intersection sphere1Dist = wormwholeX(p, rd, s1, s2);
    Intersection sphere2Dist = wormwholeX(p, rd, s2, s1);
    vec3 lightBulbPos = getLightPos() + vec3(0., .0, 0.);
    Intersection lightBulbDist = Intersection(length(p - lightBulbPos)-0.4, yellow);

    float hDistanceToPlaneBorder = max(p.x, p.z) - 20.;
    Intersection planeDist = Intersection(hDistanceToPlaneBorder > 0. ?
                                          sqrt(hDistanceToPlaneBorder*hDistanceToPlaneBorder + p.y*p.y) 
                                          : p.y, blue);
    Intersection boxDist = Intersection(sdBox(p - vec3(3, 5, 2), vec3(1.)), green);
    Intersection boxDist2 = Intersection(sdBox(p - vec3(-7, 5, -2), vec3(1.)), red);
    Intersection lightBulbRemoval = Intersection(sdBox(p - lightBulbPos, vec3(.3, .1, .3)),yellow);

    Intersection d = Intersection(1e10, vec3(0));
    d = unionOp(d, planeDist);
    d = unionOp(d, Intersection(p.z + 20., purple));
    d = unionOp(d, Intersection(p.x + 20., cyan));
    d = unionOp(d, Intersection(20. - p.x, H33(p)));
    d = unionOp(d, Intersection(20. - p.z, lightBlue));
    d = unionOp(d, Intersection(40. - p.y, vec3(.1)));
    d = unionOp(d, sphere1Dist);
    d = unionOp(d, sphere2Dist);
    d = unionOp(d, boxDist);
    d = unionOp(d, boxDist2);
    //d = unionOp(d, lightBulbDist);
    //d = unionOp(d, lightBulbRemoval);
    d = unionOp(d, substractionOp(lightBulbRemoval, lightBulbDist));
    return d;
}

Intersection RayMarch(vec3 ro, vec3 rd, float maxDistance) {
	float dO=0.;
    Intersection intersection;
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        intersection = GetDist(p);
        float dS = intersection._distance;
        dO += dS;
        if(dO>maxDistance || dS<SURF_DIST) break;
    }

    return Intersection(dO, intersection.color);
}

Intersection RayMarchRecursive(vec3 ro, vec3 rd, float maxDistance) {
	float dO=0.;
    Intersection intersection;
    vec3 p;
    for(int i=0; i<MAX_STEPS; i++) {
    	p = ro + rd*dO;
        intersection = GetDist(p);
        float dS = intersection._distance;
        dO += dS;
        if(dO>maxDistance || dS<SURF_DIST) break;
    }
    intersection = GetDistRecursive(p, rd);

    return Intersection(dO, intersection.color);
}
vec3 GetNormal(vec3 p) {
	float d = GetDist(p)._distance;
    vec2 e = vec2(.01, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy)._distance,
        GetDist(p-e.yxy)._distance,
        GetDist(p-e.yyx)._distance);

    return normalize(n);
}


Intersection GetLight(vec3 p) {
    vec3 lightPos = getLightPos();
    vec3 lightRay = normalize(lightPos-p);
    float distanceToLight = length(lightPos-p);
    vec3 n = GetNormal(p);

    // surfaces perpendicular to the light are more illuminated
    // clamp to round negative values up to 0. 
    float diffuse = clamp(dot(n, lightRay), 0., 1.);

    // if there's some object between our point and the light, reduce the diffuse light
    // the extra SURF_DIST is to not stop where we start and allow the ray to go out
    //Intersection i = RayMarch(p+n*SURF_DIST*2., lightRay, distanceToLight + SURF_DIST*50.);
    Intersection i = RayMarch(p+n*SURF_DIST*2., lightRay, min(MAX_DIST, distanceToLight + SURF_DIST*3.));
    if(i._distance < distanceToLight) {
        float transparency = .1; // 1==invisible, 0==opaque 
        diffuse *= transparency;
    } else {
        float transparency = .8; // 1==invisible, 0==opaque 
        diffuse *= transparency;
    }
    return Intersection(diffuse, i.color*diffuse);
}


mat4 loadMatrix() {
    return mat4(load(ivec2(1,1)),
                load(ivec2(2,1)),
                load(ivec2(3,1)),
                load(ivec2(4,1))
                );
}

const ivec2 mouseAddress = ivec2(9, 1);
const ivec2 positionAddress = ivec2(6, 1);
void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5*iResolution.xy)/iResolution.y;
    vec2 mouse = (iMouse.xy/iResolution.xy - .5)* 4.;

    vec4 cameraPosition = load(positionAddress);
    vec4 originalRo = vec4(0., 5.1, 10., 1.);
    vec3 ro = (cameraPosition + originalRo).xyz;


    mat4 rotationMatrix = loadMatrix();
    mat4 transposedRotationMatrix = transpose(rotationMatrix);
    vec3 zAxisOfRotationMatrix = vec3(rotationMatrix[2][0], rotationMatrix[2][1], rotationMatrix[2][2]);
    vec3 rd = normalize((vec4(uv.x, uv.y, -1, 1) * rotationMatrix).xyz);

    vec4 viewMode = ReadKey(Key_V, true);
    if (viewMode.x == 0.) {
        Intersection i = RayMarchRecursive(ro, rd, MAX_DIST);

        vec3 p = ro + rd * i._distance;

        if (true) {
        	Intersection diffuse = GetLight(p);
            fragColor = vec4(mix(diffuse.color, i.color, 0.5), 1.0);
        //fragColor = vec4(vec3(diffuse._distance), 1.0);
        } else {
        	fragColor = vec4(i.color, 1.0);
        }
    } else {
        vec2 grid = 7.*fragCoord/iResolution.y-1.;
        ivec2 coord = ivec2(floor(grid));

        if (coord.x >= 0 && coord.x <= 3 && coord.y >= 0 && coord.y <=3) {
            fragColor = vec4(rotationMatrix[coord.x][3 - coord.y], 0., 0., 0.)/3.;
            fragColor.y = -fragColor.x;
            //fragColor = vec4(fract(grid.x), fract(grid.y), 0.5, 1.);
            //fragColor = vec4(float(coord.x)*0.5, float(coord.y)*0.125, 0.5, 1.);
        } else if (coord == mouseAddress) {
            vec4 mouse = load(coord);
            if (fract(grid.x) < 0.25) {
                fragColor = vec4(mouse.x, -mouse.x, 0., 0.);
            } else if (fract(grid.x) < 0.5) {
                fragColor = vec4(mouse.y, -mouse.y, 0., 0.);
            } else if (fract(grid.x) < 0.75) {
                fragColor = vec4(mouse.z, -mouse.z, 0., 0.);
            } else {
                vec4 v_key = ReadKey(Key_V, true);
                fragColor = v_key;
            }
        } else if (coord.x == 5 && coord.y >= 1 && coord.y <= 3) {
            vec4 cameraPosition = load(positionAddress);
            fragColor = vec4(cameraPosition[3-coord.y], -cameraPosition[3-coord.y], 0., 0.)/3.;
        } else if (coord.x == 7 && coord.y >= 1 && coord.y <= 3) {
            fragColor = vec4(originalRo[3-coord.y], -originalRo[3-coord.y], 0., 0.)/3.;
        } else if (coord.x == 8 && coord.y >= 1 && coord.y <= 3) {
            fragColor = vec4(ro[3-coord.y], -ro[3-coord.y], 0., 0.)/3.;
        } else {
            fragColor = vec4(0.1, 0.2, 0.8, 1.);
        }
    }
}

