        if (thisPixelAddress.x >= 1 && thisPixelAddress.x <= 4 && thisPixelAddress.y == 1) {
            store(fragColor, identity[thisPixelAddress.x-1]);
        } else if (thisPixelAddress == mouseAddress) {
            store(fragColor, vec4(0., 0., 0., 0.));
        }
    }
}
void updateRotationMatrix(out vec4 fragColor, vec2 mouse, mat4 rotationMatrix, ivec2 thisPixelAddress) {
    float mouseSpeed = 0.75;
    vec4 previousMouse = load(mouseAddress);
    if (previousMouse.z > 0.) { // avoid jumping when clicking
        vec2 mouseDiff = vec2(mouse - previousMouse.xy);
        rotationMatrix = rotationMatrix * RotY(-mouseDiff.x * mouseSpeed); // yaw, external rotation
        rotationMatrix = RotX(mouseDiff.y * mouseSpeed) * rotationMatrix; // pitch
    }
    vec4 column = rotationMatrix[thisPixelAddress.x-1];
    store(fragColor, column);
}

void composeTransformations(out vec4 fragColor, in vec2 fragCoord) {
    ivec2 thisPixelAddress = ivec2(fragCoord);
    if (thisPixelAddress.y == 1) {
        // we will only write state to addresses y==1 to simplify

        float movement = 0.;
        mat4 rotationMatrix = loadMatrix();

        vec2 mouse = (iMouse.xy /iResolution.xy - .5) * 2.;	// range [-1, 1]

        if (thisPixelAddress == mouseAddress) {
            store(fragColor, vec4(mouse.x, mouse.y, iMouse.z, 0.));
        } else if (thisPixelAddress == positionAddress) {
            vec4 position = load(positionAddress);
            float speed = 0.2;
            float up      = ReadKey(Key_W)? speed : 0.;
            float down    = ReadKey(Key_S)? speed : 0.;
            float right   = ReadKey(Key_D)? speed : 0.;
            float left    = ReadKey(Key_A)? speed : 0.;
            float forward = ReadKey(Key_E)? speed : 0.;
            float back    = ReadKey(Key_Q)? speed : 0.;
            vec4 movement = vec4(right - left, up - down, back - forward, 1.);
            store(fragColor, movement * rotationMatrix + position);
        } else if (thisPixelAddress.x >= 1 && thisPixelAddress.x <= 4) {
            updateRotationMatrix(fragColor, mouse, rotationMatrix, thisPixelAddress);
        }

        // I will go crazy if I keep trying to remove this redundant code
        else if (thisPixelAddress.x == 4) {
            updateRotationMatrix(fragColor, mouse, rotationMatrix, thisPixelAddress);
        }
    }
}

void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    if (iFrame == 0) {
        reset(fragColor, fragCoord);
    } else {
        composeTransformations(fragColor, fragCoord);
        //reset(fragColor, fragCoord);
    }
}

