        for(int x = -1; x <= 1; x++)
            bloom += textureLod(iChannel0, (fragCoord+vec2(x, y) * scale)/iResolution.xy, logScale).rgb;

    return max(bloom/9.0 - vec3(threshold), vec3(0));
