vec4 qmul(vec4 p, vec4 q) {
  vec3 P = p.xyz, Q = q.xyz;
  return vec4(p.w*Q+q.w*P+cross(P,Q),p.w*q.w-dot(P,Q));
}

void qinit() {
  float t = iTime;
  float ltheta = 0.1*t;
  float rtheta = 0.065*t;
  lquat = vec4(sin(ltheta)*laxis,cos(ltheta));
  rquat = vec4(sin(rtheta)*raxis,cos(rtheta));
}

vec2 rotate(in vec2 p, in float t) {
  return p * cos(-t) + vec2(p.y, -p.x) * sin(-t);
}

vec4 iproject(vec3 p3) {
  float t = iTime;
  vec4 p = vec4(p3,0);
  if (dotranslate) {
    p.w = sin(0.2*t);
    //p.xw = rotate(p.xw,0.2*t); 
  }
  if (doquaternion) {
    p = qmul(p,rquat);
    //p = qmul(lquat,p);
  }
  return p;
}

vec4 map(vec3 p, inout float scale) {
  if (doinvert) {
    float c = cos(0.25*iTime);
    float r = 1.0/c;
    float r2 = r*r;
    p.x -= r-c;
    float k = r2/dot(p,p);
    p *= k;
    scale *= k;
    p.x += r-c;
  }
  vec4 p4 = iproject(p);
  return p4;
}

float scene(vec4 pos4) {
  float d = 1e8;
  if (dofold) {
    pos4 = fract(pos4);
    pos4 = min(pos4,1.0-pos4);
  }
  type = 0;
  // Distance from edges - these appear as points, usually
  d = min(d,length(pos4.xyz) - ewidth);
  d = min(d,length(pos4.yzw) - ewidth);
  d = min(d,length(pos4.zwx) - ewidth);
  d = min(d,length(pos4.wxy) - ewidth);
  // Distance from faces - these appear as lines, usually
  if (dofaces) {
    d = min(d,length(pos4.xy) - fwidth);
    d = min(d,length(pos4.yz) - fwidth);
    d = min(d,length(pos4.zx) - fwidth);
    d = min(d,length(pos4.xw) - fwidth);
    d = min(d,length(pos4.yw) - fwidth);
    d = min(d,length(pos4.zw) - fwidth);
  }
  if (dopoints) {
    float k = length(pos4) - pwidth;
    if (k < d) { type = 1; d = k; }
  }
  return d;
}

vec3 getColor(vec3 p3){
  if (type == 1) return PointColor;
  float k, d = 1e8;
  vec4 p = map(p3,k);
  if (dofold) {
    p = fract(p);
    p = min(p,1.0-p);
  }
  vec3 col = EdgeColor;
  d = 1e8;
  d = min(d,length(p.xyz) - ewidth);
  d = min(d,length(p.yzw) - ewidth);
  d = min(d,length(p.zwx) - ewidth);
  d = min(d,length(p.wxy) - ewidth);
  if (true) {
    // Do the face colouring always, so they show up on the edges.
    k = length(p.xy) - fwidth;
    if (k < d) { d = k; col = Face0Color; }
    k = length(p.yz) - fwidth;
    if (k < d) { d = k; col = Face1Color; }
    k = length(p.zx) - fwidth;
    if (k < d) { d = k; col = Face2Color; }
    k = length(p.xw) - fwidth;
    if (k < d) { d = k; col = Face3Color; }
    k = length(p.yw) - fwidth;
    if (k < d) { d = k; col = Face4Color; }
    k = length(p.zw) - fwidth;
    if (k < d) { d = k; col = Face5Color; }
  }
  return col;
}

float de(vec3 p3) {
  float scale = 1.0;
  vec4 p = map(p3,scale);
  float d = scene(p);
  return d/scale;
}

vec3 calcNormal(vec3 p, float t) {
  vec2 e = vec2(t*precis, 0.0);
  return normalize(vec3(de(p + e.xyy) - de(p - e.xyy),
                        de(p + e.yxy) - de(p - e.yxy),
                        de(p + e.yyx) - de(p - e.yyx)));
}

float march(vec3 ro, vec3 rd) {
  float t = 0.1;
  for(int i = 0; i < 200; i++) {
    //assert(i < 100);
    float h = de(ro + rd * t);
    if (h < t*precis) return t;
    if (doinvert) h *= 0.5;
    t += h;
    if (t < 0.0 || t > maxdist) break;
  }
  return -1.0;
}

vec3 transform(in vec3 p) {
  if (iMouse.x > 0.0) {
    float theta = (2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
    float phi = (2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
    p.yz = rotate(p.yz,-theta);
    p.zx = rotate(p.zx,phi);
  }
  //p.yz = rotate(p.yz,iTime * 0.125);
  //p.zx = rotate(p.zx,iTime * 0.2);
  return p;
}

const int CHAR_F = 70;
const int CHAR_I = 73;
const int CHAR_J = 74;
const int CHAR_P = 80;
const int CHAR_Q = 81;
const int CHAR_T = 84;
const int CHAR_X = 88;
const int KEY_UP = 38;
const int KEY_DOWN = 40;

bool keypress(int code) {
    return texelFetch(iChannel0, ivec2(code,2),0).x != 0.0;
}

vec4 store(int i,int j) {
  return texelFetch(iChannel1, ivec2(i,j),0);
}

int keycount(int key) {
  return int(store(0,key).x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  dofaces = keypress(CHAR_F);
  doinvert = keypress(CHAR_I);
  dopoints = !keypress(CHAR_P);
  doquaternion = !keypress(CHAR_Q);
  dotranslate = !keypress(CHAR_T);
  dofold = !keypress(CHAR_X);
  qinit();

  vec2 p = (2.0*fragCoord.xy-iResolution.xy)/iResolution.y;
  vec3 bgcol = 0.5+(0.25-0.25*p.y)*vec3(1,1,0);
  p *= 3.0;
  vec3 ro = vec3(0,0,-2.5);
  vec3 rd = vec3(p, 6.0);
  vec3 li = vec3(0.5, 2.0, -1);
  ro.z *= 1.0 + 0.1*float(keycount(KEY_DOWN)-keycount(KEY_UP));

  ro = transform(ro);
  rd = transform(rd);
  li = transform(li);
  rd = normalize(rd);
  li = normalize(li);
  vec3 col = bgcol;
  float t = march(ro, rd);
  if (t > 0.0) {
    vec3 p = ro + t * rd;
    vec3 n = calcNormal(p,t);
    float diffuse = clamp(dot(n, li), 0.0, 1.0);
    col = getColor(p) * (0.2+0.8*diffuse);
    col = mix(col,bgcol,t/maxdist);
  }
  col = pow(col, vec3(0.4545));
  if (alert) col = vec3(1,0,0);
  fragColor = vec4(col, 1.0);
}

