float rand(float x){
    x += 0.0007;
    x += 1224.124;
    x = floor(x / 0.001) * 0.001;
    return fract(sin(x) * 43758.5453);
}

vec2 getNormal(float id){
    float rnd = (rand(id) * 2. - 1.) * 0.78539816339 / 1.1;
    return vec2(sin(rnd), cos(rnd));
    float ctg = -cos(rnd)/sin(rnd);
}

vec4 getCub(float h, vec2 u, vec2 C, vec2 v){
    float d = h;
    float c = -u.x / u.y;
    float b = (3. * C.y + v.x / v.y * C.x - 2. * c * C.x - 3. * d) / (C.x * C.x);
    float a = (-v.x / v.y - c - 2. * b * C.x) / (3. * C.x * C.x);
    return vec4(d, c, b, a);
}

float getContRnd(float uvx){
    float ans = 0.;

    //float x = uv.x;
    //vec2 lv = uv;
    float k = 1.;
    float x = uvx;

    float id = (floor(x * k) + 0.5) / k;
    vec4 cB = getCub(0., getNormal(id), vec2(2., 0.), getNormal(id + 1. / k));
    x = fract(x * k) * 2. - 1.;
    vec4 pw = vec4(1., x, x * x, x * x * x);
    ans = (uvx >= id) ? dot(pw, cB) : ans;
    cB = getCub(0., getNormal(id), vec2(-2., 0.), getNormal(id - 1. / k));
    ans = (uvx <= id) ? dot(pw, cB) : ans;
    return ans;
}

float N21(vec2 uv){
    return getContRnd(uv.x - 2.521) * getContRnd(uv.y + 124.131);
}

