/*
3D Voronoi fogness.

Author: Tim Gerritsen <tim@mannetje.org>
Date: December 2017

*/

#define MAX_MARCH_STEPS 16
#define MARCH_STEP_SIZE 0.2

#define NOISE_AMPLITUDE 0.75

#define FBM_ITERATIONS 3
#define FBM_AMPLITUDE_GAIN .8
#define FBM_FREQUENCY_GAIN 1.9

// 2*tan(radians(45)/2)
#define FOV45 0.82842693331417825056778150945139

vec3 UvToWorld(vec2 uv) { return normalize(vec3((uv-0.5) * iResolution.xy, -iResolution.y / FOV45)); }
vec3 Hash3( vec3 p ) { return fract(sin(vec3( dot(p,vec3(127.1,311.7,786.6)), dot(p,vec3(269.5,183.3,455.8)), dot(p,vec3(419.2,371.9,948.6))))*43758.5453); }
float Voronoi(vec3 p)
{
	vec3 n = floor(p);
	vec3 f = fract(p);

	float shortestDistance = 1.0;
	for (int x = -1; x < 1; x++) {
		for (int y = -1; y < 1; y++) {
			for (int z = -1; z < 1; z++) {
				vec3 o = vec3(x,y,z);
				vec3 r = (o - f) + 1.0 + sin(Hash3(n + o)*50.0)*0.2;
				float d = dot(r,r);
				if (d < shortestDistance) {
					shortestDistance = d;
				}
			}
		}
	}
	return shortestDistance;
}

float FractalVoronoi(vec3 p)
{
	float n = 0.0;
	float f = 0.8, a = 0.5;
	mat2 m = mat2(1, 0.6, -0.6, 0.8);
	for (int i = 0; i < FBM_ITERATIONS; i++) {
		n += Voronoi(p * f) * a;
		f *= FBM_FREQUENCY_GAIN;
		a *= FBM_AMPLITUDE_GAIN;
		p.xy = m * p.xy;
	}
	return n;
}

vec2 March(vec3 origin, vec3 direction)
{
	float depth = MARCH_STEP_SIZE;
	float d = 0.0;
	for (int i = 0; i < MAX_MARCH_STEPS; i++) {
		vec3 p = origin + direction * depth;
		d = FractalVoronoi(p) * NOISE_AMPLITUDE;
		depth += max(MARCH_STEP_SIZE, d);
	}
	return vec2(depth, d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 res = iResolution.xy;
	vec2 uv = (fragCoord.xy / res);

	vec3 direction = UvToWorld(uv);
	vec3 origin = vec3(0.0, -iTime*0.2, 0.0);
	vec2 data = March(origin, direction);

	vec4 color = vec4(.5, 0.615733, 0.555, 1) * data.y * data.x * 0.7;
        fragColor = mix(color, vec4(1,.2,1,1), max(0.0, 0.4-data.y));
}

const float beginZoomFactor = 20.0f;

const float endZoomFactor = 100.0f;

const float quarterPi = 0.78539816339f;

const float pi = 3.14159265359f;

const float doublePi = 6.28318530718f;

const float turnsPerSecond = 2.0f;

const vec2 movementSpeed = vec2(-200.0f, -200.0f);

const float zoomSpeed = 0.03125f;

const float spacing = 8.0f;

const float seperation = 32.0f;

const vec3 positions[] = vec3[]
(
    vec3(0.0f, 0.0f, 0.0f),
    vec3(0.0f, spacing, 0.0f),
    vec3(0.0f, -spacing, 0.0f),
    vec3(spacing, 0.0f, 0.0f),
    vec3(-spacing, 0.0f, 0.0f),
    vec3(spacing * cos(quarterPi), spacing * sin(quarterPi), 0.0f),
    vec3(-spacing * cos(quarterPi), spacing * sin(quarterPi), 0.0f),
    vec3(spacing * cos(quarterPi), -spacing * sin(quarterPi), 0.0f),
    vec3(-spacing * cos(quarterPi), -spacing * sin(quarterPi), 0.0f)
);

vec4 AlphaBlend(const in vec4 bottom, const in vec4 top)
{
    float alpha = top.a + bottom.a * (1.0f - top.a);
    return vec4((top.rgb * top.a + bottom.rgb * bottom.a * (1.0f - top.a)) / alpha, alpha);
}

vec4 Sphere(const in vec3 position, const in vec4 innerColor, const in vec4 outerColor, const in vec2 uv)
{
    vec4 ret = vec4(0.0f);
    vec2 offset_uv = uv - position.xy;
    if ((offset_uv.x * offset_uv.x) + (offset_uv.y * offset_uv.y) < 1.0f)
    {
        float view_dot_normal = dot(vec3(0.0f, 0.0f, 1.0f), vec3(offset_uv.x, offset_uv.y, sqrt(1.0f - (offset_uv.x * offset_uv.x) - (offset_uv.y * offset_uv.y))));
        ret = clamp((innerColor * view_dot_normal) + (outerColor * (1.0f - view_dot_normal)), 0.0f, 1.0f);
    }
    return ret;
}

vec4 Spheres(const in vec4 backgroundColor, const in vec4 innerColor, const in vec4 outerColor, const in vec2 uv, const in bool revert)
{
    vec4 ret = backgroundColor;
    for (int index = 0; index < positions.length(); index++)
    {
        float theta = iTime * doublePi * (revert ? -turnsPerSecond : turnsPerSecond);
        float cos_theta = cos(theta);
        float sin_theta = sin(theta);
        vec3 position = vec3((positions[index].x * cos_theta) - (positions[index].y * sin_theta), (positions[index].x * sin_theta) + (positions[index].y * cos_theta), positions[index].z);
        ret = AlphaBlend(ret, Sphere(position, innerColor, outerColor, mod(uv + (seperation * 0.5f), seperation) - (seperation * 0.5f)));
    }
    return ret;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec4 color = vec4(0.125f, 0.125f, 0.25f, 1.0f);
    vec2 uv = ((fragCoord - iMouse.xy - (iResolution.xy * 0.5f)) * mix(endZoomFactor, beginZoomFactor, (cos(iTime * doublePi * zoomSpeed) * 0.5f) + 0.5f) / iResolution.y) + vec2(movementSpeed.x * cos(iTime), movementSpeed.y * sin(iTime));
    color = Spheres(color, vec4(1.0f, 0.75f, 0.75f, 0.75f), vec4(1.0f, 0.25f, 0.25f, 0.75f), uv, false);
    color = Spheres(color, vec4(0.75f, 1.0f, 0.75f, 0.75f), vec4(0.25f, 1.0f, 0.25f, 0.75f), uv + vec2(seperation * 0.5f, seperation * 0.5f), false);
    color = Spheres(color, vec4(0.75f, 1.0f, 0.75f, 0.75f), vec4(0.25f, 1.0f, 0.25f, 0.75f), uv + vec2(0.0f, seperation * 0.5f), true);
    color = Spheres(color, vec4(0.75f, 0.75f, 1.0f, 0.75f), vec4(0.25f, 0.25f, 1.0f, 0.75f), uv + vec2(seperation * 0.5f, 0.0f), true);
    fragColor = vec4(color.xyz, 1.0f);
}

float pi = acos(-1.);
float glo = 0.;

float smAdd(float m1, float m2, float k)
{
    float h = clamp(.5 + .5*(m2 - m1)/k, 0., 1.);
    return mix(m2, m1, h) - k*h*(1. - h);
}

float bump(vec3 p, float offs)
{
    return .25*sin(p.x*3.4 + (iTime + offs)*.73) *
        	   cos(p.y*3.7 + (iTime + offs)*.81) *
        	  (sin(p.z*4.0 + (iTime + offs)*.83) + cos(p.z*2.9 + (iTime + offs)*.57));
}

vec2 sph(vec3 p, float s, float offs, float matId)
{
    return vec2(length(p + bump(p, offs)) - s, matId);
}

vec2 map(vec3 p)
{   
    float matId1 = 0.;
    float matId2 = 1.;
    float dist = 1.1*sin(iTime*.7) + 3.;
    vec2 m1 = sph(vec3(p.x + dist/2., p.y, p.z), 1.5, 133., matId1);
    vec2 m2 = sph(vec3(p.x - dist/2., p.y, p.z), 1.5, 0., matId2);
    float matmix = mix(matId1, matId2, abs(m2.x - m1.x));
    vec2 m = vec2(smAdd(m1.x, m2.x, .6), matmix);
    glo += .1 / (1.5 + m.x*m.x*5000.);
    return m;
}

float sss(vec3 p, vec3 l, float d)
{
	return smoothstep(0., 1., map(p + l*d).x/d);
}

vec2 tr(vec3 ro, vec3 rd)
{
    float far = 10.;
	vec2 h,t= vec2(.01);
	for(int i = 0; i < 256; i++)
	{
		h = map(ro + rd*t.x);
		if(h.x < .0001 || t.x > far)
			break;
		t.x += h.x * .2;
        t.y = h.y;
  	}
    if(t.x > far)
        t.y = -1.;
	return t;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec3 ro = vec3(cos(.15*iTime),
                   sin(-.15*iTime),
                   -3.5);
	vec3 camTarget = vec3(0.);
	vec3 up = vec3(0., 1., 0.);
	vec3 camDir = normalize(camTarget - ro);
	vec3 camRight = normalize(cross(up, ro));
	vec3 camUp = normalize(cross(camDir, camRight));
    vec3 lightPos = vec3(.07, .07, -10.);
    vec3 lightPos2 = vec3(-5.07, -5.07, -12);
  	vec3 lightPos3 = vec3(5.07, -5.07, -12);

	vec2 screenPos = -1. + 2. * gl_FragCoord.xy / iResolution.xy;
	screenPos.x *= iResolution.x / iResolution.y;

	vec2 eps = vec2(0., .01);
	vec3 rd = normalize(camRight*screenPos.x + camUp*screenPos.y + camDir);

	vec2 t = tr(ro, rd);

    vec3 colRot = .7 * vec3(sin(.151*((iTime+180.) + 44.)),
                       		sin(.227*((iTime+180.) + 55.)),
                       		sin(.317*((iTime+180.) + 79.))) + 1.35;

    if (t.y > -1.)
    {
        vec3 hit = ro + rd*t.x;
        vec3 lightDir = normalize(lightPos - hit);
        vec3 lightDir2 = normalize(lightPos2 - hit);
        vec3 lightDir3 = normalize(lightPos3 - hit);

        vec3 norm = normalize(map(hit).x - vec3(map(hit - eps.yxx).x,
                              					map(hit - eps.xyx).x,
                              					map(hit - eps.xxy).x));

        vec3 l = -lightDir;
        float sub = 0.;
        float steps = 750.;

        for(float i = 1.; i < steps; i++)
        {
            float dist = i*2. / steps;
            sub += sss(hit, l, dist);
        }

        float diff = max(0., dot(lightDir, norm));
        float spec = pow(max(dot(rd, reflect(norm, lightDir)), 0.), 100.);
        float spec2 = pow(max(dot(rd, reflect(norm, lightDir2)), 0.), 80.);
        float spec3 = pow(max(dot(rd, reflect(norm, lightDir3)), 0.), 45.);
        float ao = clamp(map(t.x + norm*.5).x / .5, 0., 1.);

        vec3 col = .3 * colRot * (t.y + .3);
        col += sub * colRot * .006;
        col *= .15 * ao;
        col += .65 * diff * (colRot.yzx/(t.y*6.));
        col += .7 * spec * vec3(1., 1., 1.);
        col += .7 * spec2 * vec3(1., 1., 1.);
        col += .9 * spec3 * vec3(1., 1., 1.);

        col += glo*.01*colRot.xzy*(t.y);

        fragColor = vec4(col, 1.);
    }
    else
    	fragColor = vec4(glo*.01*colRot.yzx*t.y, 1.);
}

