void mainImage( out vec4 O, vec2 U )
{
    if (iFrame==0) { 
        O-=O; 
        if (floor(U) == floor(R/2.) )  O.x = 1.; // initial infection : 1
      //if ( hash(U) < Init ) O.x = 1.;          // initial infection : random people
        return; }

    O = T(U);                                    // recover previous state

    if ( iFrame % Step > 0 ) return;             // 1 step delay at a time
    if ( O.x > 0. ) { O.x *= .95; return; }      // already contaminated 
    float n = 0.;                                // local propagation with probability #Contag

    for ( int k=0; k<9; k++ ) 
        if( T(U+vec2(k%3,k/3)-1.).x > 0. ) n++;
    if ( hash(U+iTime) < n*Contag ) O.x = 1., O.z = .3;  // contaminated

    if ( ivec2(U)%Glob != ivec2(Glob)/2 ) return;
    n = 0.;                                      // long distance propagation with probability #Contag
    for ( int k=0; k<9; k++ ) 
        if( T(U+float(Glob)*(vec2(k%3,k/3)-1.)).x > 0. ) n++;
    if ( hash(U+iTime) < n*ContagG ) O.x = 1., O.y = 1.; // contaminated

