#define TAU 6.283185307
#define DIRECTIONS 4.

vec4 solver(sampler2D self, vec2 pos, float timeDelta) {
    vec4 local = texture(self, pos);
    // Update shortest path length
    local.z = max(local.z, local.x*local.y);
    // Evaluate neighboring maxima
    vec4 neighborMax = vec4(0.0);
    for (float i = 0.; i < DIRECTIONS; ++i)
    {
        float a = TAU*(i)/(DIRECTIONS);
        vec2 neighborPos = pos + (1./iResolution.xy)*vec2(sin(a), cos(a));
        vec4 neighbor = texture(self, neighborPos);
        neighbor.rgb *= neighbor.a;
        neighborMax = max(neighborMax, neighbor);
    }
    // Attenuate wave
    neighborMax.rg *= 4095.0/4096.0;
    // Update local values
    local.rgb = max(local.rgb, neighborMax.rgb*local.a);
    return local;
}

vec4 initialState(vec2 pos) {
    vec4 inputt = texture(iChannel1, pos);
    vec4 color = vec4(0.0, 0.0, 0.0, 1.0);
    // Detect starting point
    if (inputt.g > 0.5 && inputt.r + inputt.b < 1.0)
        color.g = 1.0;
    // Detect end point
    if (inputt.r > 0.5 && inputt.g + inputt.b < 1.0)
        color.r = 1.0;
    // Detect boundary
    if (inputt.r < 0.5 && inputt.g < 0.5 && inputt.b < 0.5)
        color.a = 0.0;
    return color;
}

void mainImage( out vec4 o, in vec2 f )
{
    if (iFrame < 10)
//    o = texture(iChannel1, f/iResolution.xy);
    o = initialState(f/iResolution.xy);
    else
    o = solver(iChannel0, f/iResolution.xy, iTimeDelta);
    o = clamp(o, 0., 1.);
}

