vec2 force (vec4 a, vec4 b) {
    vec2 r = b.xy-a.xy, v = b.zw-a.zw;
    float l = length(r);
    if (l < 1e-4) return vec2(0);
    vec2 f = vec2(0);
    if (l!=0.){
    f += (1.-dot(a.zw,b.zw))*r/max(1.,l*l*l);
    f -= 8.*r/max(1.,l*l*l*l*l);
    }

    if (length(f)>1.) return normalize(f);
    return f;
}
float sg (vec2 p, vec2 a, vec2 b) {
    if (length(a-b)<1e-4||length(a)<1e-4||length(b)<1e-4) return 1e9;

    float i = clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.);
	float l = (length(p-a-(b-a)*i));
    return l;
}
float ssg (vec2 p, vec2 a, vec2 b) {
    if (length(a-b)<1e-4||length(a)<1e-4||length(b)<1e-4) return 1e9;

    float i = clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.);
	float l = (length(p-a-(b-a)*i));
    l -= .75;
    if (l>0.) return l;
    else return -1e4+length(b-a);

}
float line (vec2 p, vec2 a, vec2 b) {
    if (length(a-b)<1e-4||length(a)<1e-4||length(b)<1e-4) return 1e9;
    float i = max(dot(p-a,b-a)/dot(b-a,b-a),0.);
	float l = (length(p-a-(b-a)*i));
	l -= .75;
    if (l>0.) return l;
    else return -1e4+abs(length(a-b)-0.5*length(p-a));
}

