//Graph is in Buf A, graph traversal is in Buf B
//You can pause the simulation by setting dt in Common to 0.
//Change the number of particles by changing N in Common

float gauss(float x, float r)
{
    x/=r;
    return exp(-x*x);
}

float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

links getGraphLinks(int id, int R)
{ 
    links L;
    for(int j = 0; j < linkN/4; j++)
    {
        vec4 side0 = texel(ch0, i2xy(ivec3(id, j, 1)));
        for(int i = j*4; i < j*4 + 4; i++)
        {
            L.ids[i] = int(side0[i%4]);
        }   
    }
    return L;
}

vec4 getParticlePos(int id)
{ 
    return texel(ch0, i2xy(ivec3(id, 0, 0)));
}

float getNd(vec2 p, int id)
{
    float d = 1e10;
    vec4 pp = getParticlePos(id);
    links nb = getGraphLinks(id, 0);
    for(int i = 0; i<12; i++)
    {
        if(nb.ids[i] >= 0) d = min(d, sdSegment(p, getParticlePos(nb.ids[i]).xy, pp.xy));
    }
    return d;
}

float getGraph(vec2 p)
{
    float d = 1e10;

    	d = min(d, getNd(p, (iFrame/30)%tot_n));
	return gauss(d, 1.);
}

vec2 findPath(vec2 p, vec2 pos, int initguess)
{
    int cid = initguess; //closest id
    vec2 cxy = getParticlePos(cid).xy;
    float cd = distance(p,cxy); //best distance
    float d = 1e10;
    for(int i = 0; i < 30; i++) 
    {
        int prev_best = cid;
        vec2 prev_p = cxy;
        links nb = getGraphLinks(cid, 0);
        for(int i = 0; i < linkN; i++)
        {
            if(nb.ids[i]<0) continue;
            vec2 pp = getParticlePos(nb.ids[i]).xy;
            float td = distance(p, pp);
            if(td < cd) 
            {
                cd = td;
                cxy = pp;
                cid = nb.ids[i];
            }
        }
        d = min(d, sdSegment(pos, prev_p, cxy));
        if(cid == prev_best) break; //found closest(or impossible to find a better one)
    }

    return vec2(gauss(d, 1.), cid);
}

float bisectd(vec2 p, vec2 a, vec2 b)
{
    float dd = distance(b,a);
    return dot(p - a, b - a)/dd - dd*0.5;
}

float bdist(vec2 p, int id)
{
    links nbl = getGraphLinks(id, 0);
    vec2 p0 = getParticlePos(id).xy;
    float d = 1e10;
    for(int j = 0; j <8; j++)
    {
        if(nbl.ids[j]<0) continue;
        vec2 pp = getParticlePos(nbl.ids[j]).xy;
       	d = min(d, bisectd(p, pp, p0));
    }
    return d;
}

void mainImage( out vec4 fragColor, in vec2 pos )
{
    //float gd = getGraph(pos);
    vec2 path = findPath(iMouse.xy, pos, 0);
    float e = max(bdist(pos, int(path.y)), 0.);
    fragColor = sin(0.3*vec4(1,2,3,4)*(e + path.x)) + texel(ch1, pos).zzzz;
}

