void mainImage( out vec4 U, in vec2 pos )
{
    ivec2 p = ivec2(floor(pos));
    if(any(greaterThan(p, sN*N-1))) discard;

    ivec3 sid = xy2i(p);
    ID = sid.x;
   	if(sid.z == 0) //particle computation
    {
        data = getParticle(ID);

        vec2 F = vec2(0,-0.003);

        data[0].zw += F*dt;
        data[0].xy += data[0].zw*dt;

        //border conditions
        if(size.x - data[0].x < 2.) data[0].z = -abs(data[0].z);
        if(data[0].x < 2.) data[0].z = abs(data[0].z);
        if(size.y - data[0].y < 2.) data[0].w = -abs(data[0].w);
        if(data[0].y < 2.) data[0].w = abs(data[0].w);

        if(iFrame<1) //initial condition
        {
            data[0].xy = size*hash22(pos);
            data[0].zw = 2.*(hash22(PI*pos) - 0.5);
        } 
    }
    else //graph computation
    {
        ppos = getParticlePos(ID);

        d = vec4(1e6);
        data = mat4(vec4(-1),vec4(-1),vec4(-1),vec4(-1));

        links nb = getGraphLinks(ID, sid.y);

        avgR = 0.; float NN = 0.;
        for(int i = min(0,iFrame); i < 8; i++)
        {	
            if(nb.ids[i] >= 0) 
            {
                NN++;
        		avgR += dist2p(ppos.xy, nb.ids[i]);    
            }
        }
        avgR /= NN;

        sortlinks(nb, sid.y);

        for(int i = min(0,iFrame); i < linkN; i++)
        	sortneighbors(nb.ids[i], sid.y);

        sortrandom(sid.x, sid.y);
    }
    U = save(data, sid.y);
}

mat4 getParticle(int id)
{
    mat4 da;
    for(int i = 0; i < sN.x; i++) da[i] = texel(ch0, i2xy(ivec3(id, i, 0))); 
    return da;
}

vec4 save(in mat4 da, int i)
{
    return da[i];
}

vec4 getParticlePos(int id)
{ 
    return texel(ch0, i2xy(ivec3(id, 0, 0)));
}

float dist2p(vec2 p, int id)
{
    return distance(getParticlePos(id).xy,p);
}

void sort(int idtemp, int R) //sort closest particles in 8 directions
{
    if(idtemp == ID || idtemp < 0 || any(equal(data[R], vec4(idtemp)))) return; //particle already sorted
    vec4 px = getParticlePos(idtemp);
    vec4 dx = px - ppos;

    if(R < 2) //closest
    {
        int dir = int(4.*((atan(dx.y, dx.x)+PI)/PI)); 
        int diri = dir%4;
        int dirR = dir/4;
        float nd = length(dx.xy);
        if(dirR == R && nd < d[diri])
        {
            d[diri] = nd;
            data[R][diri] = float(idtemp);
        }
    } 
    else // far particles
    {
        int dir = int(2.*((atan(dx.y, dx.x)+PI)/PI)); 
        int diri = dir%4;
        int dirR = R;
        float nd = abs(length(dx.xy) - exp2(float(R))*2.*avgR);
        if(dirR == R && nd < d[diri])
        {
            d[diri] = nd;
            data[R][diri] = float(idtemp);
        }
    }


}

void sortlinks(in links nb, int R)
{
    for(int i = min(0, iFrame); i<linkN; i++) sort(nb.ids[i], R);
}

void sortneighbors(int id, int R)
{
    links nb = getGraphLinks(id, R);
    sortlinks(nb, R);
}

void sortrandom(int id, int R)
{
    for(int i = 0; i<12; i++) sort((12*iFrame+ID+i)%tot_n, R);
}

