vec3 findClosestObj(vec2 p, int initguess)
{
    ivec4 cid = ivec4(initguess, ivec3(-1)); //closest id
    vec2 cxy = getParticlePos(cid.x).xy;
    vec4 cd = vec4(distance(p,cxy), vec3(1e10)); //best distance
    bool found = false;
    links nb;
    for(int i = 0; i < 12; i++) 
    {
        int prev_best = cid.x;
        vec2 prev_p = cxy;
        nb = getGraphLinks(cid.x, 0);
        float safeR = 1e10; //safe radius - if point is inside of it - definitely closest
        for(int i = 0; i < linkN; i++)
        {
            if(nb.ids[i]<0) continue;
            vec2 pp = getParticlePos(nb.ids[i]).xy;
            float td = distance(p, pp);
           	float sR = 0.5*distance(prev_p, pp);
            if(safeR > sR) safeR = sR;
            if(td < cd.x) 
            {
                cd = vec4(td, cd.xyz);
                cxy = pp;
                cid = ivec4(nb.ids[i], cid.xyz);
            }
            else if(td < cd.y)
            {
                cd.yzw = vec3(td, cd.yz);
                cid.yzw = ivec3(nb.ids[i], cid.yz);
            }
            else if(td < cd.z)
            {
                cd.zw = vec2(td, cd.z);
                cid.zw = ivec2(nb.ids[i], cid.z);
            }
            else if(td < cd.w)
            {
                cd.w = td;
                cid.w = nb.ids[i];
            }
        }
        //found closest(or impossible to find a better one)
        if(cid.x == prev_best) 
        {
            if(cd.x < safeR) found = true;
            break;
        }    
    }

    if(!found)
    //check second best
    for(int j = 1; j < 4; j++)
    {
        nb = getGraphLinks(cid[j], 0);
        for(int i = 0; i < linkN; i++)
        {
            if(nb.ids[i]<0) continue;
            vec2 pp = getParticlePos(nb.ids[i]).xy;
            float td = distance(p, pp);
            if(td < cd.x) 
            {
                cd.x = td;
                cxy = pp;
                cid.x = nb.ids[i];
            }
        }
    }

    return vec3(cxy, cid.x);
}

void mainImage( out vec4 U, in vec2 pos )
{
    int cid = 0;//int(texel(ch1, pos).x); //use prev data - makes it super fast
    if(iFrame < 1) cid =0;
    vec3 co = findClosestObj(pos, cid);
    U.x = co.z;
    U.y = distance(pos,co.xy);
    U.z = exp(-0.03*U.y);
}

