#define GAMMA 1


// https://iquilezles.org/articles/intersectors
bool boxIntersect( in vec3 ro, in vec3 rd, in vec3 cen, in vec3 rad ) 
{
	vec3 roo = ro - cen;
    if( abs(roo.x)<rad.x && abs(roo.y)<rad.y && abs(roo.z)<rad.z ) return true;

    vec3 m = 1.0/rd;
    vec3 n = m*roo;
    vec3 k = abs(m)*rad;

    vec3 t1 = -n - k;
    vec3 t2 = -n + k;

	float tN = max( max( t1.x, t1.y ), t1.z );
	float tF = min( min( t2.x, t2.y ), t2.z );

	return ( tN < tF && tF > 0.0);
}


// https://iquilezles.org/articles/intersectors
vec3 triIntersect( in vec3 ro, in vec3 rd, in vec3 v0, in vec3 v1, in vec3 v2 )
{
    vec3 v1v0 = v1 - v0;
    vec3 v2v0 = v2 - v0;
    vec3 rov0 = ro - v0;

    vec3  n = cross( v1v0, v2v0 );
    vec3  q = cross( rov0, rd );
    float d = 1.0/dot( rd, n );
    float u = d*dot( -q, v2v0 );
    float v = d*dot(  q, v1v0 );
    float t = d*dot( -n, rov0 );

    if( u<0.0 || v<0.0 || (u+v)>1.0 ) t = -1.0;

    return vec3( t, u, v );
}

#define ZERO_NOLOOP min(0,iFrame)

bool intersectMesh( in vec3 ro, in vec3 rd, float tmax, out float oDis, out vec3 oNor, int exclude, out int faceId)
{
   if( !boxIntersect( ro, rd, bboxCtr, bboxSiz ) )
        return false;

    bool	res = false;
    float	tmin = tmax;
    for( int i=ZERO_NOLOOP; i < numVertices / 3; i++ )
    {
		// get the triangle        
        vec3 v0 = getVertex(uint(i*3));
        vec3 v1 = getVertex(uint(i*3 + 2));
        vec3 v2 = getVertex(uint(i*3 + 1));

        int id = i * 2;
        if(id != exclude)
        {
            vec3 h = triIntersect( ro, rd, v0, v1, v2 );
            if( h.x > 0. && h.x < tmin)
            {
                tmin = h.x;
                oNor = normalize(cross(v1 - v0, v2 - v0));;
                oDis = tmin;
                faceId = id;
                res = true;
            }
        }

        // Mirror X  

        id = i * 2 + 1;
        if(id != exclude)
        {
            v0.x *= -1.;
            v1.x *= -1.;
            v2.x *= -1.;
            vec3 h = triIntersect( ro, rd, v0, v1, v2 );
            if( h.x > 0. && h.x < tmin)
            {
                tmin = h.x;
                oNor = normalize(cross(v2 - v0, v1 - v0));;
                oDis = tmin;
                faceId = id;
                res = true;
            }
        }

    }

    return res;
}


vec3 getSkyColor(vec3 rd)
{
    vec3 col = texture(iChannel0, rd).rgb;
    #if GAMMA
    	col = pow(col, vec3(2.2));
    #endif
    return col;
}


#define MAX_BOUNCES 10
#define ABSORB		vec3(0, 5, 0)

vec3 Render(in vec3 ro, in vec3 rd, in float dist)
{
    float sgn = 1.;
    float cref = 0.7;
    vec3  col = vec3(0);
    vec3  rel = vec3(1);
    float transp = 0.99;
    vec3  absorb = ABSORB;
    int	  exclude = -1;
    for(int i = 0; i < MAX_BOUNCES; i++)
    {
        vec3	n;
        float 	d;
        if(!intersectMesh(ro, rd, 15., d, n, exclude, exclude))
        {
            col += rel * getSkyColor(rd);
            return col;
        }
        vec3 rabs = mix(absorb, vec3(0), (sgn + 1.) / 2.);
        vec3 beerlamb = exp(-rabs * d);
        vec3 p = ro + rd * d;
        n *= sgn;
        vec3 refl = reflect(rd, n);
        vec3 refr = refract(rd, n, cref);
        //float fresnel = 1.0 - pow(dot(n, -rd), 2.);
        float fresnel = pow(1.0 - abs(dot(n, rd)), 2.0);
        float reflectorFactor = mix (0.2, 1.0, fresnel);
        float refractionFactor = mix (transp, 0., fresnel);

    	col += (1.0 - refractionFactor) * rel * beerlamb * getSkyColor(refl) * reflectorFactor;
    	rel *= refractionFactor * beerlamb;     

       	ro = p;     
        if (refr == vec3(0.0))
        {
            rd = refl;
        }
        else
        {
            rd = refr; 
            sgn *= -1.;
            cref = 1. / cref;
        }        
    }
    col += rel * getSkyColor(rd);
    return col;
}



void mainVR( out vec4 fragColor, in vec2 fragCoord, in vec3 ro, in vec3 rd )
{
    fragColor = vec4(Render(ro/3. + vec3(0.0,.0,4.0),rd, 14.) ,1);
}

mat3 setCamera( in vec3 ro, in vec3 ta )
{
	vec3 cw = normalize(ta-ro);
	vec3 up = vec3(0, 1, 0);
	vec3 cu = normalize( cross(cw,up) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}


vec3 vignette(vec3 color, vec2 q, float v)
{
    color *= 0.3 + 0.8 * pow(16.0 * q.x * q.y * (1.0 - q.x) * (1.0 - q.y), v);
    return color;
}

vec3 desaturate(in vec3 c, in float a)
{
    float l = dot(c, vec3(1. / 3.));
    return mix(c, vec3(l), a);
}

