}

vec2 getMax(vec2 id){
    vec2 ans = id;
    for (float i = -1.; i < 2.; i++){
        for (float j = -1.; j < 2.; j++){
            ans = (getValue(id + vec2(i, j)) > getValue(ans)) ? id + vec2(i, j) : ans;
        }
    }
    return ans;
}

vec2 upperbound(vec2 id){
    vec2 ans = getMax(id);
    for (float i = -1.; i < 2.; i++){
        for (float j = -1.; j < 2.; j++){
            float f = getValue(id + vec2(i, j));
            ans = (f < getValue(ans) && f > getValue(id)) ? id + vec2(i, j) : ans;
        }
    }
    return ans;
}

vec2 to(vec2 id){
