vec3 obDisp, obRnd, sunDir, qHit;
vec2 cId;
float dstFar, tCur, bGrid, balRad, obRot, flmCylRad, flmCylLen;
int idObj;
bool cOcc, balFlm;
const float pi = 3.14159;

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

void ObjState ()
{
  float r, s;
  r = length (cId);
  obRnd = Hashv3v2 (cId + 17.17);
  obDisp.xz = bGrid * (cId + 0.5);
  if (r > 0.) obDisp.xz += Rot2D (vec2 ((0.45 * bGrid - balRad) * (0.2 + 0.8 * obRnd.x), 0.),
     2. * pi * (0.2 * obRnd.z * tCur + obRnd.x));
  else obDisp.xz += 0.45 * bGrid - balRad;
  s = sin (2. * pi * (0.05 * obRnd.y * tCur + obRnd.x));
  obDisp.y = 12. + 6. * s;
  obRot = 0.1 * pi * (obRnd.z - 0.5) * tCur;
  balFlm = (s < 0.);
  cOcc = (r == 0. || obRnd.x > 0.7);
}

float FlmDf (vec3 p)
{
  vec3 q;
  float d, dMin;
  dMin = dstFar;
  if (cOcc) {
    p -= obDisp;
    q = p - vec3 (0., -0.8 * balRad, 0.);
    d = PrCylDf (q.xzy, flmCylRad + 0.3 * q.y / flmCylLen, flmCylLen);
    d = max (d, - q.y - 0.5 * flmCylLen);
    DMINQ (0);
  }
  return dMin;
}

float FlmRay (vec3 ro, vec3 rd)
{
  vec3 p, rdi;
  vec2 cIdP, s;
  float dHit, d, eps;
  eps = 0.0005;
  if (rd.x == 0.) rd.x = 0.001;
  if (rd.z == 0.) rd.z = 0.001;
  rdi.xz = 1. / rd.xz;
  cIdP = vec2 (-999.);
  dHit = eps;
  for (int j = VAR_ZERO; j < 160; j ++) {
    p = ro + dHit * rd;
    cId = floor (p.xz / bGrid);
    if (cId != cIdP) {
      ObjState ();
      cIdP = cId;
    }
    d = FlmDf (p);
    s = (bGrid * (cId + step (0., rd.xz)) - p.xz) * rdi.xz;
    d = min (d, abs (min (s.x, s.y)) + eps);
    dHit += d;
    if (d < eps || dHit > dstFar) break;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d;
  dMin = dstFar;
  if (cOcc) {
    p -= obDisp;
    p.xz = Rot2D (p.xz, obRot);
    q = p;
    d = max (PrSphDf (q, balRad), - PrSphDf (q, 0.98 * balRad));
    d = max (d, - PrCylDf (q.xzy - vec3 (0., 0., - balRad), 0.3 * balRad, 0.1 * balRad));
    DMINQ (1);
    q = p;
    d = PrCylDf (q.xzy - vec3 (0., 0., -1.42 * balRad), 0.05 * balRad, 0.13 * balRad);
    d = max (d, - PrCylDf (q.xzy- vec3 (0., 0., 0.02 * balRad), 0.03 * balRad, 0.13 * balRad));
    DMINQ (2);
    q = p;
    d = PrCylDf (q.xzy - vec3 (0., 0., -1.5 * balRad), 0.2 * balRad, 0.07 * balRad);
    d = max (d, - PrCylDf (q.xzy - vec3 (0., 0., -1.48 * balRad), 0.18 * balRad, 0.07 * balRad));
    DMINQ (3);
    q = p;
    q.xz = abs (q.xz) - 0.25 * balRad;
    q.y -= -1.15 * balRad;
    q.yz = Rot2D (q.yz, -0.35);
    q.xy = Rot2D (q.xy, 0.35);
    d = PrCylDf (q.xzy, 0.005 * balRad, 0.35 * balRad);
    DMINQ (4);
  }
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 p, rdi;
  vec2 cIdP, s;
  float dHit, d, eps;
  eps = 0.0005;
  if (rd.x == 0.) rd.x = 0.001;
  if (rd.z == 0.) rd.z = 0.001;
  rdi.xz = 1. / rd.xz;
  cIdP = vec2 (-999.);
  dHit = eps;
  for (int j = VAR_ZERO; j < 160; j ++) {
    p = ro + dHit * rd;
    cId = floor (p.xz / bGrid);
    if (cId != cIdP) {
      ObjState ();
      cIdP = cId;
    }
    d = ObjDf (p);
    s = (bGrid * (cId + step (0., rd.xz)) - p.xz) * rdi.xz;
    d = min (d, abs (min (s.x, s.y)) + eps);
    dHit += d;
    if (d < eps || dHit > dstFar) break;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec3 FlmCol (vec3 p, vec3 rd)
{
  vec3 q, qq;
  float a, f, dr;
  a = 0.;
  p.y -= - flmCylLen;
  dr = 0.05 / flmCylRad;
  for (int j = 0; j < 20; j ++) {
    p += dr * rd;
    q = 15. * p / flmCylLen;
    q.y -= 40. * tCur;
    qq.y = Noisefv3 (q + 0.1 * vec3 (sin (tCur)));
    qq.x = Noisefv3 (q + vec3 (qq.y));
    qq.z = Noisefv3 (q + vec3 (qq.x));
    q = p + 0.25 * (1. - 3. * p.y / flmCylLen) * (qq - 0.5);
    f = 0.45 * q.y - 2.5 * length (q.xz);
    f = clamp (sign (f) * f * f, 0., 1.) * (3. - 0.9 * q.y);
    a += f;
  }
  return clamp (a * vec3 (1., 0.5, 0.3), 0., 1.);
}

vec3 BgCol (vec3 ro, vec3 rd)
{
  vec3 col, p, c1, c2;
  float ds, fd, att, attSum, sd;
  c1 = vec3 (0.3, 0.2, 0.1);
  c2 = vec3 (0.1, 0.3, 0.);
  if (rd.y > -0.003 && rd.y < 0.01 + 0.03 * Fbm1 (16. * atan (rd.z, - rd.x))) {
    col = 0.5 * (c1 + c2);
  } else if (rd.y < 0.) {
    p = ro - (ro.y / rd.y) * rd;
    p.xz -= 0.2 * tCur;
    col = mix (c1, c2, Fbm2 (0.5 * p.xz)) * (1. - 0.1 * Noisefv2 (17. * p.xz));
    col = mix (col, 0.5 * (c1 + c2), smoothstep (-0.1, 0., rd.y));
  } else {
    p = ((200. - ro.y) / rd.y) * rd;
    ds = 0.1 * sqrt (length (p));
    p += ro;
    fd = 0.002 / (smoothstep (0., 10., ds) + 0.1);
    p.xz = p.xz * fd + 0.1 * tCur;
    att = Fbm2 (p.xz);
    attSum = att;
    for (float j = 0.; j < 4.; j ++) {
      attSum += Fbm2 (p.xz + (1. + j * ds) * fd * sunDir.xz);
    }
    attSum *= 0.3;
    att *= 0.3;
    sd = clamp (dot (sunDir, rd), 0., 1.);
    col = mix (vec3 (0.5, 0.75, 1.), mix (vec3 (0.7, 1., 1.), vec3 (1., 0.4, 0.1),
       0.25 + 0.75 * sd), exp (-2. * (3. - sd) * max (rd.y - 0.1, 0.))) +
       0.3 * (vec3 (1., 0.8, 0.7) * pow (sd, 1024.) + vec3 (1., 0.4, 0.2) * pow (sd, 256.));
    attSum = 1. - smoothstep (1., 9., attSum);
    col = mix (vec3 (0.4, 0., 0.2), mix (col, vec3 (0.3, 0.3, 0.3), att), attSum) +
       vec3 (1., 0.4, 0.) * pow (attSum * att, 3.) * (pow (sd, 10.) + 0.5);
  }
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 vn, col, colFlm, qHitFlm, u;
  float dstObj, dstFlm, s, h;
  bool inSun, inFlm;
  dstFlm = FlmRay (ro, rd);
  if (dstFlm >= dstFar) balFlm = false;
  qHitFlm = qHit;
  inFlm = balFlm;
  dstObj = ObjRay (ro, rd);
  inSun = true;
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    if (idObj != 1) balFlm = false;
    if (idObj == 1) {
      h = 0.1 + 0.8 * obRnd.z;
      u = qHit / (0.3 * balRad);
      col = mix (HsvToRgb (vec3 (h + 0.5, 0.8, 0.8)), HsvToRgb (vec3 (h, 0.7, 0.8)),
         smoothstep (0., 0.02, abs (abs (u.y) - 1.7) - 0.07));
      u.xz = Rot2D (u.xz, pi / 24.);
      u.xz = Rot2D (u.xz, 2. * pi * (floor (6. * atan (u.x, u.z) / (2. * pi) + 0.5) / 6.));
      col = mix (vec3 (1., 0., 0.), col, smoothstep (0., 0.01, length (Rot2D (vec2 (abs (u.x) - 0.3, u.y),
          pi / 6.) * vec2 (2., 1.)) - 1.));
      if (length (qHit) < 0.99 * balRad) {
        col *= 0.2;
        if (balFlm) col += 0.1 * vec3 (1., 0.5, 0.);
        inSun = false;
      } else {
        if (length (qHit) > 0.99 * balRad) vn.xz = Rot2D (vn.xz, 0.1 * pi *
           sin (pi * (0.5 - mod (24. * (atan (qHit.x, qHit.z) / (2. * pi) + 0.5), 1.))));
      }
    } else if (idObj == 2) {
      vn.xz = Rot2D (vn.xz, 0.1 * pi * sin (pi * (0.5 - mod (12. * (atan (qHit.x, qHit.z) /
         (2. * pi) + 0.5), 1.))));
      col = vec3 (0.6);
    } else if (idObj == 3) {
      vn.xz = Rot2D (vn.xz, 0.1 * pi * sin (pi * (0.5 - mod (32. * (atan (qHit.x, qHit.z) /
         (2. * pi) + 0.5), 1.))));
      col = vec3 (0.6, 0.3, 0.);
    } else if (idObj == 4) {
      col = vec3 (0.3);
    }
    if (inSun) col = col * (0.3 + 0.3 * max (- dot (vn, normalize (vec3 (sunDir.xz, 0.).xzy)), 0.) +
       0.1 * max (vn.y, 0.) + 0.8 * max (dot (vn, sunDir), 0.)) +
       0.05 * pow (max (0., dot (sunDir, reflect (rd, vn))), 32.);
  } else {
    col = BgCol (ro, rd);
    balFlm = false;
  }
  if (dstFlm < min (dstFar, dstObj)) {
    if (inFlm) {
      colFlm = FlmCol (qHitFlm, rd);
      col = mix (col, colFlm, 0.6 * length (colFlm));
    }
  }
  if (balFlm) {
    qHit.xz = Rot2D (qHit.xz, - obRot);
    qHit.y += 0.5 * balRad;
    col = mix (col, vec3 (1., 0.5, 0.),
       0.4 * pow (clamp (dot (normalize (qHit), - rd), 0., 1.), 4.));
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd;
  vec2 canvas, uv;
  float el, az, h;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  tCur += 10.;
  dstFar = 150.;
  bGrid = 6.;
  balRad = 2.;
  flmCylRad = 0.4;
  flmCylLen = 2.;
  h = 12. + 4. * sin (2. * pi * 0.02 * tCur);
  az = 0.3 * pi + 2. * pi * mod (0.003 * tCur, 1.);
  el = -0.005 * pi * (h - 12.);
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += 0.5 * pi * mPtr.y;
  }
  el = clamp (el, -0.3 * pi, 0.3 * pi);
  vuMat = StdVuMat (el, az);
  ro = vec3 (vec2 (-0.49 * bGrid), h).xzy;
  rd = vuMat * normalize (vec3 (uv, 4.5));
  sunDir = normalize (vec3 (1., 0.2, -1.));
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

float PrSphDf (vec3 p, float s)
{
  return length (p) - s;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1., 0., 1.), c.y);
}

const float cHashM = 43758.54;

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

vec3 Hashv3v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39);
  return fract (sin (dot (p, cHashVA2) + vec3 (0., cHashVA2.xy)) * cHashM);
}

vec4 Hashv4v3 (vec3 p)
{
  vec3 cHashVA3 = vec3 (37., 39., 41.);
  return fract (sin (dot (p, cHashVA3) + vec4 (0., cHashVA3.xyz)) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Noisefv3 (vec3 p)
{
  vec4 t;
  vec3 ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp *= fp * (3. - 2. * fp);
  t = mix (Hashv4v3 (ip), Hashv4v3 (ip + vec3 (0., 0., 1.)), fp.z);
  return mix (mix (t.x, t.y, fp.x), mix (t.z, t.w, fp.x), fp.y);
}

float Fbm1 (float p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noiseff (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f;
}

////////////////////////////////////////////////////////////////////////////////
//
// Project an Apollonian gasket onto a sphere.
//
// Now with transparent sphere and glowing Apollonian.
//
// Since (reverse) stereographic projection preserves circles (it's an inversion)
// we can use it to project a planar Apollonian gasket onto a sphere.
//
// The gasket is generated from an n-sided prism and the resulting pattern is
// based on the dual double ended n-sided pyramid (or vice-versa if dual mode
// is selected).
//
// <mouse>: apply a Mobius transform to the sphere
// <up>/<down>: move in and out
// <left>/<right>: change number of circles
// b: show background
// c: color cycle
// r: autorotation
// s: show sphere
// t: transparency
// i: invert with mouse or rotate
// x: special case for tetrahedron (dual mode only)
// d: show dual polyhedron
//
////////////////////////////////////////////////////////////////////////////////

const float AA = 2.0;
const int maxiterations = 50;

int NCIRCLES = 4;      // 3 or more
bool dobackground = false;
bool docolorcycle = false;
bool domonochrome = false;
bool dorotate = true;
bool dosphere = true;
bool dotransparent = true;
bool dodual = true;
bool dotetrahedron = false;

const float PI = 3.14159265;

void assert(bool b);
vec3 hsv2rgb(vec3 c);
vec3 transform(in vec3 p);

// If z is inside the circle, invert to the outside.
// s is 1 or -1, and sets sense of "inside"
bool checkinverse(inout vec2 z, vec3 circle, float s, inout float mindist2) {
  vec2 c = circle.xy;  // Centre of circle
  float r2 = circle.z; // Radius squared
  vec2 p = z-c;
  float p2 = dot(p,p);
  float d2 = s*p2 - s*r2;
  if (d2 > 0.0) {
    d2 /= r2;
    if (s < 0.0) d2 = 1.0/d2;
    mindist2 = min(d2/r2,mindist2);
    return false;
  }
  z = p*r2/p2 + c;
  return true;
}

bool checkinverse(inout vec2 z, vec3 circle, float s) {
  float mindist = 1e8;
  return checkinverse(z,circle,s,mindist);
}

// A ring of circles, centred on the unit circle, with
// incircle and outcircle. No outcircle for tetrahedron.
bool checkcircles0(inout vec2 z, inout float mindist2) {
  float theta = PI/float(NCIRCLES);
  float r = 1.0/cos(theta);
  float s = tan(theta);
  vec3 c1 = vec3(0,0,pow(r-s,2.0));
  vec3 c2 = vec3(0,0,pow(r+s,2.0));
  if (checkinverse(z,c1,1.0,mindist2)) return true;
  if (!dotetrahedron && checkinverse(z,c2,-1.0,mindist2)) return true;
  for (int i = 0; i < NCIRCLES; i++) {
    float t = 2.0*float(i)*theta-0.5*PI;
    vec3 c = vec3(r*cos(t), r*sin(t), s*s);
    if (checkinverse(z,c,1.0,mindist2)) return true;
  }
  return false;
}

// Two rings of circles, touching along the unit circle.
bool checkcircles1(inout vec2 z, inout float mindist2) {
  float theta = PI/float(NCIRCLES);
  float K = sin(theta);
  float r = K/(1.0+K);
  float s = K/(1.0-K);
  for (int i = 0; i < NCIRCLES; i++) {
    float t = 2.0*float(i)*theta;
    vec2 c = vec2(cos(t),sin(t));
    vec3 c0 = vec3((1.0-r)*c,r*r);
    if (checkinverse(z,c0,1.0,mindist2)) return true;
    vec3 c1 = vec3((1.0+s)*c, s*s);
    if (checkinverse(z,c1,1.0,mindist2)) return true;
  }
  return false;
}

bool checkcircles(inout vec2 z, inout float mindist2) {
  if (dodual) return checkcircles0(z,mindist2);
  else return checkcircles1(z,mindist2);
}

// r*(1+sqrt(2)) = 1 => r = 1/(1+sqrt(2))
vec4 gasket(vec2 z){
  // 3 circles can either be a tetrahedron, with some
  // special casing, or a triangular prism (with dual
  // a double triangular pyramid).
  if (dotetrahedron) z /= 2.0; // Scale for tetrahedron.
  for (int n = 0; n < maxiterations; n++){
    float mindist2 = 1e8;
    if (!checkcircles(z,mindist2)) return vec4(z,n,mindist2);
  }
  return vec4(z,maxiterations,0.0);
}

float framethreshold = 0.0;
vec3 gasketcolor(vec2 z) {
  vec4 data = gasket(z);
  if (framethreshold > 0.0 && data.z < framethreshold) return vec3(0);
  float t = docolorcycle ? 0.05*iTime : 0.0;
  float h = data.z/float(maxiterations);
  float v = max(2.0,data.z)/(0.3*float(maxiterations));
  vec3 col = hsv2rgb(vec3(t+h,1,smoothstep(0.0,1.0,v)));
  //float d = 1.0/(sqrt(data.w)+1.0);
  //float d = sqrt(data.w);
  //col *= smoothstep(0.0,0.1,d);
  return col;
}

// R3 inversion in sphere, centre q, square radius r2.
vec3 invert(vec3 p, vec3 q, float r2) {
  p -= q;
  p *= r2/dot(p,p);
  p += q;
  return p;
}

vec3 stereographic0(vec3 p) {
  return invert(p,vec3(0,0,-1),2.0);
}

// Invert in sphere radius sqrt(2), centre (0,0,-1)
// is stereographic projection from the unit sphere
// to z=0 (and its inverse).
vec2 stereographic(vec3 p) {
#if 0
  float k = 1.0/(1.0-p.z);
  vec2 z = k*p.xy;
  //assert(distance(istereographic(z),p) < 1e-4);
  return z;
#else
  return stereographic0(p).xy;
#endif
}

vec3 istereographic(vec2 z) {
#if 0
  float k = 2.0/(dot(z,z)+1.0);
  vec3 p = vec3(k*z,1.0-k);
  //assert(distance(stereographic(p),z) < 1e-4);
  return p;
#else
  return stereographic0(vec3(z,0));
#endif
}

bool doinversion = true;
vec3 getspherecolor(vec3 p) {
  p = transform(p); // autorotation
  // Apply inversion to sphere.
  if (true) {
    vec3 q = vec3(0);
    if (doinversion && iMouse.x > 0.0) {
      vec3 m = vec3((2.0*iMouse.xy-iResolution.xy)/iResolution.y,0);
      q = vec3(m.xyz);
    }
    // Any point is the centre of a sphere orthogonal to the
    // unit sphere, inverting in which leaves the unit sphere
    // invariant. Any point inverts to another point on the
    // line to the sphere centre, so just find other
    // intersection of ray p to r with unit sphere.
    // |p + kr|^2 = 1 where r = q-p
    // (p.p + 2kp.r + k^2(r.r)) = 1
    // 2p.r + k(r.r) = 0 since p.p = 1 & dividing by k
    vec3 r = q-p;
    float k = -2.0*dot(p,r)/dot(r,r);
    p += k*r;
  }
  // And stereographic projection from (0,0,1) to plane z = 0
  return gasketcolor(stereographic(p));
}

struct Ray {
  vec3 q;               // origin
  vec3 d;               // direction
};

struct Hit {
  float t;      // solution to p=q+t*d
  vec3 n;       // normal
};

struct Sphere {
  vec3 p;       // centre
  float r;      // radius
};

bool intersectSphere(Sphere s, Ray ray, out Hit hit[2]) {
  vec3 p = s.p;
  float r = s.r;
  float c = length(p);
  vec3 q = ray.q, d = ray.d;
  // Formula for x^2 + 2Bx + C = 0
  // |q + t*d - p|^2 = r^2
  float B = dot(q-p,d);
  float C = dot(q,q)-2.0*dot(q,p)+(c+r)*(c-r);
  float D = B*B - C;
  if (D < 0.0) return false;
  D = sqrt(D);
  float t0,t1;
  if (B >= 0.0) {
    t0 = -B-D; t1 = C/t0;
  } else {
    t1 = -B+D; t0 = C/t1;
  }
  hit[0] = Hit(t0,(q+t0*d-p)/r);
  hit[1] = Hit(t1,(q+t1*d-p)/r);
  return true;
}

bool intersectScene(Ray r, out Hit hit[2]) {
  Sphere s = Sphere(vec3(0),1.0);
  if (intersectSphere(s,r,hit)) {
    return true;
  }
  return false;
}

vec3 light;

vec3 solve(Ray r,vec2 uv) {
  Hit hit[2];
  vec3 c = vec3(0);
  if (!dosphere || !intersectScene(r,hit)) {
    if (dobackground) {
      vec3 p = istereographic(uv);
      c = getspherecolor(p);
      c *= dosphere ? 0.2 : 0.8;
    }
  } else {
    for (int ix = 0; ix < 2; ix++) {
      int i = dotransparent ? 1-ix : ix;
        if (hit[i].t < 0.0) continue;
      vec3 n = hit[i].n;
      if (dot(r.d,n) > 0.0) n *= -1.0; // ???
      vec3 basecolor = getspherecolor(hit[i].n);
      if (basecolor == vec3(0)) continue;
      vec3 color = vec3(0);
      float ambient = 0.5;
      float diffuse = 0.8;
      color += basecolor*ambient;
      color += basecolor*diffuse*max(0.0,dot(light,n));
      float specular = pow(max(0.0,dot(reflect(light,n),r.d)),5.0);
      vec3 speccolor = basecolor; //vec3(1);
      color += 1.0*specular*speccolor;
      if (!dotransparent) return color;
      c = mix(c,color,0.7);
    }
  }
  return c;
}

