  dosphere = !key(CHAR_S);
  dotransparent = !key(CHAR_T);
  doinversion = key(CHAR_I);
  NCIRCLES = max(3,4+keycount(KEY_RIGHT)-keycount(KEY_LEFT));
  dotetrahedron = key(CHAR_X) && dodual && NCIRCLES == 3;
  //framethreshold = float(max(0,keycount(KEY_PAGE_UP)-keycount(KEY_PAGE_DOWN)));

  light = vec3(0.5,1.0,1.0);
  light = normalize(light);

  vec3 p = vec3(0,0,6);
  p.z *= exp(-0.1*float(keycount(KEY_UP)-keycount(KEY_DOWN)));
  vec3 col = vec3(0);
  for (float i = 0.0; i < AA; i++) {
    for (float j = 0.0; j < AA; j++) {
      vec2 uv = (2.0*(fragCoord+vec2(i,j)/AA)-iResolution.xy)/iResolution.y;
      vec3 r = vec3(uv,-6);
