// A little SDF ray-marching "framework".
// Much of the techniques are copied from IQ's demos and articles.

#define AA 1
#define ZERO (min(iFrame,0))
// Pinched from "Happy Jumping"
//#define DEBUGSTEPS

/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
// Constants

const float tmin = 0.01;								// Ray minimum distance
const float tmax = 15.0;								
const int raySteps = 100;								// Max iterations when resolving a ray
const int shadowSteps = 50;								// Max iterations when resolving a shadow ray
const float limit = 0.0005;								// Convergence to surface limit when ray tracing
const float screenDist = 0.5;							// Screen distance as a fraction of screen width
const vec3 light = normalize(vec3(-1.0, -0.75, -0.52));	// Light direction
const float ambient = 0.3;								// Ambient light
const int bounces = 2;									// Max reflection bounces (actually bounces + 1, as includes initial ray)
const int shadowBounces = 1;							// Bounces during which shadow rays are cast.

/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
// Data structures

struct lightingParams {
 	float diffuse;
    float specular;
    float shiny;
};

struct material {
    vec3 col;											// Surface colour
    lightingParams lighting;
    float reflective;									// Reflectiveness. 0 = not reflective. 1 = completely reflective (e.g. mirror).
};

struct rayInfo {
    vec3 origin;
    vec3 dir;
};

/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
// Functions (implemented at the bottom)

// Get camera ray for specified pixel
rayInfo getRay(in vec2 fragCoord);

// Get VR ray
rayInfo getVRRay(in vec2 fragCoord, in vec3 fragRayOri, in vec3 fragRayDir);

// Get sample point.
// Format is vec4:
//	x,y		= Material parameter (e.g. uv)
//	z		= Distance to nearest surface
//	w		= Material index (will be passed to getMaterial())
vec4 map(in vec3 p);

// Get material properties by index
material getMaterial(float index, vec2 uv, vec3 p);

// Get colour for ray that misses
vec3 getRayMissCol(in rayInfo ray);

/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
// Trig

const float PI = 3.14159265;

vec3 rotatex(in vec3 p, float a) {
	return vec3(p.x,
                cos(a) * p.y - sin(a) * p.z,
                cos(a) * p.z + sin(a) * p.y);
}

vec3 rotatey(in vec3 p, float a) {
	return vec3(cos(a) * p.x + sin(a) * p.z,
                p.y,
                cos(a) * p.z - sin(a) * p.x);
}

vec3 rotatez(in vec3 p, float a) {
	return vec3(cos(a) * p.x + sin(a) * p.y,
                cos(a) * p.y - sin(a) * p.x,
                p.z);
}

vec3 rotate(in vec3 p, in vec3 euler) {
 	return rotatey(rotatex(rotatez(p, euler.z), euler.x), euler.y);   
}

/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
// Helper functions

bool checkerboard(in vec2 p) {
	float x = mod(p.x, 2.0);
    if (x < 0.0) x += 2.0;
    float y = mod(p.y, 2.0);
    if (y < 0.0) y += 2.0;
    return (x < 1.0) == (y < 1.0);
}

// Pinched from: https://iquilezles.org/articles/smin
float sminCubic( float a, float b, float k )
{
    float h = max( k-abs(a-b), 0.0 )/k;
    return min( a, b ) - h*h*h*k*(1.0/6.0);
}

/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
// Combining shapes
//
// Sample format is vec3:
//	x,y		= Material parameter (e.g. texture coordinates)
//	z		= Distance

// Union of two shapes
vec3 combine(in vec3 p1, in vec3 p2) { 
    return p1.z < p2.z ? p1 : p2;
}

// Smoothed union of two shapes
vec3 combine(in vec3 p1, in vec3 p2, float k) {
    return vec3(p1.z < p2.z ? p1.xy : p2.xy, sminCubic(p1.z, p2.z, k));
}

// Subtract shape p2 from p1
vec3 carve(in vec3 p1, in vec3 p2) { 
    return p1.z > -p2.z ? p1 : vec3(p2.xy, -p2.z);
}

vec3 carve(in vec3 p1, in vec3 p2, float k) {
 	return vec3(p1.z > -p2.z ? p1.xy : p2.xy, -sminCubic(-p1.z, p2.z, k));   
}

// Intersection of two shapes
vec3 intersect(in vec3 p1, in vec3 p2) { 
    return p1.z > p2.z ? p1 : p2;
}

/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
// Shape primitives

vec3 sphere(in vec3 p, float rad) { 
    return vec3(atan(p.x, p.y) * rad, atan(length(p.xy), p.z) * rad, length(p) - rad); 
}
vec3 box(in vec3 p, in vec3 size) {
    vec3 o = abs(p) - size;						// Distance inside/outside each side
    float d = max(o.x, max(o.y, o.z));   		// Negative distance to nearest wall if inside box
    if (d > 0.0) {								// Refine distance if outside box
	    vec3 n = min(o, vec3(0));				// Clamp to box boundaries to get nearest point. (Could be edge/corner).
        d = length(o - n);
    }
    vec3 a = abs(o);							// Nearest wall determines texture coordinates
    vec2 uv = a.x < a.y && a.x < a.z ? p.yz		
    		: a.y < a.z 			 ? p.xz
            :                          p.xy;
    return vec3(uv, d);
}
vec3 planeBottom(in vec3 p, float y)				{ return vec3(p.xz, y-p.y); }
vec3 planeTop(in vec3 p, float y) 					{ return vec3(p.xz, p.y-y); }
vec3 cylinder(in vec3 p, float radius, float height) { 
    vec3 tube = vec3(atan(p.x, p.z) * radius, p.y, length(p.xz) - radius);
    vec3 top = planeTop(p, height);
    vec3 bottom = planeBottom(p, -height);
    return intersect(tube, intersect(top, bottom));
}
vec3 torus(in vec3 p, float radius, float thickness) { 
    vec2 circleOffs = vec2(length(p.xz) - radius, p.y);
    return vec3(atan(p.x, p.z) * radius, atan(circleOffs.x, circleOffs.y) * thickness, length(circleOffs) - thickness);
}
vec3 line(in vec3 p, in vec3 from, in vec3 to, float thickness) {

    vec3 d = to - from;
    vec3 n = normalize(d);
    float l = length(d);

    float proj = dot(p - from, n);
    proj = clamp(proj, 0.0, l);
    vec3 pt = from + n * proj;
    return vec3(0, 0, length(p - pt) - thickness);    
}

// Creating and combining full sample including material index
vec4 makes(in vec3 d, float material) {
    // Like a distance sample, but with w = material index.
    // Specifically:
    //	x,y	= material parameter (e.g. texture coord)
    //	z	= distance
    //	w	= material index
 	return vec4(d, material);   
}

vec4 merges(in vec4 result, in vec3 d, float material) {
 	return d.z < result.z ? makes(d, material) : result;
}

vec4 merge(in vec4 result, in vec4 d) {
 	return d.z < result.z ? d : result;   
}

/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
// Rendering

// Calculate ray direction for a given pixel
vec3 screenRay(in vec2 s) {
    s -= iResolution.xy / 2.0;
    return vec3(s / (screenDist * iResolution.x), 1);
}

// Simple lighting
float shading(in vec3 e, in vec3 n, in lightingParams p) {

    // Diffuse term
    float d = dot(n, -light) * p.diffuse;

    // Specular term
    vec3 h = -(normalize(e) + light) / 2.0;
    float s = pow(dot(n, h), p.shiny) * p.specular;

    return max(d + s, 0.0);
}

// Calculate normal at a point
vec3 normal(in vec3 p, float t) {

	// Calculate pixel size at point distance. 
    // This will be the distance of the normal samples from the original point.
    float s = 0.1 / t;

    // Sample relative distance along each axis
    float d = map(p).z;			// Need original distance to compare it to
    vec3 r = vec3(
        map(p + vec3(s,0,0)).z - d,
        map(p + vec3(0,s,0)).z - d,
        map(p + vec3(0,0,s)).z - d);

    return normalize(r);
}

// Cast regular ray
vec4 raycast(in vec3 from, in vec3 dir) {

    float t = tmin;
    vec4 d;
    int i;
    for (i = 0; i < raySteps && t < tmax; i++) {
        vec3 p = from + dir * t;
        d = map(p);
        if (abs(d.z) <= limit * t) {
            d.z = t;
#ifdef DEBUGSTEPS
            d.x = float(i) / float(raySteps);
#endif            
            return d;
        }
        t += d.z;
    }    

    // Avoid flicker when polygons are edgewise to the camera.
    // In this case the ray cannot quite find the surface and runs out of steps.
    // Just return the last sample point.
    if (t < tmax) {
#ifdef DEBUGSTEPS
        d.x = float(i) / float(raySteps);
#endif            
        d.z = t;
        return d;
    }

#ifdef DEBUGSTEPS
    return vec4(float(i) / float(raySteps),0,-1.0,0);
#else    
    return vec4(0,0,-1.0,0);
#endif    
}

// Cast shadow ray
float shadow(in vec3 from, in vec3 dir, float k) {
    float res = 1.0;
    float t = tmin;    
    for (int i = 0; i < shadowSteps && t < tmax; i++) {
     	vec3 p = from + dir * t;
        float d = map(p).z;
        if (d <= 0.0)
            return 0.0;
        res = min(res, k*d/t);
        t += d;
    }

    return res;
}

vec3 drawPixel(in rayInfo ray) {
    ray.dir = normalize(ray.dir);

    // Sum up reflection bounces    
    vec3 sumCol = vec3(0);
    float alpha = 1.0;
    for (int bounce = 0; alpha > 0.0002; bounce++) {

	    // Ray cast
	    vec4 r = bounce < bounces 
            ? raycast(ray.origin, ray.dir) 
            : vec4(0,0,-1,0);					// Last ray always misses

#ifdef DEBUGSTEPS        
        sumCol = vec3(r.x*16.0 - floor(r.x*16.0), r.x*4.0 - floor(r.x*4.0), r.x);
        break;
#endif        

        // Distance is returned in r.z
	    // Miss?
	    if (r.z == -1.0)
	    {
            vec3 missCol = getRayMissCol(ray);
            sumCol += missCol * alpha;
	        break;
	    }

	    // Position and normal
	    vec3 p = ray.origin + r.z * ray.dir;
	    vec3 n = normal(p, r.z);

        // Get material info
        material mat = getMaterial(r.w, r.xy, p);

        // Apply lighting
        float i = shading(ray.dir, n, mat.lighting);
        if (i > 0.0 && bounce < shadowBounces) 
            i *= shadow(p, -light, 16.0);    
        i = clamp(i + ambient, 0.0, 1.0);
        mat.col *= i;

        // Add to sum of colours
        mat.col *= (1.0 - mat.reflective);
        sumCol += mat.col * alpha;
        alpha *= mat.reflective;

        // Calculate reflection vector
        ray.origin = p;
        ray.dir = normalize(reflect(ray.dir, n));
    }    

    return sumCol;
}

// Main entrypoints

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
#if AA > 1    
    vec3 col = vec3(0);
    for( int m=ZERO; m<AA; m++ )
    for( int n=ZERO; n<AA; n++ )
    {
	    rayInfo ray = getRay(fragCoord + vec2(m,n) / vec2(AA));
	    col += drawPixel(ray) / (float(AA) * float(AA));
	    fragColor = vec4(col, 1);
    }
#else    
    rayInfo ray = getRay(fragCoord);
    vec3 col = drawPixel(ray) / (float(AA) * float(AA));
    fragColor = vec4(col, 1);
#endif    
}

void mainVR(out vec4 fragColor, in vec2 fragCoord, in vec3 fragRayOri, in vec3 fragRayDir) 
{
 	rayInfo ray = getVRRay(fragCoord, fragRayOri, fragRayDir);
    vec3 col = drawPixel(ray);
    fragColor = vec4(col, 1);
}

//-----------------------------------------------------------------------------------------------------------------------------------

const vec3 camCentre = vec3(0, .4, 0);
const vec3 camOffs = vec3(0, 0, -3.5);

rayInfo getRay(in vec2 fragCoord) {    

    // Get base ray
    rayInfo ray;
	ray.origin = camOffs;
    ray.dir = screenRay(fragCoord);

    // Apply camera rotation
    float camY = iTime * .2 + 0.9;
    float camX = sin(iTime*.1)*.35 + .6;

    //camX = (1. - iMouse.y / iResolution.y) * PI/2.;
    //camY = iMouse.x / iResolution.x * PI*2.;

    ray.origin = camCentre + rotatey(rotatex(ray.origin, camX), camY);
    ray.dir = rotatey(rotatex(ray.dir, camX), camY);

    return ray;
}

rayInfo getVRRay(in vec2 fragCoord, in vec3 fragRayOri, in vec3 fragRayDir) {
 	rayInfo ray;
    ray.origin = rotatey(fragRayOri * 40.0 + vec3(0,28,10), PI*0.5);
    ray.dir = rotatey(fragRayDir, PI*0.5);
    return ray;
}

vec3 getRayMissCol(in rayInfo ray) {
	return texture(iChannel0, ray.dir).xyz;    
}

// Material types.
// Returned by map(). Then passed into getMaterial() to get colour, lighting etc.
const float groundMat = 0.;
const float donutMat = 1.;
const float icingMat = 2.;
const float candyMat = 3.;

// Lookup material colour etc
material getMaterial(float matType, vec2 uv, vec3 p) {

    // Set defaults
    material mat;
    mat.col = vec3(0.8);
    mat.lighting.diffuse = 0.8;
    mat.lighting.specular = 0.6;
    mat.lighting.shiny = 64.0;
    mat.reflective = 0.0001;

    if (matType < groundMat + 1.) {
        int u = int(uv.x + 1000.);
        int v = int(uv.y + 1000.);
        if ((u & 1) == (v & 1))
            mat.col = vec3(.3,.3,.4);
       	else
            mat.col = vec3(0,.025,.1);
        mat.reflective = 0.1;
    }
    else if (matType < donutMat + 1.) {
        mat.col = vec3(1,.6,.3) * .8;
    }
    else if (matType < icingMat + 1.) {
     	mat.col = vec3(.9,.8,1);
        mat.reflective = 0.02;
    }
    else if (matType < candyMat + 1.) {
		float f = fract(matType) * 2.*PI * 3.;
        mat.col = min(vec3(sin(f), sin(f+.666*PI), sin(f+1.333*PI)) * 1.5 + vec3(.6), vec3(1));
        mat.reflective = 0.075;
    }

    return mat;
}

vec4 donut(in vec3 p) {
    p.y-=.45;
    float ang = atan(p.x, p.z);
    float d = length(p.xz);

	// Base    
    vec3 base = torus(p, 1., .45);

    // Icing
    vec3 icingTorus = torus(p, 1., .48);
    float waveFactor = (d > 1. ? 1. : .5);
    float wave = sin(ang * 12. * waveFactor) * .062 
               + sin(ang * 34. * waveFactor + .7) * 0.027
               + sin(ang * 42. * waveFactor + 1.7) * 0.015;

    vec3 icingWave = vec3(0, 0, p.y - (wave + .03));    
    vec3 icing = carve(icingTorus, icingWave, .2);

    // Candy
    const float candySpacing = 2.*PI / 11.;
    float candyAng = round(ang / candySpacing) * candySpacing;

    vec3 candyPos = vec3(sin(candyAng), .51, cos(candyAng)) * 1.;
    vec3 candyp = p - candyPos;
    candyp.y *= 3.;
    vec3 candyShape = sphere(candyp, 0.15);
    candyShape.z /= 3.;
    vec4 candy = makes(candyShape, candyMat + fract(candyAng/(2.*PI)));


    // Candy icing indentation
    vec3 indentPos = candyPos + vec3(sin(candyAng * 7.)*.01,0,sin(candyAng * 11.)*.01);
    indentPos.y -= 0.1;
    vec3 indentp = p - indentPos;
    indentp.y *= 2.5;
    vec3 icingIndent = sphere(indentp, 0.2);
    icingIndent.z /= 2.5;
    icing = combine(icing, icingIndent, .07);
    icing = carve(icing, candyShape, 0.05);

    // Table surface objects
    p.y += .45;

    // Fallen candy
    vec3 fallCandyp = p - vec3(-.5, 0.05, 1.6);
    fallCandyp.y *= 3.;
    vec3 fallCandy = sphere(fallCandyp, 0.15);
    fallCandy.z /= 3.;

    // Icing blobs
    p.y *= 2.;
    vec3 blob1 = sphere(p - vec3(1.5, 0, 0), 0.25);
    vec3 blob2 = sphere(p - vec3(1.7,0,.45), 0.175);
    vec3 blob3 = sphere(p - vec3(1.9,0,-.35), 0.1);
    vec3 blob = combine(combine(blob1, blob2, .5), blob3);
    blob.z /= 2.;

    vec4 r = makes(base, donutMat);
    r = merges(r, icing, icingMat);
    if (candyAng != 0.) r = merge(r, candy);
    r = merges(r, blob, icingMat);
    r = merges(r, fallCandy, candyMat + 0.0);
