vec2 R;

#define A iTime*.5
#define B cos(iTime*.75)*.5

vec3 hash32(vec2 p){
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy+p3.yzz)*p3.zyx);
}

mat2 rot(float a) {
    return mat2(cos(a), -sin(a), sin(a), cos(a));
}

float march(vec3 ro, vec3 rd){
    float d = 0.0, t = 0.;
    vec3 rp=vec3(0);

    for(int i = 0; i < 60; i++){
        rp = ro + rd*t;
        d = length(rp) - 1.9;
        if(abs(d) <= 0.02)break;
        if(t >= 20.){t = 20.; break;}
        t += d * .75;
    }
	return t;
}

vec3 bg(vec3 rd){
    vec2 id = floor(rd.xy*10.);
    float chk = mod(id.x + id.y, 2.);

    return hash32(id+floor(iTime*2.)) * chk;
}

vec3 color(vec3 p, vec3 rd, vec3 n, float t){
    vec3 ld = vec3(0., .6, -.75);

	float dif = max(dot(ld, n), 0.0);
    p.xy*=rot(-iTime*.5);

    p.x -= A;
    p.y -= B;

    vec2 id = floor(p.xy*5.);
    float chk = mod(id.x + id.y, 2.);
    vec3 mat = vec3(0.8) * (chk+.1);

    return mix( mat * dif, bg(rd), step(20., t));
}

void mainImage( out vec4 f, in vec2 u ){
    R = iResolution.xy;
