    vec3 ro = vec3(0., .0, -5.);
    vec3 rd = normalize(vec3(uv, 1.0));

    vec3 col = vec3(0);

    float t = march(ro, rd);
    vec3 p = ro + rd*t;
    vec3 norm = normalize(p);

    rd.x += A;
    rd.y += B;

    col = color(p,rd, norm, t);

    f = vec4(col, 1.0);
}

