void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    // normalize coordinates ranging from -1. to 1 and fix aspect ratio
    vec2 uv = (2.*fragCoord - iResolution.xy)/iResolution.y;

    uv*=4.;

    // divides the screen into a grid
    // the id of a grid cell. X and y go from -4 to 4.
    vec2 id = floor(uv); 

    // inner coordinates of a grid cell
    vec2 gv = fract(uv);

    // ####################################
    // # First pass
    // ####################################

    // index of the voronoi cell containing the shading point
    vec2 cellIndex = vec2(0.);

    // the center of the voronoi cell
    vec2 center = vec2(0.0);

    // Distance to the center
    float minDist = 8.;  

    // the grid cell with the closest point
    vec2 closestGridCell = vec2(0.);

    // finds which cell contains the closest point to the shading point. 
    // Only 9 cells in the grid must looped through.
    for (float y = -1.; y <= 1.; y++) {
        for (float x = -1.; x <= 1.; x++) {
            vec2 offset = vec2(x, y);
            vec2 n = 0.5+sin(N22(id+offset) * iTime)*0.5;

            float d = length(offset+n - gv);
            if (d < minDist) {
                center = offset+n - gv;
                cellIndex = id+offset;
                minDist = d;
                closestGridCell = offset;
            }
        }
    }

    // ####################################
    // # Second pass
    // ####################################

    // calculates distance to the closest border

    // performs neighbor search centered at the closest cell instead of
    // the cell that contains the shading point
    float borderDist = 8.0;
    for (float y = -2.; y <= 2.; y++) {
       for (float x = -2.; x <= 2.; x++) {

           vec2 offset = closestGridCell + vec2(x, y);
           vec2 n = 0.5+sin(N22(id+offset)*iTime)*0.5;

           vec2 r = offset + n - gv;

           // skip the same cell
           if (dot(center-r, center-r) > .01) {            
           	borderDist = min(borderDist, dot(0.5*(center+r),normalize(r-center)));
           }
        }
     }

    vec3 col = vec3(minDist);

    if (cellIndex.x > 3.) {
    	col = vec3(0.70, 0.62, 0.85);
    }

    else if (cellIndex.x > -1.) {
        col = vec3(0.99, 0.64, 0.66);
    }

    else if (cellIndex.x > -5.) {
        col = vec3(0.47, 0.87, 0.47);
    }

     else if (cellIndex.x > -9.) {
        col = vec3(0.46, 0.62, 0.80);
    }

    // edges  
    col = mix(vec3(0.2, 0.2, 0.2), col, S(0.01, 0.06, borderDist));

    // isolines
    col -= abs(sin(100.0*minDist))*0.1;

    fragColor = vec4(col, 1.0);
}

