}

float edgedistance(vec3 pos, vec3 X) {
  //assert(eq(length(X),1.0));  // Use dot(X,X)?
  return length(pos-dot(pos,X)*X);
}

float raydistance(vec3 pos, vec3 q, vec3 r) {
  float s = max(0.0, dot(pos-q,r));
  return distance(pos,q+s*r);
}

// Perpendicular distance from p to segment ab
float segment(vec3 p, vec3 a, vec3 b) {
  vec3 pa = p - a;
  vec3 ba = b - a;
  float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
  float d = length(pa - ba * h);
  return d;
}

float linedist(vec3 pos, vec3 a, vec3 b) {
  vec3 pa = pos - a;
  vec3 ba = b - a;
  float h = dot(pa, ba) / dot(ba, ba);
  float d = length(pa - ba * h);
  return d;
}

float max3(float a, float b, float c) {
