// Geometric globals

// A,B,C are the mirror planes
// a,b,c are triangle corners or trihedron edge vectors or face normals
vec3 A,B,C,a,b,c;
vec3 tri2bary;

// Could use a matrix here
vec3 applybary(vec3 bary, vec3 p, vec3 q, vec3 r) {
  return bary.x*p+bary.y*q+bary.z*r;
}

vec3 refla(vec3 p) { return p - 2.0*dot(p,A)*A; } // Fold about A plane
vec3 reflb(vec3 p) { return p - 2.0*dot(p,B)*B; } // Fold about B plane
vec3 reflc(vec3 p) { return p - 2.0*dot(p,C)*C; } // Fold about C plane

// Planes are represented as vec4s: (a,b,c,d) where p = (x,y,z) is
// in plane just when (x,y,z,-1).(a,b,c,d) = 0

// For our vec4 plane representations
vec4 refla(vec4 p) { return vec4(refla(p.xyz),p.w); }
vec4 reflb(vec4 p) { return vec4(reflb(p.xyz),p.w); }
vec4 reflc(vec4 p) { return vec4(reflc(p.xyz),p.w); }

void init() {
  if (swap) {
    int t = Q; Q = R; R = t;
  }
  // Setup folding planes
  float p = PI/float(P);
  float q = PI/float(Q);
  float r = PI/float(R);
  A = vec3(1,0,0);
  // A.B = -cos(p)
  B = vec3(-cos(p),sin(p),0);
  // A.C = -cos(r)
  float x = -cos(r);
  // B.C = -cos(q) = cos(p)cos(r) + y sin(p)
  float y = -(cos(p)*cos(r)+cos(q))/sin(p);
  // |C| = 1
  float z = sqrt(1.0-x*x-y*y);
  C = vec3(x,y,z);
  a = cross(B,C);
  b = cross(C,A);
  c = cross(A,B);
  // Weights to convert trilinear (distance from region planes) to barycentric
  // (weighted sum of triangle vertices).
  tri2bary = vec3(length(a),length(b),length(c));
  a = normalize(a);
  b = normalize(b);
  c = normalize(c);
