// Could use an ivec3 and count different types of flip
vec3 fold(vec3 pos, out int flips) {
  // We want to know the number of actual reflections made
  // so keep track of that in flips.
  flips = 0;
  for (int i = 0; i < NFOLDS; i++) {
    float k = dot(pos,A);
    flips += int(k < 0.0);
    pos -= 2.0*min(0.0,k)*A;
    k = dot(pos,B);
    flips += int(k < 0.0);
    pos -= 2.0*min(0.0,k)*B;
    k = dot(pos,C);
    flips += int(k < 0.0);
    pos -= 2.0*min(0.0,k)*C;
  }
  return pos;
}

vec3 fold(vec3 pos) {
  int flips;
  return fold(pos,flips);
}

  // p is the "triangle point"
// pa,pb,pc is its reflection in the 3 walls
