vec4 aplane, bplane, cplane;

void initp(vec3 tri) {
  p = applybary(tri2bary*tri,a,b,c);
  if (true) {
    float scale = min3(edgedistance(p,A),
                       edgedistance(p,B),
                       edgedistance(p,C));
    p /= scale;
  }
  aplane = vec4(a,dot(p,a)); // Face planes
  bplane = vec4(b,dot(p,b));
  cplane = vec4(c,dot(p,c));
  pa = refla(p);
  pb = reflb(p);
  pc = reflc(p);
}

vec3 aa,bb,cc;
vec3 a0,a1,b0,b1,c0,c1;
vec3 paa,pbb,pcc;
vec3 ab,ac,ba,bc,ca,cb;

void scenesetup() {
  aa = a/dot(p,a);
  bb = b/dot(p,b);
  cc = c/dot(p,c);

  a0 = refla(reflb(p));
  a1 = refla(reflc(p));
  b0 = reflb(reflc(p));
  b1 = reflb(refla(p));
  c0 = reflc(refla(p));
  c1 = reflc(reflb(p));

  paa = normalize(pa); //pa/dot(pa,pa);
  pbb = normalize(pb); //pb/dot(pb,pb);
  pcc = normalize(pc); //pc/dot(pc,pc);

#if 1
  // These vectors give a cut at right angles to the
  // face, rather than the edge planes.
  ba = normalize(cross(a,cross(a,b)));
  ca = normalize(cross(a,cross(a,c)));
  ab = normalize(cross(b,cross(b,a)));
  cb = normalize(cross(b,cross(b,c)));
  ac = normalize(cross(c,cross(c,a)));
  bc = normalize(cross(c,cross(c,b)));
#else
  ba = normalize(a-b);
  ca = normalize(a-c);
  ab = normalize(b-a);
  cb = normalize(b-c);
  ac = normalize(c-a);
  bc = normalize(c-b);
#endif  
}

float leonardo(float d, float da, float dba, float dca) {
  float d1 = da; 
  d1 = max(d1,-da-lwidth); // The inner plane - now we have a 'plate'
  d1 = max(d1,max(-dba,-dca)); // A single face
  d = min(d,max(d1,dba-lwidth));
  d = min(d,max(d1,dca-lwidth));
  return d;
}

float faces(vec3 pos) {
  vec4 pos4 = vec4(pos,-1);
  // Signed distance from 3 planes
  float d = 1e8;
  float da = dot(pos4,aplane); // The main plane
  float db = dot(pos4,bplane);
  float dc = dot(pos4,cplane);
  if (!doleonardo) return max(da,max(db,dc));
  float dba = dot(pos-p,ba), dca = dot(pos-p,ca);
  d = leonardo(d,da,dba,dca);
  float dcb = dot(pos-p,cb), dab = dot(pos-p,ab);
  d = leonardo(d,db,dcb,dab);
  float dac = dot(pos-p,ac), dbc = dot(pos-p,bc);
  d = leonardo(d,dc,dac,dbc);
  return d;
}

vec3 triplanar(vec3 p, vec3 n, sampler2D samp) {
  vec3 texX = texture(samp, p.zy).xyz;
  vec3 texY = texture(samp, p.xz).xyz;
  vec3 texZ = texture(samp, p.xy).xyz;
  return texX * n.x + texY * n.y + texZ * n.z;
}

float colorfaces(vec3 pos, vec3 n, inout vec3 col) {
  vec4 pos4 = vec4(pos,-1);
  // Distance from 3 planes
  float d0, d = -1e8;
  d0 = dot(pos4,aplane);
  if (d0 > d) { d = d0; col = colors[0]; }
  d0 = dot(pos4,bplane);
  if (d0 > d) { d = d0; col = colors[1]; }
  d0 = dot(pos4,cplane);
  if (d0 > d) { d = d0; col = colors[2]; }
  n = fold(n);
  //col *= length(triplanar(pos,n,iChannel2).rgb);
  col = triplanar(pos,n,iChannel2).rgb;
  //col *= length(texture(iChannel2,pos.xy).rgb);
  return d;
}

void colorfaces0(vec3 pos, inout float d, inout int col) {
  vec4 pos4 = vec4(pos,-1);
  // Distance from 3 planes
  if (abs(dot(pos4,aplane)) < 1e-4) col = 0;
  if (abs(dot(pos4,bplane)) < 1e-4) col = 1;
  if (abs(dot(pos4,cplane)) < 1e-4) col = 2;
  col = 3;
}

bool alledges = true;

float dualfaces(vec3 pos) {
  //assert(eq(length(p),1.0));
  float r = 1.0/length(p);
  float d = 1e8; // Nice 'n' easy, p defines surface
  float d0 = dot(pos,p)-r;
  if (!doleonardo) return d0;
  // The 'Leonardo look' - clip by a plane parallel to faceplane
  // This should really be a plane orthogonal to dual face.
  float eps = 1e-3;
#if 0
  vec3 xplane = normalize(cross(p,cross(p,A)));
  vec3 yplane = normalize(cross(p,cross(p,B)));
  vec3 zplane = normalize(cross(p,cross(p,C)));
#else
  vec3 xplane = A;
  vec3 yplane = B;
  vec3 zplane = C;
#endif
  if (alledges || dot(p,A) > eps) d = min(d,max(d0,dot(pos,xplane)-lwidth));
  if (alledges || dot(p,B) > eps) d = min(d,max(d0,dot(pos,yplane)-lwidth));
  if (alledges || dot(p,C) > eps) d = min(d,max(d0,dot(pos,zplane)-lwidth));
  d = max(d,-d0-lwidth);
  return d;
}

float colordualfaces(vec3 pos, vec3 n, bool parity, inout vec3 col) {
  n = fold(n);
  col = triplanar(pos,n,iChannel2);
  //col = texture(iChannel2,pos.xy).rgb;
  return dualfaces(pos);
}

float scene(vec3 pos) {
  float d = 1e8;
  if (dodualfaces) d = min(d,dualfaces(pos));
  if (dofaces) d = min(d,faces(pos));
  return d;
}

float colorscene(vec3 pos, vec3 n, bool parity, out vec3 col) {
  float d = 1e8, d0;
  vec3 col0;
  if (dodualfaces) {
    d0 = colordualfaces(pos,n,parity,col0);
    if (d0 < d) { d = d0; col = col0; }
  }
  if (dofaces) {
    d0 = colorfaces(pos,n,col0);
    if (d0 < d) { d = d0; col = col0; }
  }
  return d;
}

float polyhedron(vec3 pos) {
  vec3 pos0 = pos;
  pos = fold(pos);
  float d = 1e8;
  d = min(d,scene(pos));
  if (region) {
    d = max(d,dot(pos0,A));
    d = max(d,dot(pos0,B));
    d = max(d,dot(pos0,C));
  }
  return d;
}

vec3 getColor(vec3 pos,vec3 n){
  vec3 pos0 = pos;
  int flips;
  pos = fold(pos,flips);
  vec3 col = vec3(0,0,1);
  float d = colorscene(pos,n,flips%2==0,col);
  if (region) {
    float d0 = d;
    d = max(d,dot(pos0,A));
    d = max(d,dot(pos0,B));
    d = max(d,dot(pos0,C));
    if (d != d0) col = colors[6];
  }
  return col;
}

//-------------------------------------------------
//From https://www.shadertoy.com/view/XtXGRS#
vec2 rotate(in vec2 p, in float t) {
  return p * cos(-t) + vec2(p.y, -p.x) * sin(-t);
}

float map(in vec3 p) {
  float d = polyhedron(p);
  if (slice) d = max(d,p.x);
  return d;
}

// We should be able to do this analytically from the plane hit.
vec3 calcNormal(in vec3 p) {
  const vec2 e = vec2(0.001, 0.0);
  return normalize(vec3(map(p + e.xyy) - map(p - e.xyy),
                        map(p + e.yxy) - map(p - e.yxy),
                        map(p + e.yyx) - map(p - e.yyx)));
}

float maxdist = 5.0;
float march(in vec3 ro, in vec3 rd) {
  const float precis = 0.001;
  float t = 0.1;
  float res = -1.0;
  for (int i = 0; i < 100; i++) {
    if (t > maxdist) return -1.0;
    float h = map(ro+rd*t);
    h = min(h,0.5);
    t += 0.8*h;
    if (h < precis) break;
  }
  return t;
}

vec3 transform(in vec3 p) {
  if (iMouse.x > 0.0) {
    float theta = (2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
    float phi = (2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
    p.yz = rotate(p.yz,-theta);
    p.zx = rotate(p.zx,phi);
  }
  if (dorotate) {
    p.yz = rotate(p.yz,iTime * 0.125);
    p.zx = rotate(p.zx,iTime * 0.1);
  }
  return p;
}

vec3 circuit[] = vec3[]
  (vec3(1,1,1),vec3(1,1,1),vec3(1,0,0),vec3(1,0,0),
   vec3(1,1,0),vec3(1,1,0),vec3(0,1,0),vec3(0,1,0),
   vec3(0,1,1),vec3(0,1,1),vec3(0,0,1),vec3(0,0,1),
   vec3(1,0,1),vec3(1,0,1),vec3(1,0,0),vec3(1,0,0));

const int nsteps = circuit.length();

vec3 gettri(float t) {
  int i = int(floor(t))%nsteps;
  return mix(circuit[i],circuit[(i+1)%nsteps],mod(t,1.0));
}

const int CHAR_0 = 48;
const int CHAR_A = 65;
const int CHAR_B = 66;
const int CHAR_C = 67;
const int CHAR_D = 68;
const int CHAR_E = 69;
const int CHAR_F = 70;
const int CHAR_G = 71;
const int CHAR_L = 76;
const int CHAR_M = 77;
const int CHAR_N = 78;
const int CHAR_O = 79;
const int CHAR_Q = 81;
const int CHAR_R = 82;
const int CHAR_S = 83;
const int CHAR_T = 84;
const int CHAR_X = 88;
const int CHAR_Z = 90;

const int KEY_PAGE_UP = 33;
const int KEY_PAGE_DOWN = 34;
const int KEY_LEFT = 37;
const int KEY_UP = 38;
const int KEY_RIGHT = 39;
const int KEY_DOWN = 40;

bool key(int code) {
  return texelFetch(iChannel0, ivec2(code,2),0).x != 0.0;
}

vec4 store(int i,int j) {
  return texelFetch(iChannel1, ivec2(i,j),0);
}

int keycount(int key) {
  return int(store(0,key).x);
}

// softshadow from iq.
float softshadow(vec3 ro, vec3 rd, float mint, float tmax, float w) {
  float t = mint;
  float res = 1.0;
  for (int i = 0; i < 200; i++) {
    float h = map(ro + t*rd);
    res = min(res, h/(w*t));
    t += clamp(h, 0.01*t, max(0.5,0.1*t));
    if (res < -1.0 || t > tmax) break;
  }
  res = max(res,-1.0); // clamp to [-1,1]
  res = 0.25*(1.0+res)*(1.0+res)*(2.0-res); // smoothstep
  return res;
}

float calcAO( in vec3 pos, in vec3 nor, float t ){
  float dd, hr, totao = 0.0;
  float sca = 1.0;
  vec3 aopos; 
  for( int aoi=0; aoi<5; aoi++ ) {
    hr = 0.005 + 0.05*float(aoi);
    aopos =  nor * hr + pos;
    totao += -(map(aopos)-hr)*sca;
    sca *= 0.75;
  }
  return clamp( 1.0 - 4.0*totao, 0.0, 1.0 );
}

int mymod(int n, int m) {
  // glsl % undefined for -ve arguments
  if (n < 0) return m-1-(-n-1)%m;
  else return n%m;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  bool doao = !key(CHAR_A);
  bool doshadow = !key(CHAR_S);
  doleonardo = !key(CHAR_L);
  dorotate = !key(CHAR_R);
  dodualfaces = !key(CHAR_D);
  dofaces = !dodualfaces;
  swap = key(CHAR_X);
  region = key(CHAR_F);
  slice = key(CHAR_C);
  alledges = key(CHAR_E);
  vec2 p = fragCoord.xy / iResolution.xy;
  float camera = 3.0;
  camera *= exp(0.1*float(keycount(KEY_DOWN)-keycount(KEY_UP)));  
  lwidth *= exp(0.1*float(keycount(KEY_RIGHT)-keycount(KEY_LEFT)));
  maxdist = camera + 2.0;
  vec3 ro = vec3(0.0, 0.0, -camera);
  ro = transform(ro);
  vec3 li = normalize(vec3(0.5, 2.8, -3.0));
  li = transform(li);
  li = normalize(li);
  init();
  int tindex = keycount(KEY_PAGE_UP)-keycount(KEY_PAGE_DOWN);
  vec3 tri = !key(CHAR_T) ? gettri(0.25*iTime) : circuit[mymod(2*tindex,nsteps)];
  initp(tri);
  scenesetup();
  vec3 aacol = vec3(0);
  vec3 bgcol = vec3(0.2 - 0.2*p.y);
  for (float i = 0.0; i < AA; i++) {
    for (float j = 0.0; j < AA; j++) {
      vec2 p = (2.0*(fragCoord+vec2(i,j)/AA) - iResolution.xy)/iResolution.y;
      vec3 col = bgcol;
      vec3 rd = vec3(p, 3.0);
      rd = transform(rd);
      rd = normalize(rd);
      float t = march(ro, rd);
      if (t > 0.0) {
        vec3 pos = ro + t * rd;
        vec3 n = calcNormal(pos);
        float ambient = 0.2;
        float ao = !doao ? 1.0 : calcAO(pos,n,t);
        float softshadow = !doshadow ? 1.0 : softshadow(pos,li,0.1,4.0,0.01);
        float diffuse = clamp(dot(n, li), 0.0, 1.0);
        col = getColor(pos,n) * (ambient*ao+diffuse*softshadow);
        //col = mix(col,bgcol,0.4*t/maxdist);
      }
      aacol += col;
    }
  }
  aacol = pow(aacol/(AA*AA), vec3(0.4545));
  if (alert) aacol.x = 1.0;
  fragColor = vec4(aacol, 1.0);
}

