float gcd(vec2 p){
    p = abs(p);
    float eps = 1.;
    for (float i = 0.; i < 1000. && p.x > eps; i++){
        p.y = mod(p.y, p.x);
        p.xy = p.yx;
    }
    return p.x / eps;
}

float N21(vec2 p) {
    //return fract(sin(p.x*100.+p.y*6574.)*5647.);
    return gcd(p);
}

float SmoothNoise(vec2 uv) {
    vec2 lv = fract(uv);
    vec2 id = floor(uv);

    lv = lv*lv*(3.-2.*lv);

    float bl = N21(id);
    float br = N21(id+vec2(1,0));
    float b = mix(bl, br, lv.x);

    float tl = N21(id+vec2(0,1));
    float tr = N21(id+vec2(1,1));
    float t = mix(tl, tr, lv.x);

    return mix(b, t, lv.y);
}

float SmoothNoise2(vec2 uv) {
    float c = SmoothNoise(uv*4.);

    // don't make octaves exactly twice as small
    // this way the pattern will look more random and repeat less
    c += SmoothNoise(uv*8.2)*.5;
    c += SmoothNoise(uv*16.7)*.25;
    c += SmoothNoise(uv*32.4)*.125;
    c += SmoothNoise(uv*64.5)*.0625;

    c /= 2.;

    return c;
}

vec2 N22(vec2 p){
    vec3 a = fract(p.xyx * vec3(123.34, 234.34, 345.65));
    a += dot(a, a + 34.45);
    return fract(vec2(a.x*a.y, a.y * a.z));
}

float slow_voronoi(vec2 uv, float t){
    float m = 0.;
    float minDist = 100.;
    for (float i = 0.; i < 50.; i++){
        vec2 n = N22(vec2(i + 1.));
        vec2 p = sin(n * t);

        float d = length(uv - p);
        m += smoothstep(.02, .01, d);

        if (d < minDist){
            minDist = d;
        }
    }
    return minDist;
}

