struct traceResult { vec3 hitPos; vec4 hitColor; };

vec3 bSpherePos = vec3(5, 0, 0);
vec3 wSpherePos = vec3(-5, 0, 0);

traceResult trace( vec3 startPos, vec3 startVel )
{

 	vec3 pos = startPos;
    vec3 vel = startVel;
    vec4 color = vec4(vec3(0), 1);
    float life = 0.;

    while( life < 500. )
    {

        bSpherePos = vec3(cos(iTime), 0, sin(iTime))

        float bSphereDist = max(distance(pos, bSpherePos), 0.01);
        float wSphereDist = max(distance(pos, wSpherePos), 0.01);
        float planeDist = pos.y + 10.;

        float minDist = min(min(bSphereDist, wSphereDist), planeDist);

        pos += vel * (minDist / 2.);
        life += 1.;

        vec3 bForce = normalize( bSpherePos - pos ) * 0.05 / pow( length( bSpherePos - pos ), 2. );
        vec3 wForce = normalize( wSpherePos - pos ) * 0.05 / pow( length( wSpherePos - pos ), 2. );
        vel = normalize(vel + bForce + wForce * minDist);

        if( planeDist < 0.01 )
        {    

            color = vec4(1);

        	if( mod(pos.x, 8.) > 4. && mod(pos.z, 8.) > 4. )
            {
                color = vec4(vec3(0), 1);

                if( pos.x > 0. ){ color = vec4(vec3(1, 0, 0), 1); }

            }
        }  
        else if ( bSphereDist <= 0.1 )
        {

            vec3 relative = pos - bSpherePos;
            pos = wSpherePos + relative;

        }
        else if ( length(pos) > 300. ) {

            color = vec4(vec3(0.3), 1); 
            break;

        }

    }

    return traceResult( pos, color );

}

vec3 toSphere( float azimuth, float inclination )
{
    return vec3(
        cos(inclination) * cos(azimuth),
        sin(inclination),
        cos(inclination) * sin(azimuth)
  );
}

