void fold(inout vec2 p, vec2 dir,inout int n){
    float dt = dot(p,dir);
    if (dt<0.) {
        p-=2.*dt*dir;
        n++;
    }
}
vec2 cInverse(vec2 z, vec2 center, float radius){
    z -= center;
    return z*radius*radius/dot(z,z) + center;
}
void fold_circle(inout vec2 z, vec2 c, float r, inout int n) {
    if (distance(z,c)>r) return;
    z = cInverse(z,c,r);
    n++;
}
vec3 drawCircle(vec2 z,vec2 c,float r) {
    return vec3(smoothstep(distance(z,c)-r,.01,0.));
}
void doFolds(inout vec2 z,vec2 c,float r,inout int n) {              
    fold(z,vec2(1,0),n);
    fold(z,vec2(0,1.),n);
    fold_circle(z,c,r,n);
}
float xor(float a, float b){return float(int(a) | int(b));}

// Standard spiral zoom.
vec2 spiralZoom(vec2 p, vec2 offs, float n, float spiral, float zoom, vec2 phase){

	p -= offs;
	float a = atan(p.y, p.x)/6.283 + iTime/32.;
	float d = length(p);
	//return vec2(a*n + log(d)*spiral, -log(d)*zoom + a) + phase;
    // Equivalent to the line above. Fabrice tidied it up. I'd imagine the grouped
    // operations might make it a little quicker, but don't quote me on that. :)
    return mat2(n, 2, spiral,-zoom)*vec2(a, log(d)) + phase;

}

