vec2 hash22(vec2 p)
{
    p  = fract(p * vec2(5.3983, 5.4427));
    p += dot(p.yx, p.xy +  vec2(21.5351, 14.3137));
    return fract(vec2(p.x * p.y * 95.4337, p.x * p.y * 97.597));
}

vec2 noise(float t)
{
    return hash22(vec2(t, t * 1.423)) * 2.0 - 1.0;
}

vec2 lpnoise(float t, float fq)
{
    t *= fq;

    float ft = fract(t);
    float tn = t - ft;
    ft = smoothstep(0.0, 1.0, ft);

    vec2 n0 = noise(floor(tn + 0.0) / fq);
    vec2 n1 = noise(floor(tn + 1.0) / fq);

    return mix(n0, n1, ft);
}

#define bitreduction(s,amt) s = sign(s) * floor(abs(s)/amt)*amt
#define sampleratereduction(t, amt) t = floor(t/amt)*amt


vec2 kick(float t, inout float sc){
	init;

    //sampleratereduction(t, 0.00001);

    float md = beat/4.;
    float pl = 16.;
    t = mod(t, md*pl);
    float idx = floor(t/md);
    t = mod(t, md);

    float env = exp(-t*40.7);
    s += sin((exp2(-t*80.1)*340. + 30.)*tt )*env;

    s = mix(s,sin(s*3.)*1., 0.9);


    bitreduction(s, 0.001);

    //s = sign(s)*smoothstep(0.,1.,abs(s));


    s = clamp(s,-1., 1.);


    float att = float(pattK[int(idx)]);
    s *= att;
    sc = 1. - pow(env, 0.15)*att;
    sc = smoothstep(0.,1.,sc);
    ret;
}

vec2 snarea(float t){

    init;


    sampleratereduction(t, 0.00001);
    float md = beat;
    float pl = 8.;
    t = mod(t, md*pl);
    float idx = floor(t/md);
    t = mod(t, md);


    vec2 nn = lpnoise(t, 400.);
    nn += lpnoise(t, 5000.)*0.9;
    nn += lpnoise(t, 16500.)*0.45;

    float amt = 1.;
    float env = exp(-t*30.);
    //float f = (400. + exp(-t*50.)*600.)*tt;
    float f = (300./1. + exp(-t*14.)*1100.  )*tt;
    s += sin( f + nn.x*0.2)*env;
    s += sin( f*2.5 + nn.y*4.6)*pow(env, 0.8)*0.7;
    s += sin( pow(f, 1.01)*4. )*env*0.3;



    s += sin( f*1.5 )*env*0.1;
    s *= 0.2;
    bitreduction(s, 0.001);
    //s += sin( f*2. )*env*0.1;

    s *= float(pattS[int(idx)]);
