}
vec2 snare(float t){
	init;


    float md = beat;
    float pl = 8.;
    t = mod(t, md*pl);
    float idx = floor(t/md);
    t = mod(t, md);

    float env = exp(-t*70.);

    vec2 n =lpnoise(t,2000.5);
    vec2 nb =lpnoise(t,22000.5);
    float f = 300.+ 300.*exp2(-t*26.)+ n.x*4.5 ;


    s += sin(f*tt + n.x*0.25 + nb.x*0.2*exp(-t*20.))*env;
    s += sin(f*tt*4. + n.x*0.25 + nb.x*4.2*exp(-t*10.))*pow(env, 0.8)*0.03;

